new GadgetDescription { Id = Guid.Parse("5549c772-6394-4394-96bb-d627da454b1b"), GadgetId = Guid.Parse("ce31e48f-a5bb-4503-815f-dab249d18b3c"), Value = "Laptop HP Pavillion có nhiều cổng kết nối tiện lợi bao gồm: HDMI, USB Type-A, Headphone/microphone combo, USB Type-C và khe Micro SD. Những cổng này giúp bạn dễ dàng kết nối với các thiết bị ngoại vi như màn hình phụ, ổ cứng ngoài và thiết bị trình chiếu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
