new GadgetDescription { Id = Guid.Parse("9cd77604-0b79-4c5f-86b0-a7a07cc49373"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Laptop HP Gaming VICTUS cho phép người dùng không chỉ chiến đấu hấp dẫn ở mọi tựa game một cách trơn tru mà còn thao tác hiệu quả các tác vụ văn phòng như Word, Excel,... nhờ chip AMD Ryzen 5 7535HS có 6 nhân 12 luồng, với tốc độ cơ bản 3.30 GHz và đạt tối đa đến 4.55 GHz nhờ Turbo Boost. Hơn thế nữa, máy còn hỗ trợ làm hình ảnh, thiết kế đồ hoạ, poster, render video ngắn hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("58b81885-8296-4552-91b5-a0f964071fcc"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Mang đến hiệu suất chơi game đáng kinh ngạc cùng giải pháp đồ họa hiệu quả nhờ card rời NVIDIA GeForce RTX 2050 4 GB, cho bạn thỏa sức sáng tạo với các nền tảng Adobe hay tận hưởng chất lượng hình ảnh rõ nét trong từng chuyển động ở thế giới ảo của cả các tựa game có đồ họa cao như Valorant, CS:GO, FO4 Online,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("ceb7a854-3256-470a-a3f0-c684c8154079"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/cc92cede-5a14-487b-ad7c-a902839638b6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("7041b18a-f510-4660-8470-ae0c6de605d0"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Đa nhiệm hơn khi thoải mái mở và di chuyển giữa nhiều ứng dụng cùng lúc mà không lo xuất hiện tình trạng giật hay lag máy nhờ RAM 16 GB chuẩn DDR5 với tốc độ Bus RAM 4800 MHz. Đồng thời RAM được hỗ trợ nâng cấp lên đến 32 GB theo nhu cầu sử dụng cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("95ed49db-af44-4bcf-a5bc-b6266d29866c"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Ổ cứng SSD 512 GB NVMe PCIe không chỉ mang đến không gian lưu trữ vừa đủ mà còn hỗ trợ mở và truy cập máy, ứng dụng một cách nhanh chóng chỉ trong vài giây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("2a06ac85-de56-4b69-8529-15ae111a30b7"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Bạn dễ dàng kiểm soát mọi trận đấu trong tầm tay nhờ phần mềm OMEN Gaming Hub, giúp bạn nhận diện các chỉ số quan trọng như mức sử dụng, nhiệt độ CPU, GPU và bộ nhớ hoặc cho phép điều chỉnh ép xung để tối ưu sử dụng cho gaming.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("143ce34e-acab-4bab-9eae-d0d9e67346cb"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/261a8490-cc07-4e5b-b89b-c678f5ddc603.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("b3aedfd4-66f7-4861-9b0c-17a7e1c05432"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Chiếc laptop HP này được hoàn thiện với lớp vỏ nhựa cứng cáp, các chi tiết máy không quá nổi bật nhưng bù lại bộ khung hầm hố và sắc sảo nên vẫn giữ nguyên được những nét cá tính nhất định. Hơn thế nữa, đèn nền được cài đặt dưới bàn phím giúp bạn dễ dàng nhìn thấy trong điều kiện thiếu sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("356855d6-0136-471f-bd2f-e9c42af1485a"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Máy có cân nặng tiêu chuẩn 2.29 kg vẫn gọn gàng cùng củ sạc trong balo để bạn mang theo cả ngày. Hai bên laptop được trang bị các cổng giao tiếp phổ biến như: HDMI, LAN, USB Type-C, USB Type-A, Jack tai nghe 3.5 mm và khe cắm SD, cho phép người dùng liên kết với nhiều thiết bị tương thích khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("901666dd-4bca-4b5d-be3d-4601ec343ad6"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Màn hình 15.6 inch với độ phân giải Full HD (1920 x 1080) cùng độ sáng 250 nits mang đến không gian hiển thị ấn tượng mọi nội dung, làm việc đa nhiệm với nhiều cửa sổ hay giải trí phim ảnh, chơi game đều tốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("3cdbe13d-fc6a-4dda-b0a8-6e2d802680b9"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1780867f-6a5f-4d60-8eb2-33bd4468b9c5.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("9c592cfb-671e-4492-9974-973dfd0b4a4b"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Khung hình được trải rộng lên đến 178 độ nhờ tấm nền IPS, cùng công nghệ Anti Glare, cho phép bạn tận hưởng trọn vẹn không gian hình ảnh ở bất kỳ điều kiện ánh sáng nào mà không lo mắt bị mỏi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("2d247d5b-caa9-481a-a176-f88aabe9e5ac"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Nâng tầm chất lượng hình ảnh với tần số quét màn hình 144 Hz, cho những chuyển động trong game mượt mà, chân thật đến từng chi tiết, tạo những trải nghiệm trọn vẹn với cả những tựa game hành động đầy kịch tính.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("99e8d529-2ed9-4c6f-a29f-05af955dc485"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Làm việc và học tập hay trò chuyện trực tuyến cùng các ứng dụng Zoom, Microsoft Teams, Google Meet,... với chất lượng hình ảnh sắc nét nhờ HD webcam khi sở hữu độ phân giải 720p.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("ec36418b-c0b8-48b3-89b0-a57063aa32af"), GadgetId = Guid.Parse("bfc94e37-08c7-45b7-b66e-7cf166870fb2"), Value = "Sự kết hợp của công nghệ HP Audio Boost và Audio by B&O trên laptop gaming mang đến âm thanh to rõ, hài hòa cùng khả năng khử tiếng ồn, mang đến không gian âm nhạc lý tưởng, cho bạn thư giãn sau một ngày làm việc mệt mỏi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("cf283cf7-255c-4997-a60e-c967a12aa2d4"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Laptop HP Gaming VICTUS 16 s0140AX R7 7840HS (9Q987PA) là một thiết bị mạnh mẽ được thiết kế đặc biệt để đáp ứng nhu cầu chơi game của người dùng hiện đại. Với thiết kế nổi bật và cấu hình mạnh mẽ, máy tính này không chỉ là một công cụ chơi game tốt mà còn là một trợ thủ đắc lực cho công việc đa phương tiện hoặc các nhu cầu giải trí khác.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("abed1e2f-24ae-4b22-8357-0280cc0610e6"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Hiệu năng đỉnh cao - cân mọi tựa game", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("b2a32ed1-0939-474e-9c56-61bbaf42c384"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Mẫu laptop này được trang bị với những công nghệ tiên tiến nhất hiện nay, đem đến hiệu năng vượt trội trong mọi tác vụ, từ chơi game đến công việc đa năng. Máy được trang bị bộ xử lý AMD Ryzen 7 7840HS với 8 nhân, 16 luồng cùng khả năng tăng tốc lên đến 5.1 GHz thông qua công nghệ Turbo Boost, từ đó có thể giải quyết các ứng dụng đòi hỏi cao một cách dễ dàng, nhanh chóng thông qua việc xử lý coding, văn bản, số liệu phức tạp,... ", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("bb04d55e-b90f-4d3e-aea1-66076fbce15d"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e3838bea-29ab-45cc-9029-d51e03d6372d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("db9268dc-aebc-4309-9c86-5351856c6e58"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Laptop gaming còn đi kèm với một card rời NVIDIA GeForce RTX 4050 6 GB, đem lại cảm giác chơi game và xử lý đồ họa vượt trội. Với card đồ họa này, bạn có thể thưởng thức các tựa game yêu thích ở độ phân giải cao, đồng thời cũng có khả năng xử lý các tác vụ đồ họa như thiết kế 3D, biên tập video và render một cách hiệu quả nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("491210ab-72b4-4e4d-b658-7e886e226500"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Ngoài ra, đây là mẫu laptop RAM 32 GB DDR5, có tốc độ Bus đạt 5200 MHz nâng cấp tối đa 64 GB đưa đến khả năng xử lý dữ liệu mạnh mẽ nhanh chóng với đa số các tác vụ. Sự kết hợp này giúp bạn trải nghiệm mượt mà mọi ứng dụng, từ các tựa game đình đám đến các ứng dụng đồ họa và làm việc với tệp tin lớn. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("8e8e4fc1-4937-4506-afc5-a298702a2ad3"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Máy tính sở hữu ổ cứng SSD NVMe PCIe Gen 4.0 dung lượng 512 GB, giúp tăng tốc độ đọc, ghi dữ liệu lên đến hàng chục lần so với ổ cứng truyền thống. Điều này không chỉ hỗ trợ khởi động hệ thống nhanh mà còn chạy mượt mà các ứng dụng, đem đến trải nghiệm sử dụng đỉnh cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
