new GadgetDescription { Id = Guid.Parse("16e09fdb-de57-42a1-a36c-0eade12557e0"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Màn hình có kích thước 16.1 inch phối hợp nhịp nhàng cùng mức phân giải Full HD (1920 x 1080) trên máy giúp hiển thị hình ảnh sắc nét, chi tiết, tạo ra trải nghiệm hình ảnh tuyệt vời cho cả công việc và giải trí. Không những thế, tần số quét 144 Hz còn cung cấp khả năng tái tạo mượt mà và chính xác, đặc biệt là trong các tình huống đòi hỏi độ phản ứng nhanh như các tựa game có FPS cao hay những bộ phim bom tấn bùng nổ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("273e5b6e-b98c-48f9-aeca-994b34fc4eae"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "HP đã trang bị tấm nền IPS giúp cải thiện góc nhìn rộng và màu sắc ổn định từ mọi góc độ, trong khi tính năng chống chói Anti Glare hạn chế được tình trạng ánh sáng phản chiếu, tạo ra một môi trường xem thoải mái, không gây mỏi mắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
