new GadgetDescription { Id = Guid.Parse("5132f14f-1d59-45e6-befb-d5e993e06289"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Mặt lưng của máy được thiết kế phẳng với các đường nét vuông vức, đầy đặc trưng trên những mẫu laptop HP. Ngoài ra, khung máy được chế tạo từ chất liệu nhựa chắc chắn nhưng vẫn giữ được khối lượng khá đầm tay 2.33 kg, tạo sự di động và tiện lợi cho anh em game thủ khi mang theo bên mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("fd34b39a-c95f-4551-89aa-c633cfbaca2d"), GadgetId = Guid.Parse("e8701b82-abee-4d23-b17b-0f6963ba6de4"), Value = "Thiết bị cũng được trang bị một hệ thống đèn chuyển màu RGB - 1 vùng, tạo điểm nhấn và tăng cường trải nghiệm sử dụng, đặc biệt là trong các điều kiện ánh sáng yếu. Bên cạnh đó, các cổng kết nối được đặt ở các vị trí thuận tiện, giúp dễ dàng kết nối với các thiết bị ngoại vi mà không gây cản trở cho quá trình sử dụng như LAN, Jack tai nghe 3.5 mm, HDMI, USB Type-C, USB A.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("42c55a9c-de1c-4835-92de-cf070243ac07"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Laptop HP Envy X360 13 bf0090TU i7 (76B13PA) - một siêu phẩm laptop cao cấp - sang trọng đến từ nhà HP khi sở hữu những thông số kỹ thuật ấn tượng từ cấu hình mạnh mẽ, màn hình sắc nét cho đến ngoại hình tinh tế, độc đáo với thiết kế gập 360 độ linh hoạt, hứa hẹn không làm người dùng thất vọng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("e4c54602-36ca-4060-af00-d1c82d9a7921"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Tông xanh sang trọng cùng thiết kế thời thượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("e5a577ac-b9ec-4a6c-b858-b83d2f846378"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "HP Envy X360 được hoàn thiện với tổng thể nhỏ gọn và trang nhã, bên ngoài máy được bao phủ bởi lớp sơn xanh thanh lịch, logo HP điển hình của dòng Envy có lớp phủ bóng được đặt ngay ngắn chính giữa nắp lưng máy, vừa thể hiện tính cao cấp của laptop, vừa mang thiết kế có thể nói là thích hợp để có thể mang theo bất cứ đâu để sử dụng. Vỏ máy được chế tác với kim loại có tính chắc chắn cao nhưng tuy vậy khối lượng máy chỉ có vỏn vẹn 1.34 kg, khá thuận lợi khi mình cầm nắm hay mang theo người để phục vụ nhu cầu sử dụng. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("3ba61909-f47f-4345-b623-e79ea66a9c07"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Điểm mình khá thích ở chiếc máy này có lẽ là khả năng mở gập 360 độ, thích hợp với nhiều tư thế cầm nắm hay sử dụng khác nhau. Mình cũng thường hay cầm nắm và sử dụng laptop như một chiếc tablet để tìm kiếm nhiều cảm hứng hơn mỗi lần học bài hay làm việc. Kết hợp với đó thì HP cũng trang bị thêm cho chiếc máy này một cây bút cảm ứng, hỗ trợ cho các thao tác vẽ, ghi chú hay sử dụng trực tiếp lên màn hình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("49e593c6-d3f8-49e3-b20a-b6395992fa9a"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/52e71894-9076-4ddc-ab2c-aed2c3d9b94e.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("90a17d9a-b205-4aca-ada9-b6b76c67459c"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Bàn phím được thiết kế gần sát sang hai cạnh bên, các nút được bố trí rộng rãi thoải mái cho mình gõ phím, hành trình phím ở mức tương đối cùng độ nảy tốt, khi soạn thảo văn bản rất thích tay và không bị mỏi kể cả khi thao tác trong thời gian dài. Với lại, bàn phím có đèn nền 2 mức độ sáng nên mình cũng không còn quá lo lắng khi phải chạy deadline vào buổi khuya nữa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("8ce28657-837d-45dc-8612-a985857931cc"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Touchpad có kích thước lớn cùng độ nhạy cao nên cảm giác khi lướt, di vuốt hay kéo thả chuột của mình diễn ra khá suôn sẻ, không còn bị khựng lại giữa chừng. Tuy nhiên, với khối lượng công việc lớn thì việc thao tác lâu với bàn di chuột có thể hơi mỏi, theo mình các bạn vẫn nên trang bị thêm cho mình chuột bên ngoài để đạt được hiệu quả tốt nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("aed6f80d-a752-4ceb-a400-786d447c68c7"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/78f2e252-f3b2-4cac-9500-39909c35de60.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("97c56b30-86f4-46a4-ac2f-0d68b5dc60ad"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Bởi tính chất mỏng nhẹ nên kèm theo chiếc máy này không có quá đa dạng cổng với Jack tai nghe 3.5 mm, USB 3.2, Thunderbolt 4, nếu với nhu cầu sử dụng thường ngày mình vẫn thường dùng thêm một Hub chuyển đổi để thuận tiện truyền tải dữ liệu hơn. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("263a522c-9536-454e-8713-911ab30de166"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Mượt mà mọi tác vụ bạn cần", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("b6c10a44-b921-41fc-a6bd-3968dbab0d5c"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Laptop HP Envy được trang bị với con chip Intel Core i7 1250U kết hợp cùng card Intel Iris Xe Graphics cho mình khả năng hoàn thành hiệu quả mọi tác vụ từ cơ bản cho đến nâng cao. Điểm hiệu năng mình đo được bằng công cụ Cinebench R23 với con laptop này là 1602 điểm đơn nhân và 5509 điểm đa nhân, với những thông số trên thì quá đủ để HP Envy X360 đáp ứng mình với các tác vụ Google Drive, Word, Excel,... hay chỉnh sửa hình ảnh, video, âm thanh đơn giản cũng như chơi một số tựa game. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("194f4493-65fa-4674-a5a7-a9f3843c683e"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Đặc biệt, máy còn đạt tiêu chuẩn nền Intel Evo cho phép mình làm việc nhanh và hiệu quả hơn với các tính năng như sạc nhanh, hiệu năng mạnh mẽ,... ", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("4adc1e3a-51e9-4fd4-97ec-afd8a0ccc791"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1cfc4c7d-a139-4b67-b2e7-908737ff6949.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("bb11403f-19c2-4731-8e36-38c8ce772331"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Khả năng đa nhiệm trơn tru đến từ bộ nhớ RAM 16 GB giúp mình có thể xử lý cùng lúc lên đến hàng chục tab Chrome cùng các ứng dụng khác như Spotify, Photoshop,... Kết hợp với đó là ổ cứng SSD 512 GB NVMe PCIe còn mang lại cho mình không gian lưu trữ đầy đủ và thậm chí còn dư thừa mà không cần phải nâng cấp thêm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("c951e04a-7ece-4863-a636-8f10d08eabdb"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Màn hình OLED sắc nét, giải trí sống động", Type = ParseGadgetDescriptionType("BoldText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("bd0494a6-58d2-480b-b682-bfd35be71363"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Màn hình cảm ứng nhỏ gọn với kích thước 13.3 inch cùng khung viền mỏng đâu đó chỉ 5 mm vẫn cho hiển thị nội dung đầy đủ cho mọi hoạt động của mình. Màn hình hỗ trợ độ phân giải 2.8K giúp màu sắc được thể hiện chân thực và sống động, cảm giác khi làm việc và xem phim với khung hình sắc nét cũng trở nên thú vị hơn đôi phần.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("b1fe7c93-8978-45e8-8c8d-31bc42096495"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Độ chuẩn màu đạt 100% sRGB, 99% Adobe RGB và 100% P3 đem đến trải nghiệm xem đầy mãn nhãn khi giải trí, bên cạnh đó mỗi lần mình thực hiện chỉnh sửa đồ họa thì khả năng tái tạo màu sắc đều rất chuẩn xác. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("45dcf7da-f58b-40b1-a469-9f00fe118d91"), GadgetId = Guid.Parse("a6e3d3ca-e0d8-4e01-bee3-14606aa64778"), Value = "Màn hình sắc nét kết hợp với âm thanh to rõ đến từ công nghệ HP Audio Boost và Bang & Olufsen audio khiến cho trải nghiệm giải trí của mình thêm phần trọn vẹn, đắm chìm trong thế giới âm nhạc và phim ảnh đầy thư giãn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("0a2e98bc-59db-426c-a21d-4cb605b31f0f"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Laptop HP Envy X360 14 fc0083TU Ultra 7 155U (A19BSPA) mang đường nét sang trọng cùng lối thiết kế 2 trong 1 tiện dụng, khối lượng nhẹ nhàng cho di chuyển. Máy còn sở hữu hiệu năng vượt trội từ con chip Intel Ultra thế hệ hoàn toàn mới sẵn sàng cân mọi tác vụ cho người dùng ở bất cứ đâu.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("7a8dad1d-c22d-42ee-87e4-20801810c7e4"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "HP Envy X360 là một chiếc laptop cao cấp hoàn hảo cho những ai yêu thích sự tiện lợi và di động. Với khối lượng chỉ 1.39 kg, thiết bị này dễ dàng mang theo mọi nơi. Lớp vỏ kim loại cao cấp không chỉ mang lại vẻ ngoài sang trọng mà còn đảm bảo độ bền và bảo vệ tối ưu cho các thành phần bên trong, kết hợp với màu xanh tinh tế của máy phù hợp với mọi phong cách và môi trường sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("4aeb5675-4d1f-4f2f-8b8a-7b555e6d0c0d"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Thiết kế 2 trong 1 của laptop HP Envy cho phép người dùng dễ dàng gập mở 360 độ, chuyển đổi giữa chế độ laptop và máy tính bảng. Ở chế độ laptop, bạn có thể tận dụng bàn phím và touchpad để làm việc, viết lách hoặc thực hiện các tác vụ liên quan đến hình ảnh. Chế độ này lý tưởng cho công việc văn phòng, viết email hoặc duyệt web.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("44830bde-61e6-45bc-ac74-c9a2b0d552ca"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/df7cddd3-4422-4b63-8cc1-2827bf430ac7.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("5ae620f6-d685-492a-a0f6-b7e470f0d0cf"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Chuyển sang chế độ máy tính bảng, bạn có thể sử dụng màn hình cảm ứng và bút HP kèm theo để tương tác trực tiếp với nội dung. Chế độ này rất hữu ích cho việc vẽ, ghi chú hoặc duyệt ảnh và xem phim một cách thoải mái.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("ff34478a-b69b-4fa7-9d2f-d45f699b32f7"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Tính năng nhận dạng khuôn mặt cho phép mở khóa nhanh chóng và an toàn hơn. Máy đồng thời cũng đạt chuẩn Intel Evo, đáp ứng các tiêu chí về hiệu năng, tuổi thọ pin, khả năng khởi động nhanh và trải nghiệm người dùng tốt, là sự lựa chọn hoàn hảo cho nhu cầu làm việc và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("7e26541b-25bb-41dd-bd09-3c50c8948022"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Bàn phím với đèn nền đơn sắc trắng phù hợp cho những ca làm việc vào đêm khuya, dễ dàng nhận diện phím gõ hơn trong bóng tối. Thiết bị cũng có đa dạng các cổng ở 2 bên cạnh như: HDMI, USB Type-A, USB Type-C,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("f820bb6a-f5eb-47ec-9d79-cd015f75d27f"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/8dd6d429-024e-499f-bf0b-cc8f23754cba.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("6e722e84-2ef8-4d8b-a6ca-60f915121d3a"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Trang bị bộ vi xử lý Intel Core Ultra 7 155U, máy đảm bảo hiệu năng cao và tiêu thụ điện năng tối ưu, giúp xử lý mượt mà các tác vụ hàng ngày, đồ hoạ hay lập trình với các chức năng AI từ thế hệ mới. Card đồ họa tích hợp Intel Graphics cung cấp khả năng xử lý đồ họa tốt, tái tạo hình ảnh chính xác và màu sắc sống động, mang lại trải nghiệm xem phim, chỉnh sửa hình ảnh và chơi game sắc nét.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("a6934dd6-de3e-42c0-8bf9-3e13955b4602"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Với bộ nhớ RAM 32 LPDDR5, máy có khả năng xử lý đa nhiệm hiệu quả, cho phép chạy các ứng dụng nặng và mở nhiều tab trình duyệt cùng lúc mà không gặp trở ngại. Ổ cứng SSD NVMe PCIe Gen 4 lưu trữ được đa dạng tài nguyên hơn với dung lượng trang bị sẵn là 1 TB, đồng thời việc khởi chạy các phần mềm hay truy xuất dữ liệu với khối lượng lớn cũng được diễn ra vô cùng nhanh chóng", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("fd46d1f3-9017-408b-9a46-9aad85618932"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Màn hình OLED 14 inch trên laptop HP với độ phân giải 2.8K (2880 x 1800) cung cấp hình ảnh rõ ràng và sắc nét, lý tưởng cho việc thưởng thức nội dung phim ảnh. Hơn nữa, hãng cũng khéo léo lắp đặt với tỷ lệ khung hình 16:10 cho phép hiển thị nhiều nội dung hơn trên khung hình cố định, điều tiết tầm nhìn thị giác tốt và cho bạn tập trung 100% vào công việc trước mắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("b9eadfac-73db-408e-8148-35c80c0a759f"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/bccfbe3d-2220-4804-94f6-94a32bf81411.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("2c575ecb-713c-4482-a95e-29c31849ddbb"), GadgetId = Guid.Parse("0a1bef2d-1f68-441c-913b-b3478cb741a8"), Value = "Mức hiển thị 1.07 tỷ màu tái hiện chân thực, sắc nét và sống động, với độ chính xác màu sắc cao lên đến 100% DCI-P3. Công nghệ Low Blue Light giúp giảm ánh sáng xanh phản chiếu từ màn hình laptop đến mắt người, mang lại trải nghiệm xem tốt trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
