new GadgetDescription { Id = Guid.Parse("3d2b7272-8d61-48a1-b66c-4510e1bbf0f0"), GadgetId = Guid.Parse("d55cd173-99a1-4cdb-aea7-4180bb9e9aa5"), Value = "Vỏ laptop được làm bằng kim loại - Nhôm cứng cáp, có độ sáng và bóng nhất định, để làm tăng tính thẩm mỹ đặc trưng cho thiết bị, đồng thời tạo nên cấu trúc chắc chắn hơn trước những tác động từ môi trường xung quanh. Khối lượng nhẹ nhàng 1.22 kg không gây bất cứ vướng víu nào kể cả khi mang máy theo bên mình cả ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("a6c96b19-b6d0-4063-a7cd-838f1f3e9f52"), GadgetId = Guid.Parse("d55cd173-99a1-4cdb-aea7-4180bb9e9aa5"), Value = "Làm việc hiệu quả ngay cả khi thiếu sáng với đèn nền bàn phím đơn sắc. Các cổng giao tiếp phổ biến được tích hợp hai bên thân laptop: USB 3.2, Thunderbolt 4, HDMI,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("0eafefd8-1169-42bd-aafd-f3701c01ba90"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "Nhỏ gọn, sang trọng cho cả ngày dài đi chuyển hay tiện dụng và mạnh mẽ cho mọi công việc cá nhân đều được tích hợp trên laptop HP Elitebook 630 G10 i7 1355U (9H1N9PT). Tham khảo ngay mẫu máy tiện dụng này với mức giá thành hợp lý tại Thế Giới Di Động để hoàn thiện thật tốt mọi vấn đề.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("32fa1002-5e5d-4478-953a-9d5574b49d4b"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "Chiếc laptop học tập - văn phòng này có giao diện ngoài nhỏ gọn với khối lượng chỉ 1.28 kg, thân máy được vuốt mảnh mai, điểm nhấn bởi logo HP ánh bạc trên mặt A, thể hiện độ tinh tế mà dòng Elitebook muốn mang đến cho người dùng. Vỏ máy chế tác từ kim loại, tăng độ sang trọng và đảm bảo tính bền chắc trong quá trình sử dụng. Sự nhỏ gọn và nhẹ nhàng này giúp người dùng dễ dàng mang theo và sử dụng ở mọi nơi, từ văn phòng đến các cuộc họp hay khi di chuyển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("3f0e6a75-5f04-4d75-b33e-4173fce2cc72"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/c2015530-27fc-47ec-8831-230b5d036f9d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("838bbae2-0a2f-48a3-baef-8c8ad18e7276"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "Tính năng bảo mật vân tay giúp bạn truy cập vào laptop thuận tiện và bảo mật hơn chỉ với một chạm đơn giản. Công tắc khoá camera này đảm bảo dữ liệu của bạn luôn an toàn trước những nguy cơ xâm nhập trái phép.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("9c6a0dfa-3c20-4a12-a964-ae39f00fb74d"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "Bàn phím có đèn nền hỗ trợ làm việc vào buổi khuya hay ở những nơi thiếu sáng, tăng cường sự tiện dụng và thoải mái cho người dùng. Máy được trang bị đầy đủ các cổng kết nối thông dụng dọc 2 bên thiết bị như: LAN (RJ45), USB 3.2, HDMI, Thunderbolt 4,... giúp người dùng dễ dàng kết nối với các thiết bị ngoại vi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("3071f3a9-246c-41f8-8afe-4f9c6a183634"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9b4335a9-49ea-4a3d-8072-c2e681935e22.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("53c7b90c-154f-4c20-855d-996fa880fd44"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "Laptop HP sử dụng chip Intel Core i7 1355U có tốc độ xung nhịp tối đa lên đến 5.0 GHz đi kèm với card Intel UHD Graphics, đảm bảo khả năng vận hành hoàn hảo mọi tác vụ học tập, doanh nghiệp trên Word, Excel, PowerPoint,... hay chỉnh sửa đồ hoạ, thiết kế, lập trình phần mềm. Khi nâng cấp thêm một thanh RAM, laptop sẽ hoạt động với card Iris Xe Graphics, nâng cao hiệu suất từ GPU và chất lượng đồ hoạ video​.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("4b6ea682-1eea-4803-941e-7cd04b31fe0d"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "Bộ nhớ RAM 16 GB có hỗ trợ nâng cấp lên đến 32 GB cho việc linh động chuyển đổi tab khi làm việc, mở nhiều cửa sổ mà không bị gián đoạn hay giật lag. Ổ cứng SSD 512 GB NVMe PCIe giảm thiểu thời gian chờ đợi khi khởi động hệ thống, không gian lưu trữ lớn có thể mở rộng đến 1 TB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("8877ad7f-d71d-4c9e-9251-4d458ea4c011"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1f1ab625-d508-4be8-8c1e-282441a41dec.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("2abaff16-c5de-4afd-9ded-9de22fde8f8d"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "Màn hình laptop HP Elitebook có kích thước tiêu chuẩn 13.3 inch cùng độ phân giải Full HD (1920 x 1080) giữ cho nội dung hiển thị luôn được rõ nét và thuận mắt nhất. Kích thước hợp lý cũng vẫn khá thoải mái cho người dùng chia đôi cửa sổ để linh động thao tác hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("3d2a16b7-954f-4133-a8d7-a7a48b082997"), GadgetId = Guid.Parse("09fa2350-88cc-4e57-b0e6-e227f6d2d430"), Value = "Hệ thống loa với công nghệ Realtek High Definition Audio tăng tần số của các dải âm khiến âm thanh được phát ra có độ sống động cao hơn, âm bass mạnh mẽ để bạn có những phút giây giải trí thực sự hấp dẫn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c86ea234-c5dc-49c6-9cda-539c9f84ff3b"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Laptop HP OMEN 16 xf0071AX R7 7840HS (8W946PA) đáp ứng \"nhiệt tình\" cho nhiều con game đồ hoạ của anh em, sáng tạo và giải trí tuyệt đỉnh với bộ cấu hình gồm chip AMD Ryzen 7 dòng HS, card rời RTX 40 series và hơn hết là khung hình 2K sống động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("fc3ba46c-fbf5-4c5b-8cce-e4e9335e31b2"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Đẳng cấp gaming qua hiệu năng khủng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("5300e05d-dbef-42c9-8a32-e8ca9b7211b8"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Laptop HP OMEN sử dụng CPU AMD Ryzen 7 7840HS với xung nhịp tối đa đạt 5.1 GHz cân hoàn hảo các tác vụ nặng như stream game, chiến các tựa game AAA thoải mái hay thiết kế những bản đồ hoạ, kiến trúc 3D phức tạp đều không thành vấn đề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("1f48d5f6-9bc8-48b9-9fb7-3d99c7c188d5"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Laptop này được trang bị RAM 32 GB DDR5 dung lượng lớn và tốc độ truy cập nhanh, cung cấp khả năng xử lý đa nhiệm mượt mà, cho phép bạn chạy nhiều ứng dụng, tác vụ đồng thời mà không gặp bất kì trục trặc nào. Hiệu suất vận hành thêm hoàn hảo nhờ khả năng nâng cấp lên đến 64 GB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("6ad97d98-98b3-48b2-9559-f9aa16d727b7"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/badc4ae0-4b45-4725-b510-bbccda16be47.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("4685c3ed-8826-4cd8-ac28-7399eeb29b55"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Mẫu máy xách tay cao cấp đi kèm với ổ cứng SSD 1 TB M.2 PCIe cho khả năng truy xuất và khởi động dữ liệu chỉ trong vài giây. Ngoài ra, thiết bị còn cho phép người dùng nâng cấp lên tới 2 TB, cung cấp không gian lưu trữ thoải mái cho các tệp tin dự án lớn hay các con game đồ hoạ tốn tài nguyên.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("6daaac2e-7b1d-4dba-a46b-0999c366eab6"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Card NVIDIA GeForce RTX 4060 với 8 GB VRAM đưa đến hiệu suất đồ họa vượt trội, bạn có thể tận hưởng trải nghiệm chơi các tựa game AAA mới nhất với chất lượng hình ảnh tuyệt đẹp và độ phân giải cao, màu sắc sống động và hình ảnh mượt mà. Card đồ họa này hỗ trợ công nghệ Ray Tracing và DLSS để mang lại hình ảnh chân thực, hiệu suất tối ưu nhất, kể cả cho các công việc đồ hoạ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("57bddf7d-d9d0-4aca-a096-a8514209c1a1"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Màn hình hoàn hảo, chất âm cực đã", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("f25ee414-873f-4295-8ef6-7676b81e8ea2"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Với kích thước 16.1 inch đi cùng tấm nền IPS tạo nên khoảng không gian giải trí rộng lớn và sống động cho game thủ với độ chính xác màu sắc gần như là tuyệt đối dưới mọi hướng nhìn. Màn hình laptop HP sở hữu độ phân giải QHD (2560 x 1440) mang đến hình ảnh sắc nét, chi tiết và độ phân giải cao, giúp hiển thị các chi tiết nhỏ trong trò chơi hay các nội dung đa phương tiện một cách rõ ràng. Độ sáng 300 nits tiêu chuẩn để bạn quan sát rõ ràng trong bất cứ môi trường nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("e0b0ced8-2ba0-4bd4-bd63-0b5cb4d73bab"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/2a9dd25d-78ea-407d-9668-538d7258bd23.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("f7965d49-be15-4507-866f-90f4081ee19b"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Tần số quét đến 240 Hz đặc biệt tạo nên độ mượt tối ưu cho các trải nghiệm phim ảnh hay giải trí, màn hình sẽ không bị các hiện tượng nhoè, nhiễu hay rách hình xảy ra khi chưa đồng bộ tốt với GPU. Độ phủ màu đạt đến 100% sRGB tái hiện màu sắc chính xác, sống động làm cho hình ảnh trở nên tươi sáng, hấp dẫn hơn, đem lại trải nghiệm trực quan tuyệt vời. Điều này cũng khá hữu ích trong các công việc thiết kế, sáng tạo đòi hỏi tính chuẩn xác sắc độ cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("e8d77331-492a-473e-a9db-c06f3eebd27e"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Công nghệ DTS:X ULTRA tạo ra một trải nghiệm âm thanh 3D sống động, cho phép bạn chìm đắm trong âm nhạc và hiệu ứng âm thanh của game. Kết hợp với một số công nghệ loa hàng đầu như Realtek High Definition Audio, HP Audio Boost và Bang & Olufsen audio mang lại âm lượng mạnh mẽ, sống động hơn mọi khoảnh khắc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("44350c92-01d1-41d8-8d65-0d4678c66c1f"), GadgetId = Guid.Parse("c2478d8e-65ce-4333-9a8d-c2a8868b3320"), Value = "Thiết kế cá tính, thời thượng, bật lên phong cách gamer", Type = ParseGadgetDescriptionType("BoldText"), Index = 12 },
