new GadgetDescription { Id = Guid.Parse("8e980b3c-5ffc-4bd4-a860-eef00707bc2f"), GadgetId = Guid.Parse("9c33d94b-9f8d-4aee-8332-32676c9124aa"), Value = "Laptop HP Envy X360 14 fa0050AU R5 8640HS (A29H8PA) là một tuyệt tác công nghệ, hòa quyện hoàn hảo giữa hiệu suất mạnh mẽ và thiết kế tinh tế, mang đến trải nghiệm đỉnh cao cho cả công việc lẫn giải trí. Cho dù bạn là một nhà sáng tạo nội dung, lập trình viên hay đơn giản là một người đam mê công nghệ, chiếc laptop này sẽ đáp ứng mọi yêu cầu theo cách chuyên nghiệp và khắt khe nhất.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("53020131-0c7a-4541-ba41-239f63f19558"), GadgetId = Guid.Parse("9c33d94b-9f8d-4aee-8332-32676c9124aa"), Value = "Với phong cách định hình ở dòng máy là cao cấp sang trọng nên laptop HP Envy sở hữu vẻ ngoài tương đối bóng bẩy, vỏ máy bằng kim loại cứng cáp được hoàn thiện tỉ mỉ và sở hữu gam màu bạc cuốn hút, phù hợp với mọi môi trường và hoàn cảnh sử dụng. Khối lượng cực nhẹ chỉ 1.39 kg cho mọi hành trình di chuyển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
