new GadgetDescription { Id = Guid.Parse("e4c0786f-017d-489d-9140-2c7ab792453e"), GadgetId = Guid.Parse("9c33d94b-9f8d-4aee-8332-32676c9124aa"), Value = "Bàn phím với hành trình sâu, độ nảy tốt và đèn nền màu trắng hỗ trợ làm việc hiệu quả trong điều kiện thiếu sáng. Thêm vào đó, tính năng mở khóa khuôn mặt với Windows Hello giúp bạn đăng nhập nhanh chóng và an toàn chỉ với một ánh nhìn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("b2303178-758b-482e-b93b-f6cdd11549d8"), GadgetId = Guid.Parse("9c33d94b-9f8d-4aee-8332-32676c9124aa"), Value = "Laptop cao cấp - sang trọng được trang bị đầy đủ các cổng kết nối như HDMI, USB Type-A, USB Type-C và jack cắm headphone/microphone combo đảm bảo khả năng kết nối linh hoạt với nhiều thiết bị ngoại vi, giúp bạn dễ dàng thiết lập một hệ thống làm việc hoặc giải trí toàn diện, phù hợp với mọi nhu cầu và mục đích.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
