new GadgetDescription { Id = Guid.Parse("ae5035d9-b30b-4eed-b9a3-b081ac038cbe"), GadgetId = Guid.Parse("9c33d94b-9f8d-4aee-8332-32676c9124aa"), Value = "Ổ cứng SSD 512 GB NVMe PCIe Gen 4.0 không chỉ mang lại không gian lưu trữ rộng rãi mà còn đảm bảo tốc độ truy xuất dữ liệu vượt trội. Bạn có thể dễ dàng lưu trữ hàng ngàn tài liệu, hình ảnh và cả bộ sưu tập game yêu thích mà không cần lo lắng về vấn đề dung lượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
