new GadgetDescription { Id = Guid.Parse("3f1f6d30-a145-4bb1-b652-0f3e4c2f8604"), GadgetId = Guid.Parse("9c33d94b-9f8d-4aee-8332-32676c9124aa"), Value = "Quan sát ấn tượng thông qua màn hình OLED 14 inch độ phân giải 2.8K (2880 x 1800) mang đến hình ảnh sống động, sắc nét với màu sắc trung thực đến từng chi tiết. Đặc biệt, tính năng cảm ứng đa điểm và bút cảm ứng đi kèm biến chiếc laptop thành một công cụ sáng tạo đa năng, từ việc thiết kế đồ họa 2D, tạo banner đến ghi chú nhanh chóng trong công việc và học tập.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("e5204728-96db-412f-af91-ab542ea218b3"), GadgetId = Guid.Parse("9c33d94b-9f8d-4aee-8332-32676c9124aa"), Value = "Chuẩn màu đạt 100% DCI-P3 làm cho chiếc màn hình của laptop HP thành sự lựa chọn lý tưởng cho những ai làm việc nhiều với màu sắc như biên tập viên video hay nhà thiết kế nội dung. Khả năng hiển thị màu sắc chính xác sẽ giúp bạn tạo ra những sản phẩm chất lượng cao, đặc biệt khi làm việc trên các phần mềm như Canva, Premiere Pro,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("70ca3c3c-1b6f-4494-9a8a-7707f665cbb5"), GadgetId = Guid.Parse("9c33d94b-9f8d-4aee-8332-32676c9124aa"), Value = "Nâng cao thêm trải nghiệm giải trí trong không gian cá nhân mà không cần thông qua bất cứ công cụ nào khác nhờ loa tích hợp các công nghệ HP Audio Boost và DTS:X ULTRA, tái hiện hoàn hảo trên từng âm bass sống động, thể hiện cấu trúc âm thanh vòm hấp dẫn để bao quát mọi loại nội dung người đùng thưởng thức.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("d60d4642-b522-4468-b1ef-330378cd27f0"), GadgetId = Guid.Parse("b4753053-e860-45f7-ab59-3305ae5870b2"), Value = "Laptop HP 15 fd0304TU i3 1315U (A2NL5PA) là lựa chọn lý tưởng cho sinh viên, học sinh, nhân viên văn phòng và những ai đang tìm kiếm một chiếc laptop để làm việc văn phòng và giải trí cơ bản trong tầm giá phải chăng. Chiếc laptop này sở hữu con chip Intel thế hệ 13 có độ ổn định cao, màn hình lớn mang đến cho bạn những trải nghiệm tuyệt vời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("a4e91697-d39a-441d-8066-18b2478a10b6"), GadgetId = Guid.Parse("b4753053-e860-45f7-ab59-3305ae5870b2"), Value = "Laptop được trang bị bộ vi xử lý Intel Core i3 Raptor Lake - 1315U kết hợp cùng GPU Intel UHD Graphics mang đến hiệu năng ổn định giúp bạn dễ dàng giải quyết mọi công việc trên công ty hay trường học. Dù là xử lý các tác vụ văn phòng phức tạp trên Microsoft Office, duyệt web mượt mà hay chỉnh sửa ảnh cơ bản, con chip này đều có thể đáp ứng một cách hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("09422e22-8e31-4bee-82c2-db86d25edba7"), GadgetId = Guid.Parse("b4753053-e860-45f7-ab59-3305ae5870b2"), Value = "Đắm chìm trong công việc với khả năng đa nhiệm vượt trội nhờ bộ nhớ RAM 8 GB DDR4 tốc độ Bus 3200 MHz. Xử lý nhiều ứng dụng cùng lúc, di chuyển giữa các tab trình duyệt mượt mà, soạn thảo văn bản nhanh chóng và tận hưởng âm nhạc yêu thích mà không lo giật lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("3e80d026-60ca-4f96-bdb4-870a2f1aabfc"), GadgetId = Guid.Parse("b4753053-e860-45f7-ab59-3305ae5870b2"), Value = "Ổ cứng SSD NVMe PCIe với dung lượng 512 GB mang đến không gian lưu trữ rộng rãi cho các tệp tin và ứng dụng. Đồng thời còn giúp khởi động máy, truy cập dữ liệu và tải ứng dụng chỉ trong tích tắc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("9ce2a700-878d-490a-84d2-26d1f82d4f12"), GadgetId = Guid.Parse("b4753053-e860-45f7-ab59-3305ae5870b2"), Value = "Trải nghiệm hình ảnh sống động và sắc nét đến từng chi tiết với màn hình 15.6 inch, độ phân giải Full HD (1920 x 1080) của dòng laptop HP cơ bản. Cho dù bạn đang xem phim bom tấn, chỉnh sửa ảnh hay đơn giản là lướt web, màn hình này sẽ mang đến cho bạn chất lượng hình ảnh tuyệt vời nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("cf5a0e85-b152-4407-8fd4-0461689947be"), GadgetId = Guid.Parse("b4753053-e860-45f7-ab59-3305ae5870b2"), Value = "Công nghệ chống chói Anti Glare trên màn hình sẽ hạn chế hiện tượng chói sáng, lóa mắt, kể cả khi bạn sử dụng laptop ở ngoài trời nắng gắt. Màn hình cho phép bạn tập trung tối đa vào công việc hay giải trí mà không bị phân tâm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("5fb3cee2-27cd-4c99-a146-4d79e87c0ddc"), GadgetId = Guid.Parse("b4753053-e860-45f7-ab59-3305ae5870b2"), Value = "Laptop HP sở hữu thiết kế thanh lịch, hiện đại với lớp vỏ được hoàn thiện từ chất liệu nhựa cao cấp. Vừa đảm bảo tính thẩm mỹ, độ bền bỉ cho sản phẩm, vừa tối ưu hóa khối lượng, giúp bạn dễ dàng mang theo bên mình mọi lúc mọi nơi. Chỉ nặng 1.59 kg, chiếc laptop này sẽ là người bạn đồng hành lý tưởng cho những ai thường xuyên di chuyển, cần làm việc ở nhiều địa điểm khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("c8934fdd-dc14-4a2a-a31d-bf5b60062de8"), GadgetId = Guid.Parse("b4753053-e860-45f7-ab59-3305ae5870b2"), Value = "Xung quanh thân chiếc laptop học tập - văn phòng này được trang bị các cổng kết nối gồm HDMI, USB Type-A , USB Type-C (chỉ hỗ trợ truyền dữ liệu) và headphone/microphone combo, hỗ trợ bạn tốt hơn trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("c7c00ab4-69c6-40c0-bbfa-c4653835c0be"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Laptop HP 15s fq5147TU i7 1255U (7C133PA) được trang bị bộ vi xử lý Intel Core i7 thế hệ 12 cùng những tính năng hấp dẫn khác, giúp người dùng có thể thực hiện các công việc hàng ngày một cách dễ dàng, hiệu quả.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("caef2990-bd90-4118-8e83-dc00ea3a2176"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Thiết kế cơ bản, giao diện hiện đại", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("4e7d007e-a39b-40f8-8d6d-7408959d9534"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Lược sơ qua về mặt ngoại hình thì mình không có quá nhiều ấn tượng về mẫu thiết kế của mẫu máy này, có thể là không chỉ mình mà các bạn cũng đã bắt gặp kiểu mẫu này ở đâu đó rồi. Tuy cũ nhưng mặt ngoại hình này cũng có một số những ưu điểm nhất định cho quá trình sử dụng. Trước hết là laptop sở hữu một kích thước khá phổ thông, độ dày 17.9 mm và khối lượng 1.69 kg thuận tiện cho vấn đề đi lại hàng ngày của mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("f81980d2-357f-40da-9006-067c27634a2e"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Vỏ máy được hoàn thiện từ lớp vỏ nhựa khá cứng, sở hữu ánh bạc tương tự như ở chất liệu kim loại nên thể hiện được nét thẩm mỹ riêng và hoàn toàn đặc biệt. Kết cấu bản lề chắc chắn nên cảm giác khi mình mở máy hoặc đóng gập hoàn toàn thoải mái. Thêm một điểm về thiết kế nữa mà mình cũng rất ưng, đó là các cạnh viền máy được bo cong tròn mềm mại và khác với các kiểu thiết kế bây giờ, có thể bỏ gọn trong balo, mang theo bên mình mà không gặp cảm giác bị cấn vào lưng hoặc vướng khi để chung với nhiều vật dụng khác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("162fbbf6-144b-4644-9fdb-9c147cc45298"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/771b542e-b2c9-4dd3-a59f-8de5721ef1ce.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("6c8ff174-a2c5-49d4-86ef-7efbeccdc3b3"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Cơ bản là một mẫu laptop học tập - văn phòng nên HP 15s có khá đầy đủ các cổng kết nối thông dụng từ: HDMI, Jack tai nghe 3.5 mm, USB 3.2, USB Type-C và khe SD hỗ trợ mình truy cập hình ảnh từ nhiều thiết bị ngoại vi, xuất file trình chiếu tại lớp học hoặc kết nối sử dụng với màn hình rời hay chuột ngoài. Máy cũng có đáp ứng được các nhu cầu sử dụng cơ bản với HD webcam, tuy hình ảnh quay chụp chưa thực sự chất lượng nhưng vẫn khá tốt nếu vận dụng làm việc trực tuyến từ xa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("bf46c1b8-247b-452e-ae3f-1d9de7ad7989"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Phim ảnh mượt mà, tối ưu trải nghiệm", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("7d485eb9-65f2-4222-ac84-42e181d2e418"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Màn hình laptop HP sở hữu kích thước 15.6 inch tích hợp chung với tấm nền LED Backlit cho mình không gian hiển thị tương đối thoải mái, việc mình có thể linh hoạt xử lý trên nhiều tab công việc đều rất dễ dàng. Độ phân giải Full HD (1920 x 1080) cũng giúp chiếu rõ nét và chi tiết các hình ảnh từ bộ phim hoặc các trò chơi cho mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("29b112c5-ed3c-4a8b-ae34-252a5c760a96"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/917de992-7e98-4ccb-bb14-c843eb04846f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("9af2b660-b63d-453d-bd0c-880c1ba17d56"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Để mà đánh giá sâu hơn thì màn hình không có nhiều điểm nổi bật nhưng lại là sự trang bị khá tốt trên những mẫu máy đặc biệt thuộc phân khúc thấp như này. Từ trải nghiệm làm việc, phim ảnh đến giải trí cơ bản với nhiều mức sáng, cùng nhiều màu sắc khác nhau thì tính tương phản vẫn được cân bằng khá tốt và vượt trội hơn nhiều so với các mẫu mã khác cùng phân khúc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("59b677b7-dae9-4b98-9e7b-df46cc8e4c04"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Công nghệ âm thanh Realtek High Definition Audio cho chất âm khá to, mình cũng chưa cần phải mở full loa để có thể trải nghiệm giải trí đã tai, tuy đôi lúc sẽ có một chút rè nhưng thực sự là sẽ không ảnh hưởng quá nhiều đến việc cảm nhận âm nhạc hoặc phim ảnh đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("b26ca9fe-a354-4513-8ff2-9563ab83ebcd"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d9c4a965-7a75-4056-8fdb-1f05bfdf51ff.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("7d9a98ee-b484-473b-bb24-706ca280a3bd"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Hiệu năng cân đối, đáp ứng đầy đủ ", Type = ParseGadgetDescriptionType("BoldText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("b23d0f41-a8a3-4480-91c9-44cd08403599"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Laptop HP cơ bản được tích hợp bộ vi xử lý Intel Core i7 1255U cùng card tích hợp Intel Iris Xe Graphics, hỗ trợ phân luồng đơn nhân khá tốt cho những tác vụ Microsoft Office như Word, Excel, PowerPoint,... đồng thời vẫn đáp ứng được việc mình căn chỉnh hình ảnh trên Canva và Photoshop, edit được video ngắn trên Capcut, Premiere. Còn một số nội dung khác như chơi game nặng thì mình cũng không khuyến khích lắm đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("c108de77-0157-4ad8-a62c-2a44e11057bc"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Bộ nhớ RAM 8 GB có sẵn cung cấp cho mình đầy đủ tài nguyên để làm việc thật linh hoạt, mở cùng lúc hàng chục tab Chrome mà máy vẫn không bị chậm hay giật lag. Thời gian tới, nếu khối lượng công việc nhiều hơn thì mình cũng có ý định nâng cấp lên đến 32 GB để thoải mái sử dụng nhất. Ổ cứng SSD 512 GB đủ dung lượng dùng cho mọi loại tài liệu cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("fcfe4459-4375-426e-aa23-3bfece1076bd"), GadgetId = Guid.Parse("a1ee8e24-2041-40f4-88c2-9b234bf3266b"), Value = "Nhìn chung, với việc sở hữu laptop HP 15s fq5147TU i7 1255U (7C133PA), bạn vẫn sẽ có được một con laptop có ngoại hình đẹp, màn hình hiển thị tốt cùng hiệu năng tích hợp hoàn toàn ấn tượng cho công việc. Các đối tượng người dùng như nhân viên văn phòng hoặc sinh viên các khối ngành kinh tế, tài chính cũng có thể tham khảo thêm nhé.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("c282034d-07e0-4aa3-8fac-8d8a79931a99"), GadgetId = Guid.Parse("4f366b24-db87-41db-8f00-07294c888dc7"), Value = "Trong thời đại công nghệ phát triển như vũ bão, việc lựa chọn một chiếc laptop phù hợp không chỉ đòi hỏi sự mạnh mẽ của hiệu năng mà còn phải đáp ứng các yếu tố thiết kế hiện đại, tiện ích và nhất là phải hợp túi tiền. Một trong những sản phẩm đáp ứng tất cả những tiêu chí này là laptop HP 15 fd1064TU Ultra 5 125H (9Z2Y2PA), một sự lựa chọn hoàn hảo với cấu hình mạnh mẽ đáp ứng được nhiều mặt của công việc.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("384b812b-e968-44bc-802a-ec2d1ebc7698"), GadgetId = Guid.Parse("4f366b24-db87-41db-8f00-07294c888dc7"), Value = "Với vi xử lý Intel Core Ultra 5 Meteor Lake - 125H, laptop HP sở hữu một trong những CPU hàng đầu từ Intel, với 14 nhân và 18 luồng xử lý, đảm bảo khả năng xử lý đa nhiệm mạnh mẽ và hiệu quả cao cho các tác vụ đòi hỏi năng suất. Công nghệ Turbo Boost cũng giúp tăng tốc độ xử lý lên đến 4.5 GHz, đáp ứng nhanh các tác vụ nặng như đồ hoạ nhiều layer hay yêu cầu truy xuất cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("f52cecd6-83d6-48c5-939f-fc970d9336bb"), GadgetId = Guid.Parse("4f366b24-db87-41db-8f00-07294c888dc7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b66c22cb-a4b0-4526-a466-3f802da007a4.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
