new GadgetDescription { Id = Guid.Parse("30ab671b-1953-42d6-a305-1d2d83a3fa4b"), GadgetId = Guid.Parse("4f366b24-db87-41db-8f00-07294c888dc7"), Value = "Laptop HP cơ bản sở hữu kiểu dáng khá phổ thông với khung máy được bo tròn mềm mại, tông màu bạc trang nhã nên cho dù bạn có sử dụng ở bất cứ không gian nào vẫn rất phù hợp. Dễ dàng mang theo thiết bị bên mình đến mọi nơi với khối lượng cân đối 1.65 kg.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
