new GadgetDescription { Id = Guid.Parse("46548066-32e5-4d84-9b6e-94df1371242f"), GadgetId = Guid.Parse("ab34fa9a-03eb-466b-9c18-d2bfeca531b7"), Value = "Một chiếc laptop học tập - văn phòng không chỉ sở hữu bộ phần cứng ổn định hỗ trợ đa công việc, thiết kế sang trọng đầy cao cấp mà còn có một mức giá siêu lý tưởng. Laptop Asus Vivobook X1404ZA i3 1215U (NK246W) hoàn toàn phù hợp với các bạn sinh viên thuộc các khối ngành kinh tế, tài chính hoặc nhân viên văn phòng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
