new GadgetDescription { Id = Guid.Parse("842686f8-a5f9-4fb5-a4e5-12f162b1b848"), GadgetId = Guid.Parse("ab34fa9a-03eb-466b-9c18-d2bfeca531b7"), Value = "HD webcam mang đến tính thuận lợi khi tham gia các hoạt động trực tuyến như video call, học online mà không cần kết nối thêm với thiết bị bên ngoài, đóng mở dễ dàng để đảm bảo an toàn cho hình ảnh cá nhân với công tắc khoá camera. Hơn nữa, tính năng bảo mật vân tay cho phép bạn truy cập thiết bị an toàn và tiện hơn so với kiểu nhập mật khẩu truyền thống.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
