new GadgetDescription { Id = Guid.Parse("e95dc299-32bb-412a-bc9e-72cea90b05b2"), GadgetId = Guid.Parse("cc15b7d6-15fa-4aca-9f21-be8035c41d91"), Value = "Mình đặc biệt khá thích thiết kế của những mẫu laptop từ nhà Asus bởi đặc điểm khá đơn giản, đường nét hiện đại phù hợp cả nam lẫn nữ cũng như nhiều lứa tuổi khác nhau. Asus Vivobook 14 được hoàn thiện với lớp phủ màu đen, viền khung mềm mại cùng mặt lưng với chỉ một cái logo đơn giản. Mình chắc chắn máy sẽ rất hợp gu với những bạn yêu thích sự tối giản, không quá cầu kì.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
