new GadgetDescription { Id = Guid.Parse("8af5f3e1-ebb6-4ca7-b1a9-378bc451329b"), GadgetId = Guid.Parse("cc15b7d6-15fa-4aca-9f21-be8035c41d91"), Value = "TouchPad có kích thước vừa phải, thoải mái, độ nhạy cũng tốt cho các thao tác kéo thả, di vuốt mượt mà. Nhưng mình vẫn khuyên các bạn nam có khổ tay hơi quá to thì nên sắm cho mình thêm chuột bên ngoài để có trải nghiệm sử dụng tốt nhất. Hơn nữa, phần bàn di chuột cũng có tích hợp thêm tính năng bảo mật vân tay, mình có thể nhanh chóng mở máy và truy cập các web dễ dàng hơn đáng kể.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("d1fb0b41-7d4b-4389-9b7d-3b195a66b653"), GadgetId = Guid.Parse("cc15b7d6-15fa-4aca-9f21-be8035c41d91"), Value = "Công tắc khóa camera hỗ trợ đóng mở webcam thủ công, bảo vệ tốt hình ảnh và thông tin cá nhân. Các cổng kết nối như HDMI, USB, Jack tai nghe và USB Type-C thoải mái cho mình kết nối với chuột, bàn phím cũng như xuất hình ảnh lên máy chiếu dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
