new GadgetDescription { Id = Guid.Parse("cb1bbe73-29c9-4b19-b990-2d709d21b39f"), GadgetId = Guid.Parse("cc15b7d6-15fa-4aca-9f21-be8035c41d91"), Value = "RAM 8 GB đủ để đáp ứng nhu cầu học tập và giải trí khi mình hoàn toàn có thể vừa làm việc vừa nghe nhạc trên YouTube hay mở hơn 10 tabs trình duyệt cùng lúc. Trong thời gian tới mình chắc chắn sẽ nâng cấp RAM bởi em máy hỗ trợ khả năng nâng cấp lên tối đa 16 GB, chắc chắn sẽ phục vụ hoàn hảo mọi nhu cầu sử dụng của mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
