new GadgetDescription { Id = Guid.Parse("7f512524-86f1-4bc4-8a8f-436ba18f3ec1"), GadgetId = Guid.Parse("cc15b7d6-15fa-4aca-9f21-be8035c41d91"), Value = "Không gian ổ cứng 512 GB SSD NVMe PCIe cho phép mình tải về và lưu trữ một vài tựa game thường hay chơi và dữ liệu cần thiết cho việc học, do hỗ trợ tốc độ truyền tải cao nên mình mở máy và ứng dụng cũng đều nhanh chỉ trong vài giây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("c24a2fd9-086f-4465-996e-8c3375cec6df"), GadgetId = Guid.Parse("cc15b7d6-15fa-4aca-9f21-be8035c41d91"), Value = "Tổng thể mình nhận thấy laptop Asus Vivobook 14 A1405VA i3 1315U (LY097W) vừa sở hữu thiết kế đẹp mắt, hiệu năng ổn định để xử lý đa tác vụ cùng màn hình sắc nét chắc chắn sẽ là một sản phẩm khá cạnh tranh trong phân khúc giá rẻ và đáng để bạn cân nhắc khi tìm kiếm một chiếc laptop để đáp ứng các nhu cầu của mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("4c32c3c4-8d9b-465e-a37a-ecc1a233c03b"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Laptop Asus Vivobook S 14 Flip TP3402VA i5 (LZ031W) được thiết kế với sự cân bằng tuyệt vời giữa sức mạnh và tính di động, một chiếc laptop học tập - văn phòng được tích hợp nhiều tính năng và cấu hình tiên tiến, sẵn sàng đáp ứng mọi nhu cầu sử dụng của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("751622cb-f176-42d6-a0d1-d2911ddf0fde"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Xử lý hoàn hảo mọi tác vụ với bộ vi xử lý Intel thế hệ 13 mới", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("27855a7d-a833-41f2-a822-451aa29bde01"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Laptop Asus Vivobook được trang bị bộ vi xử lý Intel Core i5 13500H, cung cấp hiệu năng mạnh mẽ và khả năng xử lý đa nhiệm vượt trội nhờ kiến trúc Raptor Lake mới nhất. Bên cạnh đó, máy cũng tích hợp card đồ họa Intel Iris Xe Graphics, đảm bảo hiển thị hình ảnh sắc nét, mượt mà, đảm bảo xử lý đồ họa tốt, từ đó cho phép mình chơi game, xem phim và làm việc với các tệp phim ảnh một cách trơn tru.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("51302797-c9ad-423d-b6bc-35ba41dceca6"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Cụ thể hơn, với bộ vi xử lý Intel Core i5 13500H mình hoàn toàn có thể sử dụng máy để xử lý các tác vụ văn phòng hàng ngày như làm việc với văn bản, bảng tính và trình diễn slide một cách mượt mà. Đối với các hoạt động thực hiện trên web cũng luôn diễn ra nhanh chóng, khi xem các nội dung đa phương tiện như video, ảnh và âm nhạc máy đều đáp ứng mượt mà với nội dung hiển thị sắc nét.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("fa46389b-8333-42c8-a2ce-818d711533de"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Card đồ họa Intel Iris Xe Graphics của Vivobook S 14 Flip cũng cho phép mình chơi các game nhẹ và xử lý đồ họa cơ bản tốt, mình có thể chiến mượt Liên Minh Huyền Thoại ở mức cấu hình trung bình và thực hiện chỉnh sửa hình ảnh 2D hay thậm chí 3D cơ bản trên các phần mềm thiết kế của nhà Adobe như Photoshop hay Illustrator.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("c606d6a5-9281-4905-9c6b-3e492af2bbb8"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ced8777c-3fe3-4c88-9914-284b4fefb7a3.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("4fbe6cb2-ccec-400b-afa6-35efb9052393"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Laptop Asus sở hữu RAM 16 GB RAM với 2 khe (8 GB tích hợp + 1 khe 8 GB), mang lại cho mình khả năng xử lý tác vụ và đa nhiệm mượt mà. Mình hoàn toàn có thể mở cùng lúc nhiều tác vụ làm việc văn phòng hay cửa sổ trình duyệt Chrome mà không lo bị giật lag hay tràn RAM gây đứng máy, nhờ đó mà mình có thể làm việc hiệu quả với trải nghiệm tốt nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("419768f4-17da-451c-9129-d13e185404d8"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Ngoài ra, máy cũng được trang bị ổ cứng SSD NVMe PCIe dung lượng 512 GB, cho phép mình lưu trữ dữ liệu và truy cập nhanh chóng vào các file và ứng dụng. Tốc độ đọc và ghi nhanh của SSD giúp máy tính khởi động nhanh, mở các ứng dụng và tải dữ liệu tức thời. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("b6ee1923-5641-4f78-8d29-4c84260104e4"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/936963bd-b4e1-4450-9160-b797c86c2c21.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("d4251517-d498-417a-b059-c242946d2b12"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Với những thông số kể trên, Vivobook S 14 Flip đủ sức để chạy các ứng dụng như chỉnh sửa video, thiết kế đồ họa và xử lý âm thanh. RAM lớn cũng như ổ cứng SSD nhanh đảm bảo khả năng xử lý mượt mà, tải dữ liệu nhanh chóng. Thậm chí mình còn có thể làm việc với các tệp tin, bảng tính và cơ sở dữ liệu lớn mà không gặp trục trặc hay bất kỳ sự chậm trễ nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c3f2f10d-4b57-46aa-ab20-5171c7175269"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Trong quá trình sử dụng mình cũng đánh giá cao hệ thống tản nhiệt của máy, khi sử dụng thông thường thì quạt hoạt động êm ái, luồng gió mạnh hỗ trợ tốt việc làm mát các linh kiện quan trọng như bộ xử lý (CPU) và card đồ họa (GPU). Kể cả khi mình sử dụng ở các tải công việc nặng, máy cũng luôn duy trì nhiệt độ ổn định và không bị tình trạng quá nhiệt, đảm bảo hiệu suất làm việc tối ưu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("eb84657d-fa58-4702-9263-92769df95766"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Tận hưởng không gian làm việc, giải trí sống động", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("90fc268a-407f-4b7a-81a5-f65c2b6a57b8"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Với kích thước 14 inch và độ phân giải WUXGA (1920 x 1200), màn hình thích hợp cho việc thực hiện nhiều công việc khác nhau như làm việc trên các ứng dụng văn phòng với khả năng hiển thị văn bản và hình ảnh rõ ràng. Mình vẫn thường soạn thảo tài liệu, chỉnh sửa bảng tính và tạo các bài thuyết trình một cách tiện lợi và hiệu quả. Ngoài ra màn hình độ phân giải cao cũng rất phù hợp cho các hoạt động giải trí như xem phim, xem video và chơi game với màu sắc sống động và độ chi tiết cao đến tuyệt vời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("6f65faf3-6652-4793-82e8-5e7499ce35c0"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/c47f40bd-e9ac-45e9-bc36-79bd11f50856.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("034ac962-bfc5-40ec-b360-a1b7b04b566b"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Thêm một tính năng mà mình rất thích trên màn hình của Vivobook S 14 Flip đó là khả năng thao tác cảm ứng đa điểm, cho phép mình tương tác trực tiếp với các ứng dụng và nội dung trên màn hình. Mình hoàn toàn có thể vuốt, nhấn và kéo trên màn hình một cách dễ dàng, mang đến trải nghiệm tương tác tự nhiên và tiện lợi hơn. Màn hình gương cũng giúp mình thao tác dễ dàng hơn, không bị cấn hay khựng khi vuốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("ceaca0ae-3bac-4fac-89d1-e1a155dcb1f8"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Laptop được trang bị hệ thống loa tinh chỉnh bởi Harman/Kardon, một trong những thương hiệu âm thanh danh tiếng, đi cùng công nghệ Smart AMP Audio để cung cấp âm thanh tuyệt vời cho người dùng. Được tối ưu hóa và điều chỉnh đặc biệt, máy có khả năng tái tạo âm thanh rõ ràng, chi tiết và mạnh mẽ, từ đó hỗ trợ nâng cao trải nghiệm nghe nhạc, xem phim và chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("8cd829d6-b0b9-4a9e-91ca-a40b47dd1e1e"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Ngoại hình thời trang, bản lề thiết kế linh hoạt", Type = ParseGadgetDescriptionType("BoldText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("2cb9ee3d-5faf-4d64-aafe-d47d74da97ac"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Vivobook S 14 Flip có ngoại hình đẹp và thu hút ánh nhìn với thiết kế tinh tế, vỏ máy được làm từ chất liệu nhựa chắc chắn, riêng nắp máy được hoàn thiện từ kim loại sang trọng, vừa đảm bảo độ bền trong quá trình sử dụng vừa thể hiện lối thiết kế thời trang đối với các bạn trẻ. Toàn bộ lớp vỏ ngoài của máy đều được sơn màu bạc sáng cũng như được sáng bóng, tạo nên vẻ ngoài bắt mắt và cảm giác sang trọng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("171f1396-689e-4e5e-bebb-2df29e756ca0"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Ngoại hình mỏng nhẹ của máy cũng là một điểm nhấn với độ dày chỉ 18.9 mm và khung viền màn hình hẹp, trông rất hiện đại và thời thượng. Khối lượng của máy tính cũng chỉ dừng lại ở con số 1.5 kg để tạo sự thoải mái khi cầm nắm và di chuyển. Bản lề của máy được thiết kế đặc biệt cho phép mình gập màn hình 360 độ để sử dụng máy ở nhiều chế độ khác nhau như laptop, chế độ xem phim, chế độ bảng hoặc chế độ lều. Nhờ vậy mà mình có thể tận dụng tối đa màn hình cảm ứng với nhu cầu sử dụng khác nhau, từ công việc cho đến giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("b2a2ee83-ff9e-4590-ab8f-6f9e5fabb870"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Khả năng bảo mật cũng được chú trọng trên Vivobook S 14 Flip với cảm biến vân tay tích hợp chung trên nút nguồn, giúp bảo vệ thông tin cá nhân của chính mình, đồng thời hỗ trợ mở khóa máy tính một cách an toàn và nhanh chóng bằng dấu vân tay nhằm ngăn chặn người lạ truy cập trái phép vào máy tính. Máy còn được trang bị công tắc khóa camera, cho phép mình có thể tắt hoặc mở camera một cách dễ dàng, chủ động bảo vệ quyền riêng tư và tránh bị xâm phạm thông qua việc truy cập trái phép vào camera máy tính.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("926d54d3-3f37-42b0-91ad-a7d619c0240f"), GadgetId = Guid.Parse("b5a044c8-0ef4-4978-b6c6-884cf3e1a657"), Value = "Tổng thể, Asus Vivobook S 14 Flip TP3402VA i5 (LZ031W) là một sự lựa chọn tuyệt vời cho những ai đang tìm kiếm một chiếc máy tính học tập - văn phòng có cấu hình mạnh mẽ, thiết kế linh hoạt có khả năng mang theo bên mình đi nhiều nơi và hệ thống bảo mật nâng cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("5a4904aa-4dbc-4300-9459-e239c03ab64e"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Trong bối cảnh thị trường laptop ngày càng sôi động và cạnh tranh khốc liệt, laptop Asus Vivobook S 14 OLED S5406MA Ultra 5 125H (PP136W) xuất hiện thật nổi bật dành riêng cho những ai yêu thích sự thông minh từ chip AI, tinh tế và hiệu suất mạnh mẽ. Đây không chỉ là một chiếc laptop, mà còn là người bạn đồng hành lý tưởng cho nhiều vấn đề trong học tập, làm việc và giải trí đa dạng của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b94c0be0-a5d1-4ae6-a6d3-0628ad4b556a"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Hãng đã trang bị cho chiếc máy này con chip Intel Core Ultra 5 Meteor Lake - 125H, một trong những vi xử lý thế hệ mới nhất hiện nay. Kết hợp với đó là card đồ họa tích hợp Intel Arc Graphics, chiếc laptop này sẵn sàng chinh phục mọi tác vụ từ cơ bản đến phức tạp, bao gồm việc chạy mượt mà các ứng dụng nặng như Adobe Photoshop, Premiere, Figma,... Khả năng xử lý luồng thông minh nhờ AI hỗ trợ giúp bạn hoàn thành công việc một cách nhanh chóng và hiệu quả hơn bao giờ hết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("0eec0fc6-c930-4d20-94c1-3114aa0f14f2"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Trải nghiệm đa nhiệm trên laptop Asus được nâng tầm nhờ dung lượng RAM 16 GB LPDDR5X. Bạn có thể mở hàng chục tab trình duyệt, sử dụng Microsoft Office, Adobe Illustrator và Spotify mà không lo máy bị giật lag hay giảm hiệu suất. Dung lượng RAM nguyên bản đã quá đủ để thực hiện nhiều vấn đề mà không cần phải nâng cấp thêm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("85986eb2-fdaa-4ff6-a434-a1990780a4d2"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Ổ cứng SSD 1 TB NVMe PCIe Gen 4 trên máy mang lại tốc độ khởi động và truy cập dữ liệu nhanh chóng. Bạn chỉ cần đợi vài giây để máy sẵn sàng hoạt động, tiết kiệm thời gian và nâng cao trải nghiệm người dùng khi sử dụng các ứng dụng cần truy xuất dữ liệu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("eef9c83e-47a2-43f8-9be7-6a2daffaa576"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Vivobook S 14 sở hữu màn hình 14 inch với độ phân giải 2.8K (2880 x 1800), mang đến trải nghiệm hình ảnh sắc nét và sống động. Công nghệ OLED với độ phủ màu 100% DCI-P3 giúp hiển thị màu sắc chân thực và rực rỡ, biến mọi tác vụ giải trí và công việc của bạn trở nên thú vị hơn bao giờ hết, đặc biệt là khi xem phim trên Netflix hay chỉnh sửa video trên Photoshop, AI.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("5693f253-e09e-4055-b37a-01145df056ae"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Tốc độ làm tươi 120 Hz mang đến trải nghiệm hình ảnh mượt mà và giảm thiểu hiện tượng mờ hay nhòe. Độ sáng màn hình tinh chỉnh được cao lên đến 600 nits (khi bật HDR) đảm bảo trình hiển thị rõ ràng ở mọi điều kiện sáng, nhiều môi trường khác nhau. Công nghệ DisplayHDR True Black 600 mang đến độ tương phản ấn tượng với màu đen sâu thẳm và sắc trắng rực rỡ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("d48f7fab-5c74-4581-98d2-3bb74b9a6fe9"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Ánh sáng xanh từ màn hình có thể gây hại cho mắt nhưng với công nghệ Low Blue Light, người dùng có thể thoải mái sử dụng trong thời gian dài mà không lo bị mỏi mắt hay mất ngủ. Điều này rất hữu ích cho những người thường xuyên làm việc hoặc giải trí trên máy tính như đọc sách hay duyệt web trên Google Chrome.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("7947fff0-2c55-45ad-acfb-f4f8ee51d553"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Công nghệ Smart AMP khuếch đại âm thanh mạnh mẽ, rõ ràng, kết hợp với hệ thống loa tinh chỉnh bởi Harman Kardon mang đến chất âm sống động. Bạn sẽ được tận hưởng từng giai điệu trong bản nhạc yêu thích hay những âm thanh chân thực trong phim bom tấn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("7aa200ce-20e8-4035-93e2-5bc1cb58f5ae"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Với khối lượng chỉ 1.3 kg và lớp vỏ kim loại - Nhôm màu xanh dương sang trọng, laptop không chỉ bền bỉ mà còn thể hiện phong cách thời thượng. Thiết kế mỏng nhẹ giúp bạn dễ dàng mang theo bên mình, từ quán cà phê, thư viện đến trường học hay văn phòng, tiện lợi sử dụng ở bất cứ đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("f5775130-2f80-47db-a466-a8e755bbe79b"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Bàn phím có đèn nền RGB - 1 vùng rực rỡ và hành trình phím sâu mang lại cảm giác gõ phím êm ái và chính xác ngay cả trong điều kiện thiếu sáng. Tiêu chuẩn Nền Intel Evo trên máy cung cấp nhiều tính năng như: sạc nhanh, bảo mật hơn, duy trì được hiệu suất kết nối cao,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("b3dce4c8-4598-424d-bb59-0644a81a7923"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Hệ thống camera trên laptop Asus Vivobook tích hợp nhận diện khuôn mặt chính xác hỗ trợ truy cập máy nhanh hơn, cùng với đó là công tắc khóa camera giúp bạn tắt camera thủ công khi không sử dụng, tạo sự an tâm khi tham gia các cuộc họp trên Zoom hay Google Meet.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("88bf07d7-c2a7-4f56-bf15-bbf79f946e26"), GadgetId = Guid.Parse("e7a9cf46-8b41-42c9-97d9-90496cfa1c44"), Value = "Laptop học tập - văn phòng có bố trí đầy đủ các cổng kết nối thông dụng như: Jack 3.5 mm, USB 3.2, HDMI, Thunderbolt 4 và khe thẻ nhớ Micro SD. Bạn dễ dàng chia sẻ dữ liệu và kết nối với các thiết bị khác, đáp ứng mọi nhu cầu công việc và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("28f2d4da-95bf-4264-80c9-bead999b85bd"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Bắt đầu cho một kỷ nguyên AI bùng nổ, laptop Asus Vivobook S 15 S5507QA X1E 78 100 (MA089WS) mở ra tính năng tương tác toàn diện cho người dùng với trí tuệ nhân tạo tân tiến, vận hành vượt trội và vẻ ngoài thời thượng. Tham khảo ngay mẫu công cụ làm việc tuyệt vời này tại Thế Giới Di Động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ee2b8791-62ee-481a-b3f5-2c138820073a"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Cấu hình đỉnh cao, trơn tru mọi vấn đề", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("0cfa366a-2559-4b37-bbce-4a5512c0764e"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Laptop Asus được trang bị chip Snapdragon X Elite - X1E 78 100, với 12 nhân và 12 luồng, cùng Qualcomm Adreno GPU. Sự kết hợp này mang lại hiệu suất vượt trội, đáp ứng mọi nhu cầu từ công việc văn phòng, lập trình, thiết kế đồ họa cao, edit video đến giải trí đa phương tiện, tối ưu hóa việc phân luồng giữa các lõi hiệu năng cao và lõi tiết kiệm điện, giúp máy hoạt động mượt mà và tiết kiệm pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("b2824aea-0655-416b-bed8-d0d74f67d64f"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "RAM 32 GB LPDDR5X cung cấp khả năng đa nhiệm tuyệt vời nhờ tốc độ Bus đến 8448 MHz, vận hành được cùng lúc ở nhiều các ứng dụng đồ hoạ, công cụ phát triển phần mềm nặng, ngốn nhiều tài nguyên nhưng vẫn có một tốc độ phản hồi vượt trội.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("ddd36ede-0a4e-41ca-8e1c-bfeed3d42fe1"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Ổ cứng SSD 1 TB trên laptop mang lại không gian chứa rộng rãi cho nhiều ấn phẩm render hay các file dữ liệu, đồng thời SSD với chuẩn NVMe PCIe Gen 4 còn cho tốc độ truy xuất nhanh khi khởi đồng thiết bị, đọc ghi dữ liệu lớn nhanh chóng và hệ thống lưu trữ cũng bảo mật, an toàn hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("a9635207-3801-412c-91ae-1562c94ecfb1"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Kỷ nguyên AI đầy ấn tượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("0dc578de-2aab-4cea-9b93-7130defbc0af"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Hiệu suất công việc nâng cao đáng kể với thế hệ trí tuệ nhân tạo hoàn hảo hơn, vi xử lý AI với hiệu năng TOPS lên đến 45, tương thích với nhiều phần mềm và công cụ từ đồ hoạ, lập trình, giải trí,... tân tiến hiện nay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("cc2c1b8a-cef3-472f-ab8c-e99804c4380c"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Tăng tốc khả năng sáng tạo, tương tác với thiết bị cá nhân với trợ lý ảo Copilot, trả lời và điều hướng hữu ích cho mọi vấn đề hàng ngày của người dùng. Tính năng Recall hỗ trợ tìm kiếm văn bản, website,... dựa trên mô tả cá nhân. Tạo lập phụ đề chính xác cho các cuộc trò chuyện online, video bạn xem qua tính năng Live Captions để không bỏ lỡ bất cứ nội dung quan trọng nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("b9430321-b0d9-466a-ae9e-3ef34f1461ef"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Bên cạnh những tính năng \"bề nổi\", thì hệ thống AI còn được thực hiện thông qua các ứng dụng ASUS AI với khả năng tăng cường bảo mật, tự động điều chỉnh độ sáng màn hình theo sự hiện diện, kéo dài thời lượng sử dụng và khử tiếng ồn thông minh trong các cuộc họp trực tuyến, mang lại trải nghiệm người dùng vượt trội và thuận tiện trong đời sống.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("7ab42cf9-1d4a-4ae9-9ac4-d75d651be630"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Sắc nét và hút mắt hơn qua từng khung hình", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("670adbbb-7c17-457d-b282-b56e61a80852"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Màn hình OLED 15.6 inch với độ phân giải 2.8K (2880 x 1620) của Vivobook S 15 mang lại trải nghiệm hình ảnh sắc nét và sống động. Khung viền mỏng 3 cạnh vừa tối ưu cho diện tích hiển thị hình ảnh, vừa hạn chế đi việc phân tâm trong khi làm việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("e316963e-7e6f-4d8c-b735-8ddab28735d4"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Tần số quét 120 Hz giúp các chuyển động trên màn hình mượt mà, phù hợp cho cả công việc thiết kế và giải trí. Độ phủ màu 100% DCI-P3 tái tạo màu sắc chính xác, hỗ trợ tốt trong sáng tạo và render ấn phẩm đồ hoạ, video. Công nghệ HDR với độ sáng lên đến 600 nits cùng các công nghệ bảo vệ mắt như EYE CARE giúp giảm mỏi mắt khi sử dụng trong thời gian dài, tương thích sử dụng được trong nhiều môi trường khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("b5154689-e237-4db2-b9f0-b0d70930745b"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Laptop Asus Vivobook cũng được trang bị hệ thống loa Harman/Kardon với công nghệ Smart AMP, mang lại âm thanh sống động và rõ ràng, nâng cao trải nghiệm người dùng trong mọi hoạt động xem phim, nghe nhạc đến chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("3052a75a-9967-4e88-b71e-7bd4b331ba75"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Vẻ ngoài đẹp mắt, định hướng chuẩn chuyên gia", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("85cda8e6-090e-4361-a8dc-fda6698ddc5a"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "ASUS Vivobook S 15 gây ấn tượng với lối thiết kế thanh lịch và hiện đại. Máy có bề mặt mỏng, cạnh viền vuông vắn cùng các đường cắt tinh tế và đồng thời được phủ bởi tông màu bạc sang trọng tạo nên vẻ ngoài thời thượng. Độ mỏng chỉ 15.9 mm và khối lượng 1.42 kg, máy tính xách tay dễ dàng mang theo bên mình, dù là trong balo hay túi xách, không gây cồng kềnh hay nặng nề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("1c5a74fa-ecae-4226-9fd1-43f5d0fc5f7c"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Thiết kế Full HD Webcam và camera IR cho phép người dùng tham gia các cuộc họp trực tuyến với chất lượng hình ảnh rõ nét, khả năng nhận diện và mở khoá khuôn mặt ngay cả ở những vị trí thiếu sáng. Ngoài ra, công tắc khóa camera đảm bảo quyền riêng tư qua hỗ trợ đóng mở thủ công khi không sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("47d7ef90-3dcb-4f01-b774-f43871a7af08"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Cá nhân hoá trải nghiệm của bản thân nhờ đèn nền chuyển màu RGB - 1 vùng với đa thiết lập màu sắc và tính năng chiếu sáng. Không chỉ hỗ trợ nhấn gõ tuyệt vời vào đêm tối mà còn mang lại nhiều cảm hứng trong sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("2e4ea6e8-4bd0-45e0-b9f7-5ce803053d81"), GadgetId = Guid.Parse("fa4c0bbf-da11-42b5-8031-c1957a1ac3f8"), Value = "Vỏ laptop cao cấp được làm từ kim loại cứng cáp, mang lại độ bền vượt trội trước các tác động bên ngoài, hạn chế được việc flex các bề mặt và dễ dàng vệ sinh hơn. Dù cạnh máy mỏng nhưng Vivobook S 15 vẫn được trang bị đầy đủ các cổng kết nối thông dụng như: Jack 3.5 mm, USB 3.2, HDMI và USB Type-C với USB 4 đảm bảo khả năng kết nối với các thiết bị ngoại vi dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("e1bdb75f-07ce-47fa-99c3-0bd0b8f421c7"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "Với bộ vi xử lý Intel Core i5 dòng H mạnh mẽ và card đồ họa rời NVIDIA GeForce RTX 2050 4 GB, laptop Asus TUF Gaming F15 FX506HF là một trong những lựa chọn tuyệt vời cho các game thủ và những người dùng làm việc đa tác vụ hoặc liên quan đến đồ họa, kỹ thuật.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4d22d443-0c85-480c-bf35-87007f4792e0"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "Với bộ vi xử lý Intel Core i5 11400H có tốc độ lên đến 4.5 GHz, chiếc laptop Asus TUF Gaming này đảm bảo hoạt động mượt mà và có thể đáp ứng tốt các tác vụ đa nhiệm, mang lại trải nghiệm sử dụng tuyệt vời trong cả công việc lẫn giải trí, chiến game ở mức cấu hình cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("db1b5516-b16d-4a55-a59e-e2f9aef4cadd"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/2976f521-f2ae-4aff-b5df-19b48f33e6c1.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("50522c3a-6219-4361-9b48-d50fd81ad106"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "Asus TUF Gaming F15 được trang bị card đồ họa NVIDIA GeForce RTX 2050 với bộ nhớ đồ họa 4 GB, giúp đáp ứng tốt nhu cầu chơi game cấu hình cao và xử lý các file thiết kế nặng. Từ đó mang lại cho người dùng trải nghiệm chơi game đỉnh cao và hiệu suất làm việc mượt mà.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("d8b95a0a-8d75-47e3-aacc-0510bae931f1"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "Bộ nhớ RAM 8 GB DDR4 với khả năng nâng cấp tối đa lên đến 32 GB đáp ứng nhu cầu chạy đa nhiệm mượt mà, cho bạn tận hưởng những giây phút chiến game đỉnh cao khi có thể mở nhiều ứng dụng từ nghe nhạc, xem phim, game nặng cùng lúc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
