new GadgetDescription { Id = Guid.Parse("9e0b57fa-8c5b-4680-8b67-1739c87f6bb2"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "Với ổ cứng SSD 512 GB, bạn không cần phải lo lắng về dung lượng lưu trữ và đồng thời tận hưởng tốc độ khởi động nhanh chóng cùng khả năng tải ứng dụng mượt mà của chiếc laptop gaming.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("d0da4720-bc34-4347-87de-f34e2dbde025"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "Màn hình 15.6 inch độ phân giải Full HD (1920 x 1080) và tốc độ làm mới 144 Hz mang lại hình ảnh sắc nét và mượt mà, cho trải nghiệm chơi game tuyệt vời, đặc biệt là các game có tốc độ khung hình cao. Đồng thời tính năng chống chói Anti Glare giúp hạn chế hiện tượng ánh sáng phản chiếu khi sử dụng trong điều kiện ánh sáng mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
