new GadgetDescription { Id = Guid.Parse("b34861f9-04aa-4a70-a569-d2bf38c092d4"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "Bên cạnh đó, laptop còn được trang bị hệ thống âm thanh DTS software tiên tiến, mang lại cho bạn trải nghiệm âm thanh sống động và chân thực hơn bao giờ hết khi sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a767789b-5079-468f-a6f1-b257b9889192"), GadgetId = Guid.Parse("d1510413-2b6c-4c4d-9ad1-5fea30c70f5b"), Value = "Laptop Asus còn có lối thiết kế hầm hố với sắc đen nam tính, tạo nên sự mạnh mẽ và đậm tính thể thao. Điểm nhấn của bàn phím là đèn nền RGB có thể chuyển đổi màu sắc, tạo điểm nhấn độc đáo khi sử dụng nơi đông người.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
