new GadgetDescription { Id = Guid.Parse("36d540b0-b388-42c4-9f23-aed22472ad31"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Laptop Asus Gaming TUF Dash F15 FX517ZE i5 (HN045W) không chỉ cùng mình chinh phục mọi đấu trường ảo nhờ bộ vi xử lý Intel Gen 12 và card đồ họa rời NVIDIA GeForce RTX 3050 Ti 4 GB mà còn là một chiếc laptop đồ họa - kỹ thuật lý tưởng, sẵn sàng hỗ trợ mình xử lý mọi công việc.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("1f056f18-65da-4fd1-bafc-30ec2e6655fb"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Bùng nổ hiệu năng cùng bộ vi xử lý Intel Gen 12", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("b1645dc6-6772-4611-b912-891340ad0658"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Laptop Asus TUF Gaming mang trong mình hiệu năng vượt trội đến từ bộ vi xử lý Intel Core i5 12450H, đạt số điểm ấn tượng 10817 điểm đa nhân và 1688 điểm đơn nhân khi mình đo hiệu năng bằng công cụ Cinebench R23, sẵn sàng cân mọi tác vụ học tập, văn phòng từ nhẹ đến nặng. Bên cạnh đó, card rời GeForce RTX 3050 Ti 4 GB với điểm số 3DMark mình đo đạc được tổng 5856 điểm GPU cũng cho phép mình làm chủ mọi chiến trường ảo hay xử lý trơn tru các tác vụ thiết kế, đồ họa. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("be48ab1d-dc93-47df-84a0-7f6428a23f35"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Laptop RAM 8 GB đủ để đáp ứng nhu cầu học tập và giải trí khi mình hoàn toàn có thể vừa chơi game vừa nghe nhạc Remix trên YouTube hay mở hơn 10 tabs trình duyệt cùng lúc. Trong thời gian tới mình chắc chắn sẽ nâng cấp RAM bởi em máy hỗ trợ thêm 1 khe RAM trống cùng khả năng nâng cấp lên tối đa 32 GB, chắc chắn sẽ phục vụ hoàn hảo mọi nhu cầu sử dụng của mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("ffe37e94-ec92-4331-bdb1-2afc117daeff"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/077a9470-2949-4005-beb7-e0bf07d1747f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("24d12087-3fea-4fdb-9fb4-9012a44e677d"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Không gian ổ cứng 512 GB SSD NVMe PCIe cho phép mình tải về và lưu trữ một vài tựa game thường hay chơi và dữ liệu cần thiết cho việc học, do hỗ trợ tốc độ truyền tải cao nên mình mở máy và ứng dụng cũng đều nhanh chỉ trong vài giây. Máy hỗ trợ thêm 1 khe cắm SSD M.2 PCIe mở rộng khác cho những bạn nào có nhu cầu lưu trữ nhiều phim ảnh hay tài liệu quan trọng, còn đối với một người dùng là học sinh như mình thì 512 GB vẫn đủ để sử dụng trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("c8525f97-822b-4e92-a1fe-90145bd8fc4f"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Màn hình 144 Hz giải trí đỉnh cao", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("4b586ed9-577f-4587-a603-1c6f30636377"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Laptop Asus mang đến cho mình một không gian hiển thị với kích thước 15.6 inch, hỗ trợ độ phân giải Full HD (1920 x 1080) đi cùng những công nghệ tiên tiến như tấm nền IPS nâng cao góc nhìn lên đến 178 độ, cho phép mình chia sẻ nội dung hiển thị trên màn hình với những người bạn ngồi kế bên hay công nghệ chống chói Anti Glare bảo vệ thị giác cũng như hạn chế hiện tượng ánh sáng hắt vào màn hình gây khó chịu, bạn nào thường xách máy ra quán cà phê làm việc sẽ rất cần đến tính năng này.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("4d7ea17d-5c6c-47bb-9f4d-678b32f29b16"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b2f17c2f-8a17-477f-af16-613f79717bbf.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("594fbe08-3caf-4fb6-a2e5-a745fc510012"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Sự cộng hưởng của tần số quét 144 Hz và công nghệ Adaptive-Sync đồng bộ tốc độ khung hình của card rời RTX 30 series với màn hình cho mình một cảm giác thích thú mỗi khi có thời gian rảnh giải trí chơi game. Tốc độ làm tươi cao tạo cho mình nhiều lợi thế hơn hẳn so với đối thủ khi chơi các tựa game FPS cao, khi xoay chuột chỉnh góc nhìn sẽ rất mượt mà, đồng thời cũng hạn chế được tình trạng nứt hình, xé hình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("3f10eb53-3a5f-4426-8dcd-b5a1525c9b7c"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Thiết kế hiện đại, đậm chất gaming", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("321e4e13-0f6e-41b4-abb8-dbbbdba38a74"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Là một chiếc máy chuẩn gaming nên chắc chắn Asus TUF không thiếu cổng kết nối, dọc hai bên máy có cổng USB 3.2, Thunderbolt 4, Jack tai nghe 3.5 mm, HDMI, LAN (RJ45) và USB Type-C phục vụ hoàn hảo cho cả nhu cầu học tập, giải trí của mình khi có thể kết nối cùng lúc với chuột, tai nghe, bàn phím và mạng có dây. Cổng HDMI và Thunderbolt 4 cũng hỗ trợ mình xuất hình ảnh lên màn hình lớn để xem phim, chiến game cho thoải mái hơn, đôi khi cũng hữu ích khi mình xuất bài thuyết trình lên máy chiếu ở các buổi học trên trường.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("899c08f8-928c-4dd6-81c9-165a538c630e"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3c548d48-07be-4da4-9dd0-564f5240cda8.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("259e5f67-6086-48a7-a45e-7951c9144fef"), GadgetId = Guid.Parse("3107f7b5-bd87-45e1-be15-72644b7a0605"), Value = "Sở hữu bộ vi xử lý Intel Gen 12 mạnh mẽ, card đồ họa rời NVIDIA cho khả năng chiến game chuyên nghiệp, Asus Gaming TUF Dash F15 FX517ZE i5 (HN045W) sẵn sàng hỗ trợ mình thực hiện mọi công việc được giao, là người bạn đồng hành lý tưởng cùng mình trong suốt quãng đường đại học.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("01d24230-7046-40a6-8ec3-e715f8b8e359"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Laptop ASUS TUF Gaming A15 FA507UV R7 8845HS (LP142W) là cỗ máy chiến binh mạnh mẽ, đáp ứng mọi nhu cầu từ chơi game, đồ họa đến lập trình khi sở hữu GPU RTX 40 series và CPU AMD Ryzen 7. Đây là lựa chọn hoàn hảo cho những ai đang tìm kiếm một chiếc laptop gaming mạnh mẽ, đa năng, sẵn sàng chinh phục mọi thử thách trong game và công việc.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("04afbf52-d4f4-46d1-95c6-5a78a9b83976"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Laptop Asus này sở hữu bộ vi xử lý AMD Ryzen 7 - 8845HS mạnh mẽ với 8 nhân và 16 luồng xử lý. Nhờ cấu trúc này, con chip mang đến khả năng đa nhiệm vượt trội, cho phép bạn thoải mái thực hiện nhiều tác vụ cùng lúc. Không dừng lại ở đây, CPU với tốc độ xung nhịp tối đa lên đến 5.1 GHz có thể xử lý mượt mà mọi tác vụ, từ những công việc văn phòng đơn giản đến các ứng dụng đồ họa nặng hay những tựa game bom tấn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("1b4b24c4-df79-4231-a32a-300e3462a3c4"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Nâng tầm trải nghiệm đồ họa của bạn lên một tầm cao mới với GPU NVIDIA GeForce RTX 4060 8 GB mạnh mẽ được trang bị trên chiếc laptop này. Bạn sẽ chiến đấu mượt mà trong những tựa game bom tấn cấu hình cao với đồ họa sắc nét. Tự tin xử lý các tác vụ nặng như dựng phim, chỉnh sửa ảnh, thiết kế 3D,... một cách mượt mà và hiệu quả, biến mọi ý tưởng sáng tạo của bạn thành hiện thực một cách hoàn hảo nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("231417de-bdd5-41b0-98ab-5a2692a61d6a"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6525f17b-ea23-4ea8-8830-933b80ff856e.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("58d631a0-af9a-46bb-aab0-693347c07cb9"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Được trang bị RAM 16 GB DDR5 tốc độ 4800 MHz mạnh mẽ, chiếc laptop này mang đến cho bạn trải nghiệm đa nhiệm mượt mà, loại bỏ hoàn toàn tình trạng giật lag hay đơ máy. Đối với người dùng văn phòng thì bạn có thể soạn thảo văn bản, bảng tính, thuyết trình cùng lúc mà không lo bị gián đoạn. Những trận game với mức cấu hình cao cũng được xử lý trơn tru, các game thủ có thể thỏa sức tung hoành khắp mặt trận. Đồng thời, bạn có thể sử dụng các phần mềm đồ họa đòi hỏi nhiều tài nguyên một cách mượt mà.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("834cce14-7eba-4c1b-8acb-cdb93a309d38"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Laptop Asus TUF Gaming có không gian lưu trữ khá lớn với ổ cứng 512 GB SSD NVMe PCIe Gen 4. Loại ổ cứng này cũng có tốc độ đọc ghi nhanh chóng, tối ưu thời gian cho bạn trong quá trình sử dụng. Bạn có thể tháo ổ cứng có sẵn trong máy và thay bằng ổ cứng khác có dung lượng tối đa 2 TB để mở rộng không gian lưu trữ khi cần thiết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("1c3f2872-6eea-41a4-a2eb-b410998bc2df"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/dfd8767f-214e-4e68-a1aa-9272cfa3e24b.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("4ab5f6cd-3b83-41df-a917-73f88fd18e9e"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Cho dù bạn là dân văn phòng, học sinh, sinh viên, designer hay game thủ, chiếc màn hình 15.6 inch với độ phân giải Full HD cũng sẽ mang đến cho bạn trải nghiệm thị giác tuyệt vời. Tấm nền IPS mang đến góc nhìn rộng lên đến 178 độ, cho phép bạn nhìn rõ hình ảnh từ mọi vị trí. Màu sắc được tái hiện một cách chính xác và chân thực, mang đến trải nghiệm hình ảnh sống động như thật.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("606189e9-3fe8-4f6d-bbdd-86cf05db4428"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Với tần số quét 144 Hz, màn hình laptop này như một cánh cửa dẫn bạn vào thế giới game mượt mà, sắc nét chưa từng có. Mỗi chuyển động, mỗi pha hành động đều diễn ra một cách trơn tru, không hề có hiện tượng giật lag hay xé hình nhờ công nghệ G-Sync. Bạn sẽ đắm chìm hoàn toàn vào những tựa game hành động, đua xe tốc độ cao mà không bỏ lỡ bất kỳ chi tiết nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("d0040237-31f1-4126-9543-092d12f520f7"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Với công nghệ chống chói Anti Glare, bạn có thể thoải mái làm việc hoặc giải trí trên chiếc laptop này ở bất cứ đâu, bất cứ khi nào bạn muốn. Dù là dưới ánh nắng gay gắt của buổi trưa hay trong căn phòng tối, màn hình vẫn hiển thị rõ nét, không bị lóa, bảo vệ đôi mắt của bạn. Độ sáng 250 nits đủ để bạn nhìn rõ mọi chi tiết, kể cả khi ở ngoài trời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("85e4aa75-45bc-4897-affd-548d188571d6"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3ea07348-8375-48a3-885f-a9864ea48c53.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c3bd5e07-065e-43fa-b8b1-2d284a8f1fd2"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Nếu bạn đang xem một bộ phim bom tấn với âm thanh vòm Dolby Atmos, từng tiếng súng, tiếng nổ đều vang lên một cách sống động, chân thực đến từng chi tiết. Hay bạn đang thưởng thức một bản nhạc cổ điển với chất lượng Hi-Res Audio, từng nốt nhạc đều được tái tạo một cách tinh tế, trong trẻo. Đó chính là những gì bạn sẽ trải nghiệm với chiếc laptop này.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("b555f8f5-f22a-43e2-bafa-1620b50aa7f0"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Với thiết kế góc cạnh, lớp vỏ nhựa xám mạnh mẽ và những đường nét cá tính, chiếc laptop này như một tuyên ngôn cho phong cách của bạn. Dù bạn là một game thủ đam mê chinh phục những thử thách hay một người làm việc sáng tạo, chiếc laptop này đều đáp ứng được nhu cầu của bạn. Và với khối lượng 2.2 kg, bạn có thể mang nó theo bên mình mọi lúc mọi nơi, biến mọi không gian thành không gian làm việc hoặc giải trí của riêng mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("c477cada-b8e1-441c-9b34-b378537e7f9c"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Bàn phím của laptop này là sự kết hợp hoàn hảo giữa công nghệ và thẩm mỹ. Cảm giác gõ phím êm ái, độ nảy tốt cùng với đèn nền chuyển màu RGB đa màu sắc tạo nên một trải nghiệm người dùng tuyệt vời. Bốn phím WASD được thiết kế đặc biệt, giúp bạn dễ dàng điều khiển nhân vật trong game với độ chính xác cao. Cho dù bạn là game thủ chuyên nghiệp hay người dùng văn phòng, bàn phím này đều đáp ứng được.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("561c1cb0-338f-4354-a3e1-8255ff3d83c8"), GadgetId = Guid.Parse("57b33510-99c8-4d3f-a5cf-96ebef7372e7"), Value = "Hệ thống cổng kết nối đa dạng của chiếc laptop này đáp ứng mọi nhu cầu của người dùng, từ làm việc đến giải trí. Với USB Type-C, Jack tai nghe 3.5 mm, USB 3.2, HDMI và LAN cho phép bạn kết nối với các thiết bị khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("786f97f9-09fc-4edf-a834-609f2fc79cfa"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Mẫu sản phẩm doanh nhân dẫn đầu tương lai, laptop Asus Zenbook 14 OLED UX3405MA Ultra 9 185H (PP475W) với dòng chip Intel Ultra 9 tích hợp AI hiện đại, nội dung hiển thị xuất sắc trên khung hình 2.8K,... sẽ mang đến cho bạn nhiều trải nghiệm sử dụng thật tuyệt vời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("693dec21-76dd-4e75-91ce-4f6ee8bddfb4"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Sang trọng, mỏng nhẹ bất ngờ ", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("16ca965c-025e-4389-92dd-7e5c3d1439d7"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Thiết kế của laptop cao cấp - sang trọng phô diễn vẻ đẹp của sự giản lược, tạo nên một diện mạo hiện đại cao cấp. Máy có đường nét chắc chắn, góc cạnh rõ ràng và được làm nổi bật bởi những đường cắt độc đáo trên nắp lưng. Kích thước siêu mỏng chỉ 14.9 mm và khối lượng nhẹ nhàng 1.2 kg, Zenbook 14 OLED dễ dàng gói gọn trong balo, nhẹ nhàng như việc bạn đang mang theo một quyển sách.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("ac0150d2-6810-4928-9b54-8623c3836437"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Laptop Asus Zenbook còn đảm bảo sự linh hoạt trong công việc với Webcam Full HD và camera IR, bạn có thể kiểm soát việc sử dụng camera với công tắc khoá, đảm bảo quyền riêng tư của hình ảnh cá nhân. Ngoài ra, khả năng nhận diện khuôn mặt được tích hợp giúp bạn mở khoá máy nhanh chóng và an toàn hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("da8ee929-44b3-4c33-8b8f-903e44907c07"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/bc75e952-fcd6-42ae-985c-21b76c678d1c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("f55fbff8-756a-4378-ad4c-0395d9472326"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Tính năng nâng cao với Numberpad được tích hợp ngay trên touchpad giúp nhập liệu số dễ dàng và đèn nền bàn phím hỗ trợ bạn làm việc ngay cả trong điều kiện thiếu sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("c75688bd-7c9b-433e-9bb1-9a7001b53bd9"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Vỏ máy làm từ nhôm cao cấp, đạt chuẩn quân sự MIL STD 810H, cho phép thiết bị chịu đựng tốt các tác động từ môi trường như nhiệt độ cao hay va đập. Mặc dù mỏng, máy vẫn cung cấp đầy đủ các cổng kết nối cần thiết như HDMI, Jack tai nghe 3.5 mm, USB 3.2 và Thunderbolt 4, đảm bảo khả năng liên kết tốt với các thiết bị ngoại vi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("0192f7e3-d6a6-4efa-9f9f-cff2e9e1fb9e"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Khung hình sống động, sắc nét ", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("0e3fe8df-0af6-4218-b35a-b82610dfd4f1"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Màn hình laptop như một bức tranh sống động với kích thước 14 inch cùng tỷ lệ khung hình 16:10 hợp lý mang lại không gian hiển thị thoải mái, cho phép bạn quan sát, sáng tạo với các cửa sổ làm việc cùng lúc. Tần số quét 120 Hz đảm bảo mọi hình ảnh chuyển động mượt mà, không giật, mướt mắt hơn hẳn kể cả với những nội dung chuyển động tốc độ cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("204b9691-68a0-4399-b581-e750791dd237"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Với dải màu chuẩn 100% DCI-P3 cùng mức phân giải 2.8K, mỗi màu sắc được tái hiện trên màn hình đều chân thực đến từng chi tiết, làm cho công việc thiết kế và đồ họa của bạn trở nên sống động và chính xác. Công nghệ HDR với độ sáng lên đến 600 nits, cùng với EYE CARE và Low Blue Light, bảo vệ đôi mắt bạn khỏi ánh sáng xanh, giảm mệt mỏi cho những giờ làm việc, giải trí thoải mái.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("21897ef9-844a-463d-ac41-f448e9f24849"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/0682171f-ad4d-43c9-b5f5-ccafc2a49869.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("dee6e877-bc60-4762-9adc-ae72248fa7e5"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Sự kết hợp hoàn hảo giữa loa siêu tuyến tính và sự tinh chỉnh từ Harman/Kardon cùng công nghệ Smart AMP, mang đến trải nghiệm âm thanh đỉnh cao, phong phú và chi tiết, phục vụ tốt cho cả công việc, giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("2c47fc76-bd6c-4f9a-81f6-41e0b1bcbdc2"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Hiệu năng ấn tượng, xử lý thông minh", Type = ParseGadgetDescriptionType("BoldText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("a991e7b1-6596-4c93-844c-807798d93361"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Bộ vi xử lý Intel Core Ultra 9 185H thuộc dòng chip thế hệ thứ 14, mang lại sức mạnh vượt trội với 16 nhân và 22 luồng. Sự kết hợp hoàn hảo với GPU Intel Arc Graphics giúp máy xử lý mượt mà mọi tác vụ từ công việc văn phòng đến thiết kế đồ họa chuyên nghiệp trên nhiều nền tảng như: Photoshop, Premiere, Ai,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("78dd6c85-d97e-4451-bf9b-985781165d7e"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/08c5448c-bbbc-46e3-9d30-5a8c921fc58b.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("d75b5336-f254-4141-b1fd-107bd08dc29c"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Chip mới này còn được cải tiến với NPU - nhân xử lý AI tiên tiến, tự động điều chỉnh hiệu suất phù hợp với từng nhu cầu sử dụng, đồng thời quản lý năng lượng hiệu quả, kéo dài thời gian hoạt động của máy. Các tính năng AI đáng chú ý bao gồm nhận diện vật thể thông minh, tăng tốc độ chỉnh sửa đồ họa, lọc tiếng ồn cho cuộc họp trực tuyến rõ ràng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("17ac93e5-a61c-4c69-8c71-98aa45420ed7"), GadgetId = Guid.Parse("18a4c9d1-700c-488b-8edb-3a2aaa428c5c"), Value = "Không chỉ có vậy, laptop Asus còn sở hữu RAM 32 GB LPDDR5X và ổ cứng SSD 1 TB NVMe PCIe Gen 4, cho phép bạn xử lý nhanh chóng mọi tác vụ từ làm việc, học tập, giải trí, cho đến chơi game, duyệt web hàng ngày. Với không gian lưu trữ lớn, bạn có thể lưu trữ mọi dữ liệu mà không lo lắng về dung lượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("6b066e5a-2c7a-4fb0-b408-489894a9ed67"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "Laptop Asus Gaming Vivobook K3605ZC i5 12500H (RP629W) nổi bật trong phân khúc tầm trung với bộ cấu hình mạnh mẽ, màn hình sắc nét và thiết kế sang trọng, chiếc máy này chắc chắn sẽ làm hài lòng ngay cả những người dùng khó tính nhất trong thị trường laptop gaming hiện đại.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("5853949d-7a82-476e-b112-0ccaa0547d73"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "Laptop được trang bị vi xử lý Intel Core i5 Alder Lake - 12500H, kết hợp cùng card đồ họa rời NVIDIA GeForce RTX 3050 4 GB. Sự kết hợp này không chỉ mang đến khả năng xử lý đa nhiệm mạnh mẽ mà còn đảm bảo hiệu suất cao cho mọi tác vụ, từ làm việc văn phòng, thiết kế đồ họa 2D, edit video thông dụng và lập trình phát triển phần mềm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("86b1cb91-a578-46af-b97d-94231ff75ce1"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/faaf1576-812d-4988-b5b9-636829f068af.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("faae78f2-8705-4996-a4ba-3bc0686872fd"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "Ngoài ra, bộ cấu hình này trên chiếc laptop gaming vẫn đủ khả năng đáp ứng được việc chiến các con game phổ biến như Valorant, LOL, FO4 ở các mức setting hợp lý, đáp ứng yêu cầu làm việc và giải trí toàn diện hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("c56ccbe1-ba70-493f-9af5-c939cf14c1d6"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "Ổ cứng SSD NVMe PCIe với dung lượng 512 GB cung cấp tốc độ truyền dữ liệu cao, giảm thời gian chờ đợi khi truy xuất tài liệu và nâng cao hiệu quả làm việc. Bộ nhớ RAM 16 GB có thể nâng lên 24 GB giúp bạn xử lý mượt mà mọi tác vụ từ văn phòng đến phân tích số liệu phức tạp mà không gặp tình trạng đơ hay giật lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("226eefeb-f066-41fb-8724-0256d6bcfda2"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "Màn hình của laptop Asus có kích thước 16 inch độ phân giải WUXGA (1920 x 1200) tạo ra hình ảnh chi tiết và sắc nét. Tỷ lệ khung hình lớn mang lại phần nhìn hiệu quả hơn với các dạng nội dung văn bản, giải trí hay xem phim đều đã mắt. Với tần số quét 144 Hz hỗ trợ trải nghiệm trình chiếu hình ảnh động mượt mà, sắc nét và nhạy bén.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("135cb61c-0a58-429d-a67a-f3e3e2895200"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e7fc1d08-bb02-47ee-889f-52c6484fe1a7.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("6211a230-9f57-42cd-99b6-1fc2c7145446"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "Asus Gaming Vivobook còn sở hữu công nghệ chống chói Anti Glare giảm ánh sáng phản chiếu, giúp hình ảnh trở nên rõ nét và dễ nhìn hơn trong môi trường có ánh sáng mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("1c3c264c-6efb-432c-882c-884cf6bf7ae0"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "Âm thanh trên laptop được tăng cường qua SonicMaster audio, mang đến trải nghiệm âm thanh mạnh mẽ và sống động, tăng cường việc thưởng thức thông qua thính giác, xem phim và chơi game trở nên chân thực và thú vị.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("a6ad0b1a-edc8-4670-bbe2-b7f85c7fb3b8"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/99fc1a94-56d4-4928-9e0c-2c59183dcd38.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("73cc6d32-902a-43e6-aac0-8e9080b98f61"), GadgetId = Guid.Parse("56a9319b-297c-4ae3-84c3-d6b30c8478ce"), Value = "Máy được cấu tạo vô cùng bền bỉ với lớp vỏ bên ngoài bằng nhựa, nắp lưng kim loại cứng cáp và thẩm mỹ, đặc biệt tổng thể còn đạt độ bền chuẩn quân đội MIL STD 810H khi thông qua các kiểm nghiệm chống sốc, nhiệt,... Đèn nền bàn phím cũng được trang bị phòng những trường hợp mất điện hoặc phải dùng máy trong bóng tối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
