new GadgetDescription { Id = Guid.Parse("3599b415-d4cf-49d4-913b-e72e3dd936fe"), GadgetId = Guid.Parse("ec646b95-f5b9-41f4-ae19-58ae42f3147e"), Value = "Màn hình 15.6 inch rộng rãi cho phép bạn làm việc trên nhiều cửa sổ, kết hợp hoàn hảo với tấm nền LED Backlit cho chất ảnh sáng và tươi hơn, công nghệ chống chói Anti Glare và độ phân giải Full HD (1920 x 1080) khiến mọi nội dung đều được tái tạo hoàn hảo, độ chi tiết cao với màu sắc chân thực và nịnh mắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("b2b0c137-253c-4ac9-889c-9f5f265eecca"), GadgetId = Guid.Parse("ec646b95-f5b9-41f4-ae19-58ae42f3147e"), Value = "Trải nghiệm khung hình chuyển động rõ nét ở tốc độ cao nhờ màn hình tần số quét 120 Hz, tái hiện từng chi tiết của một hành động, mượt mà từ các hoạt động giải trí, cuộn, lướt cho đến chơi những tựa game có tiết tấu nhanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
