new GadgetDescription { Id = Guid.Parse("ad3ba98b-6e59-4349-a274-9a6f78e412f0"), GadgetId = Guid.Parse("ec646b95-f5b9-41f4-ae19-58ae42f3147e"), Value = "Đa dạng các cổng kết nối: USB 2.0, USB 3.2, HDMI, Jack tai nghe 3.5 mm và khe đọc thẻ nhớ SD.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("143e670f-9139-4981-a31b-3284139d068e"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Bạn đang tìm kiếm chiếc laptop giải quyết được nhiều tác vụ từ học tập đến công việc nhưng chỉ với mức giá dưới 20 triệu thì laptop Dell Inspiron 15 3530 i5 1334U (71043885) là sản phẩm phù hợp với bạn. Máy không những sở hữu hiệu năng mạnh mẽ từ chip Intel thế hệ 13, thiết kế hiện đại mà còn đi kèm Office Home & Student vĩnh viễn và Keep Your Hard Drive tiện lợi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("5c3c1475-7195-4577-9c56-39020c870d93"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Với chip Intel Core i5 1334U, máy mang đến hiệu suất vượt trội cho mọi hoạt động hàng ngày. Với công nghệ Turbo Boost, máy có thể đạt tốc độ tối đa 4.6 GHz, giúp xử lý nhanh chóng các tác vụ văn phòng như soạn thảo văn bản, tạo bảng tính trên Microsoft Office, Google Workspace,... cho đến những công việc đòi hỏi hiệu suất cao hơn như xử lý đồ họa 2D, render video ngắn,... Mọi thao tác trên máy đều diễn ra mượt mà và linh hoạt, mang đến trải nghiệm sử dụng ấn tượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("78710fbb-c621-4d34-a408-132911c20ca6"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Laptop Dell còn được tích hợp card đồ họa Intel Iris Xe Graphics, mang lại chất lượng hình ảnh ấn tượng khi xem phim, chơi game hay sử dụng các phần mềm đồ họa phổ biến như Photoshop, Canva,... Ngoài ra, máy còn đảm bảo khả năng chơi mượt mà các tựa game phổ thông đang được ưa chuộng hiện nay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("b45d4178-fea3-4305-956d-1f826b2711a9"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Với RAM 16 GB, laptop cho phép bạn mở nhiều tab trình duyệt và chạy đồng thời nhiều ứng dụng mà hạn chế gặp phải tình trạng chậm hay giật máy. Ổ cứng SSD 512 GB NVMe PCIe không chỉ cung cấp dung lượng đủ dùng mà còn có tốc độ đọc và ghi nhanh, giúp mọi thao tác trở nên nhanh chóng và mượt mà hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("aa1e11c7-c35f-4e37-a59e-1dbc11a54da9"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Dell Inspiron 15 3530 được trang bị màn hình 15.6 inch với độ phân giải Full HD (1920 x 1080), mang đến hình ảnh rõ nét và chi tiết. Tấm nền WVA cung cấp màu sắc trung thực cùng góc nhìn rộng, thuận tiện khi bạn muốn chia đôi cửa sổ để làm việc đa nhiệm. Đặc biệt, tần số quét 120 Hz cho phép hiển thị mượt mà, tái hiện chính xác những chuyển động nhanh mà không bị mờ hay nhòe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("58273acc-2118-4c1e-a7f5-6c9f6526a928"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Công nghệ Realtek Audio mang lại trải nghiệm sống động với âm thanh rõ ràng và chi tiết. Chất lượng âm thanh chân thực giúp bạn thư giãn thoải mái khi nghe nhạc, xem phim hay thưởng thức các nội dung giải trí khác sau một ngày làm việc căng thẳng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("d85dffac-f4ce-46b8-aeb3-1027c820f063"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Laptop Dell Inspiron sở hữu thiết kế thanh lịch với khối lượng chỉ khoảng 1.66 kg và kiểu dáng gọn nhẹ, chiếc laptop này là người bạn đồng hành lý tưởng cho những ai thường xuyên di chuyển. Máy dễ dàng được đặt trong balo hoặc túi xách, thuận tiện mang theo từ nơi làm việc đến quán cà phê hay lớp học.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("f718759f-6eae-4b2f-8dff-1bc6fcbbeffd"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Chiếc laptop học tập - văn phòng này được tích hợp nhiều cổng kết nối đa dạng như: HDMI, USB 2.0, USB 3.0, Headphone/microphone và khe đọc thẻ SD, giúp bạn dễ dàng kết nối với các thiết bị ngoại vi như chuột, bàn phím hoặc ổ cứng di động. Các cổng được bố trí hợp lý, mang lại sự tiện lợi khi sử dụng mà không gây bất tiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("d0580807-adcb-421b-a9a1-b1c1cf45d7e6"), GadgetId = Guid.Parse("6da3b280-467f-4a16-bd30-84d99ce3361c"), Value = "Laptop còn sở hữu chế độ bảo hành ổ cứng KYHD (Keep Your Hard Drive), khi ổ cứng bạn gặp vấn đề cần phải thay ổ cứng mới, hãng sẽ cho bạn giữ lại ổ cứng cũ mà không cần phải thu lại. Thật tiện lợi cho bạn trong việc bảo quản các dữ liệu cá nhân, tránh bị mất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("aac5bc72-cb0d-4dfd-9236-139097cbfd13"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Mẫu máy tính xách tay quốc dân, laptop Dell Inspiron 15 3530 i7 1355U (N5I7301W1) - một công cụ làm việc hữu ích không chỉ đáp ứng mọi nhu cầu làm việc mà còn hỗ trợ cho giải trí đa phương tiện hoàn hảo. Hãy khám phá chiếc laptop này với những tính năng vượt trội và thiết kế ấn tượng chỉ với mức giá cực lý tưởng tại Thế Giới Di Động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("6e5eaa8e-41d2-404a-a41b-5cf9f1672883"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Inspiron 15 3530 cấu tạo với lối thiết kế hiện đại, tinh tế, lớp vỏ ngoài bằng nhựa cao cấp không chỉ bền bỉ mà còn mang lại vẻ ngoài sang trọng và chuyên nghiệp. Với lớp vỏ màu đen đẹp mắt, máy vô cùng phù hợp với môi trường làm việc từ văn phòng đến các không gian học tập, thư viện. Tính năng bảo mật vân tay mở máy nhanh chóng và an toàn hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("699fc62c-f8af-477a-b8d8-57354f4855ba"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/182f567a-ce99-4a57-bb9d-a63ad8f3c77b.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("5c33895a-5e61-45be-8260-2019104696db"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Với khối lượng chỉ 1.65 kg và kích thước gọn nhẹ, đây là người bạn đồng hành lý tưởng cho những ai thường xuyên di chuyển. Bạn có thể dễ dàng bỏ máy vào balo hay túi xách và mang theo đến bất kỳ đâu, từ văn phòng đến quán cà phê hay lớp học. Thiết kế bo cong viền với độ mảnh mai cũng phần nào hạn chế được vấn đề cấn móp khi bỏ trong không gian hẹp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("72df8c9c-488d-43c8-9988-f431798d27cf"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Laptop Dell Inspiron được trang bị nhiều cổng kết nối đa dạng bao gồm: USB Type-C, USB 2.0, USB 3.2,... giúp bạn dễ dàng kết nối với các thiết bị ngoại vi, từ chuột, bàn phím đến ổ cứng di động, đảm bảo mọi nhu cầu kết nối của bạn đều được đáp ứng. Các cổng kết nối được bố trí một cách hợp lý, giúp bạn dễ dàng sử dụng mà không gặp bất kỳ vướng víu và trở ngại nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("05289ac7-2068-4ea6-ad6d-312a5e455a86"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/5c0a4197-3917-4d4a-95bb-64541584e315.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("9689c79c-4255-4e8d-9a02-27c4bd14608a"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Laptop học tập - văn phòng sử dụng sức mạnh từ chip Intel Core i7 1355U thế hệ 13 cho hiệu suất mạnh mẽ, đáp ứng mượt mà mọi nhu cầu từ công việc văn phòng, xử lý đồ họa đến giải trí đa phương tiện và chơi game. Với tốc độ xung nhịp cao tối đa 5.0 GHz với Turbo Boost và khả năng xử lý đa nhiệm vượt trội, bạn có thể dễ dàng thực hiện nhiều tác vụ cùng lúc, xử lý tốt hơn ở các công cụ đồ hoạ, lập trình chuyên sâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("ec74b493-116d-46c9-8614-ad8e1098b6e2"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Với card đồ họa tích hợp Intel Iris Xe Graphics cung cấp hiệu suất đồ họa tốt cho chất lượng video, làm việc hiệu quả với nhiều công cụ trên nền tảng Adobe, Figma,... Bên cạnh đó, bạn còn có thể vô tư chơi các con game phổ thông hấp dẫn như: FO4, LOL, Valorant,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("c101ac70-2e32-4cb8-b446-e5f40e670a77"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/56ea870f-b33c-4a7a-8df0-68310530dade.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("de09a261-957b-41d9-81e6-3df20fbfc9a4"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Laptop Dell có gắn RAM 16 GB, chia đều vào hai khe 8 GB cho phép kích hoạt Dual Channel, tăng tốc độ truyền tải dữ liệu và cải thiện hiệu năng tổng thể. Điều này bổ trợ cho việc đa nhiệm thêm hiệu quả, thực hiện công việc với cùng lúc nhiều cửa sổ khác nhau mà không bị gián đoạn hay giật lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("f77f7365-254e-4a85-a632-a76235abdea0"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Ổ cứng SSD NVMe PCIe dung lượng 512 GB cung cấp không gian lưu trữ rộng rãi cho tài liệu, hình ảnh, video và phần mềm, thoải mái tháo lắp và nâng cấp lên tối đa tận 2 TB. Dạng ổ SSD này cũng hỗ trợ tốc độ truy xuất cao hơn các dạng ổ HDD truyền thống, đặc biệt hữu ích trong giải quyết các file dữ liệu lớn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c3abed0a-ffba-4cd2-b50c-c8f58bf91f61"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Màn hình 15.6 inch Full HD (1920 x 1080) hỗ trợ tốc độ làm tươi 120 Hz đảm bảo hình ảnh rõ nét, mượt mà và sống động. Tấm phủ WVA giúp mở rộng góc nhìn, cho phép bạn thưởng thức hình ảnh từ mọi góc độ mà không bị biến dạng màu sắc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("4737d2c5-780b-4ac4-bef9-0dbbd9903d30"), GadgetId = Guid.Parse("d0855f47-37f4-440a-9ce9-9e78fccc411a"), Value = "Hệ thống âm thanh của Dell Inspiron 15 3530 với Realtek Audio mang đến trải nghiệm âm thanh sống động, rõ nét, giúp bạn tận hưởng trọn vẹn những phút giây giải trí với âm nhạc, phim ảnh hay chơi game. Chất âm mạnh mẽ sẽ làm phong phú thêm trải nghiệm đa phương tiện của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("ebdaf4ef-c921-4d76-a668-de0eb29be98f"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Là một sinh viên hoặc nhân viên văn phòng, bạn thường xuyên phải đối mặt với khối lượng công việc và bài tập khổng lồ đòi hỏi một chiếc laptop có hiệu năng ổn định và gọn gàng để mang theo nhiều nơi. Hiểu được nhu cầu đó, Dell mang đến cho bạn chiếc laptop Dell Inspiron 14 5440 Core 7 150U (7FN5J) - sự lựa chọn hoàn hảo đáp ứng mọi yêu cầu của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("5ca70282-ce8d-41f4-a46d-e7a29a0100c8"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Hiệu năng ổn định từ bộ đôi Intel Core 7 Raptor Lake - 150U và card tích hợp Intel Graphics mang đến hiệu suất ổn định, đáp ứng tức thì khi bạn thao tác trên các nền tảng học tập, công việc quen thuộc như Word, Excel, PowerPoint,... còn hỗ trợ bạn xử lý các tác vụ đồ họa cơ bản với Photoshop, AI, Figma,... một cách mượt mà, biến những ý tưởng sáng tạo thành hiện thực một cách dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("7536d69d-2790-4881-90ff-a982cc945c3a"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9df57daf-9d0e-46e6-bc1c-3de2825815f6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("2c49f7cc-a414-486f-b044-893b1c4ef770"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Laptop Dell Inspiron sở hữu bộ nhớ RAM 16 GB khả năng nâng cấp ấn tượng lên đến 24 GB mang đến cho bạn trải nghiệm đa nhiệm mượt mà. Thoải mái mở hàng chục tab, sử dụng nhiều ứng dụng cùng lúc mà không lo giật lag hay đứng máy, tối ưu hóa hiệu suất công việc cho bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("90a62774-675a-47f0-bf4e-3a03b4a740e2"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Ổ cứng SSD 1 TB M.2 PCIe mang đến dung lượng lưu trữ khổng lồ, đáp ứng mọi nhu cầu lưu trữ tài liệu, hình ảnh, video của bạn. Khởi động máy và truy cập dữ liệu trong chớp mắt, tiết kiệm thời gian quý báu cho công việc và học tập.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("dbbda91f-d453-4677-9cf2-826ad3113d26"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b22db85f-ef5c-4277-81e6-c8a5294e4dd9.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("3631cffb-7fa0-4fff-a3f7-8d3a2ff26b78"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Chiếc laptop Dell sở hữu kích thước màn hình 14 inch, tỉ lệ 16:10 tuy không quá lớn nhưng vẫn cho phép bạn chia đôi khung hình, tăng hiệu quả công việc. Màn hình có độ phân giải Full HD+ (1920 x 1200) cùng tấm nền WVA cho hình ảnh rõ nét, chi tiết, mang đến cho bạn những trải nghiệm thị giác tuyệt vời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("d7cd1af8-c8a2-4453-bd05-f916508a3d7d"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Với công nghệ chống chói Anti Glare hạn chế tối đa tình trạng phản chiếu ánh sáng, cho bạn hình ảnh hiển thị rõ ràng, sắc nét dù bạn đang ở đâu. Dù là trong nhà, ngoài trời, quán cà phê hay thư viện, bạn cũng có thể thoải mái sử dụng máy tính mà không lo bị chói mắt hay mỏi mắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("c9beb73a-d55e-4f19-9edb-017d0a4bb567"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Bộ đôi công nghệ Waves MaxxAudio Pro và Dolby Atmos trên chiếc laptop này sẽ mang đến cho bạn từng giai điệu du dương, sâu lắng,... biến những phút giây thư giãn với âm nhạc của bạn trở nên tuyệt vời hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("458faf6a-d5fe-42e0-be32-5eb4197edd35"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Nâng tầm linh hoạt trong công việc với Webcam Full HD, giải pháp hoàn hảo cho những ai thường xuyên tham gia làm việc trực tuyến. Laptop cho phép bạn dễ dàng kết nối và tương tác hiệu quả trong mọi môi trường, bất kể bạn đang ở đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("9c8045de-7bc8-4efd-a29a-44b657832e0a"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/0bdc1303-0942-45fd-b9a5-94a2d44fcc74.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("25f2472b-2d45-483b-9be8-818e848707a0"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Laptop học tập văn phòng sở hữu ngoại hình cuốn hút với vỏ nhựa cao cấp được phủ tông màu xanh dương tinh tế. Với khối lượng chỉ 1.56 kg, Dell Inspiron là người bạn đồng hành lý tưởng cho những ai thường xuyên di chuyển. Bạn có thể dễ dàng mang theo laptop bên mình mọi lúc mọi nơi mà không cảm thấy nặng nề hay cồng kềnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("71d9797f-c015-4f8f-80ae-a40a78c83e3b"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Chỉ với một chạm đơn giản, bạn đã có thể truy cập vào laptop Dell một cách nhanh chóng và an toàn mà không cần phải nhập mật khẩu. Với tính năng bảo mật vân tay hiện đại giúp bảo vệ dữ liệu của bạn một cách hiệu quả, cho bạn sự an tâm sử dụng máy tính ở bất cứ đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("4343c6ca-cae9-4515-bf58-a491e0c02995"), GadgetId = Guid.Parse("56669eb6-886d-4102-bea1-1872cf2a869f"), Value = "Laptop được tích hợp đầy đủ các cổng kết nối phổ biến như HDMI, USB 3.2, Headphone/microphone combo, USB Type-C 3.2 (hỗ trợ Power Delivery và DisplayPort) và khe đọc thẻ nhớ SD.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("b47f22b5-b733-4a64-8348-267f2a6544a8"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Laptop Dell Vostro 15 3520 i3 1215U (V5I3614W1) là một lựa chọn tuyệt vời cho những bạn đang cần tìm một chiếc laptop học tập - văn phòng sở hữu cấu hình ổn định, có khả năng vận hành trơn tru mọi tác vụ thường ngày.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ddec02f7-f31d-4a80-a175-ded4f01276c3"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Hiệu năng đầy đủ cho đa công việc ", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("69e46a32-05f8-4b31-b7c7-fe5c6e8eabc4"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Laptop Dell được tích hợp bộ vi xử lý Intel Core i3 1215U không quá mới mẻ nhưng vẫn khá đủ dùng cho mọi tác vụ mà mình cần từ học tập, làm việc cơ bản đến nâng cao trên những ứng dụng của nhà Microsoft Office, Google như Word, Excel, Google Drive,... máy còn có khả năng giải quyết nhanh gọn các nhiệm vụ liên quan đến đồ họa, mình tạo file thiết kế, chỉnh sửa hình ảnh cơ bản trên ứng dụng thuộc hệ sinh thái Adobe đều trơn tru và nhịp nhàng nhờ card tích hợp Intel UHD Graphics.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("0de9795d-0730-4507-a2d5-b3897311b4df"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/dedd40eb-dec6-49a5-94c3-c9868d0dfcba.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("e9169a80-3429-4c86-b95c-06841be2cfb2"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Bộ nhớ RAM 8 GB của máy cho khả năng đa nhiệm tốt, mình vừa mở Photoshop để chỉnh sửa hình ảnh trong lúc chờ chọn tướng trong Liên Minh Huyền Thoại, cũng như mở thêm cửa sổ Chrome, bật YouTube để nghe nhạc mà máy vẫn hoạt động ổn định. Trong quá trình sử dụng xuyên suốt đâu đó tầm 2 tuần, mình nhận thấy việc mở từ 8 đến 10 tabs Chrome cùng lúc đều không gặp các hiện tượng giật lag, gián đoạn. Với một sinh viên như mình thì chắc cũng không cần phải sử dụng đến tối đa khả năng nâng cấp 16 GB của máy đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("c776dd1b-8b16-42e8-9dbb-5ddf6f07b95e"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Máy cũng đủ không gian cho mình lưu trữ các tài liệu học tập hay những tựa game cỡ nặng với ổ cứng SSD 256 GB NVMe PCIe, đồng thời chuẩn SSD cũng hỗ trợ khởi động máy hay mở ứng dụng nhanh chỉ trong vài giây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("89c39c62-324e-46fd-8c69-b633f10e34a7"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Dell Vostro 15 3520 sở hữu viên pin 41 Wh đối với các tác vụ văn phòng cơ bản máy trụ được khoảng 3 giờ, một con số không cao nhưng hoàn toàn có thể chấp nhận được và trên thực tế mình cũng thường sử dụng máy làm việc trong văn phòng, luôn cắm sạc để tối ưu hiệu năng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("880b291d-9451-4847-98dc-c44118960688"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/61f831db-975a-4d06-867f-42c8784721ef.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("7619519c-e819-4f84-a998-4108bbe704ec"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Màn hình to rõ, hiển thị chân thực", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("9d37a9dd-6cf4-4b80-a717-8ea0a865a720"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Màn hình chiếc laptop Dell Vostro có kích thước 15.6 inch với khả năng hiển thị sắc nét nhờ độ phân giải Full HD (1920 x 1080), công nghệ LED Backlit giúp tiết kiệm điện năng đi cùng công nghệ Anti Glare hạn chế tình trạng bóng gương, bóng loá. Viền hai bên màn hình được Dell làm tương đối mỏng, tạo cho mình một không gian hiển thị lớn hơn về mặt thị giác. Có thể công dụng không quá nổi bật nhưng đều là những thông số màn hình cân bằng đủ để cung cấp được những phần nhìn tốt nhất cho người dùng trong tầm giá.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("84d75249-fdfd-4084-be94-03ce0c0d6ead"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Bên cạnh trải nghiệm về hình ảnh, chất lượng âm thanh laptop cũng hoàn toàn đáp ứng tốt được nhu cầu của mình, chất âm được tái tạo một cách chân thực, sống động nhờ công nghệ Realtek Audio. Khi làm việc trong phòng riêng mình chỉ cần mở âm lượng ở mức 50 - 60% là đã có thể cảm nhận âm thanh thật đã tai rồi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("89057f80-2793-418f-940d-66675d87d037"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Thiết kế đơn giản, kết cấu bền bỉ", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("dc6aba37-b7e3-4f70-8b61-7ba1149d98e7"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Mình đánh giá khá cao từ mẫu thiết kế đơn giản mà nhà Dell đặt trên mẫu laptop này, vỏ ngoài bằng nhựa mang tông màu xám trung tính, được tô điểm duy nhất bởi logo hãng trên phần mặt A của máy. Kết cấu của laptop khá bền bỉ, mình hoàn toàn không thấy độ flex quá nhiều như nhấn xuống phần mặt lưng, bàn phím hay mặt sau máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("2a05433e-1539-4cb9-b519-77e7dbbbf2d2"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Thêm nữa, khối lượng 1.83 kg cùng độ dày 18.99 mm theo mình đánh giá, cũng không quá nặng để có thể bỏ gọn gàng trong balo và mang theo cả ngày. Với những nhu cầu học tập, làm việc hiện nay thì mình nghĩ chỉ cần một chiếc laptop vừa học tập, làm việc là thậm chí đã thay thế gần như hoàn toàn những cuốn sách cồng kềnh rồi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("c52d25b0-e1f1-4617-a6bb-b7274292ff8e"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/5098a30e-c0dd-434d-9f72-7b2195ee1c54.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("c8a5375a-6b29-43f7-a371-10e42726d9c0"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Bàn phím laptop to, được bố trí thêm một cụm phím số ở phía bên tay phải hỗ trợ rất nhiều mỗi lần mình làm việc trên bảng tính hay tiếp xúc với các con số. Hành trình phím tương đối sâu cùng cách sắp xếp hợp lý nên việc thao tác trở nên dễ dàng hơn rất nhiều, dễ dàng nhận diện được mặt chữ và ít khi bị vướng víu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("f3b2a953-0ccc-4d09-8572-402d8e590745"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Bề mặt TouchPad khá mịn cho cảm giác di vuốt hiệu quả cao, mình hoàn toàn có thể làm việc hàng tiếng đồng hồ chỉ với bản di chuột mà thao tác tay vẫn cực kì thoải mái. Tuy nhiên, nếu bạn là người có size tay hơi lớn một chút hoặc chưa quen thuộc với kiểu mẫu làm việc như vậy, việc sở hữu thêm một con chuột rời bên ngoài sẽ là một lựa chọn không tồi đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("f888a7ad-c164-442b-aef8-0a701982830b"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Với thiết kế chuyên phục vụ cho công việc nên em máy này có đầy đủ các cổng kết nối thông dụng từ: HDMI, USB 2.0, Jack tai nghe 3.5 mm, USB 3.2,... cho phép mình truyền xuất dữ liệu ra nhiều thiết bị ngoại vi như máy in, màn hình rời, chuột,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("df4e48a3-8417-4372-9bd0-d431f7624952"), GadgetId = Guid.Parse("f1ed9701-dc97-4fd1-b6a1-6a5a74b50947"), Value = "Tổng kết lại, trong phân khúc giá chỉ nhỉnh hơn 11 triệu một chút thì laptop Dell Vostro 15 3520 i3 1215U (V5I3614W1) hoàn toàn đáp ứng được đầy đủ các tiện ích học tập, làm việc hay giải trí mà một mẫu người dùng cơ bản cần, đây chắc chắn là sự lựa chọn \"đáng đồng tiền bát gạo\" cho mọi người dùng trong tầm giá.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("77bd8b66-f759-4bcd-8fae-356b53dc6b75"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Laptop Dell Vostro 15 3520 i5 1235U (5M2TT2) mới nhất đến từ nhà Dell sẽ khiến bạn thực sự ấn tượng với kiểu dáng hiện đại, sở hữu những thông số kỹ thuật mạnh mẽ có thể chạy mượt những tác vụ học tập, văn phòng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("58385b9b-6d9c-4acd-8014-8d296fc8e312"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Hiệu năng xử lý mạnh mẽ và ổn định", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("b411de01-4b9b-4b17-9919-7658073502fe"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Laptop Dell Vostro được trang bị bộ vi xử lý Intel Core i5 1235U với cấu trúc 10 nhân và 12 luồng, có tốc độ xung nhịp cơ bản 1.30 GHz và tối đa lên đến 4.4 GHz nhờ công nghệ Turbo Boost. Thiết bị đạt 5240 điểm đa nhân và 1127 điểm đơn nhân khi mình đo được bằng công cụ Cinebench R23, từ hai con số trên cũng đủ cho thấy CPU có khả năng xử lý mượt mà và hiệu quả các tác vụ thường ngày như lướt web, soạn thảo văn bản, xem phim, giải trí hay chỉnh sửa video, hình ảnh và chơi game ở mức cấu hình trung bình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("ad3472ec-600a-4e43-8f19-8c99f62cea8f"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/4f0cbe50-7aae-4f1d-b690-c614883c94c1.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("858d6e19-a9ef-46dd-bd24-81dfbaf94425"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Bộ nhớ RAM 8 GB đối với một người dùng là sinh viên như mình vẫn đủ để sử dụng cho các tác vụ học tập, làm việc văn phòng. Tuy nhiên, khi mình thực hiện các tác vụ chỉnh sửa video hay thiết kế và mở đồng thời hàng chục tab Chrome, máy sẽ có hiện tượng giật lag nhẹ, do đó nếu bạn có nhu cầu làm việc nặng có thể cân nhắc lắp thêm một thanh RAM 8 GB nữa để tối ưu hiệu suất sử dụng và hạn chế tình trạng giật lag gây gián đoạn trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("96c4ba53-85ae-4ea9-ab0a-be70729036cc"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Hơn nữa khi nâng cấp RAM, Dell Vostro 15 cũng sẽ kích hoạt được cơ chế RAM kênh đôi cho khả năng xử lý công việc tốt hơn, đồng thời máy cũng nâng lên card đồ họa Iris Xe Graphics hỗ trợ xử lý đồ họa tốt hơn bởi theo mặc định card đồ họa sẽ là Intel UHD Graphics, chỉ có thể đáp ứng nhu cầu sử dụng đồ họa ở mức cơ bản, chẳng hạn như xem video, xem phim hoặc chỉnh sửa hình ảnh đơn giản trên các ứng dụng như Photoshop, Canva,…", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("917ea5ed-9a9c-4916-9a89-13943b737c0b"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Nhờ có ổ cứng SSD 512 GB NVMe PCIe mà Dell Vostro 15 có tốc độ đọc, ghi cao, từ đó tối ưu và rút ngắn đáng kể thời gian phản hồi khi mình thực hiện các thao tác trên ứng dụng, mình khởi động máy hay các phần mềm tương đối nặng như Photoshop cũng chỉ mất vài giây. Dung lượng 512 GB đối với một học sinh như mình thì tương đối rộng rãi, thoải mái cho mình lưu trữ tài liệu học tập, phim ảnh cũng như một vài tựa game để mình giải trí sau những giờ học tập mệt mỏi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("a146b79f-9b36-49bf-912a-8e31b0170cb3"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Thêm một điểm mà mình thích trên Dell Vostro 15 chính là thiết kế bản lề khi mở nắp máy sẽ nâng lên một góc nhất định giúp cho hệ thống tản nhiệt làm việc tốt hơn. Thiết bị được bố trí với các khe hút gió phía trên bàn phím và khe tản nhiệt phía dưới đáy máy, khi làm việc với các tác vụ văn phòng thì máy có nhiệt độ không quá nóng, giao động từ 72 đến 81 độ. Còn khi mình mở nhiều tác vụ nặng thì tất nhiên đối với một chiếc laptop học tập - văn phòng máy tỏa nhiều nhiệt hơn, nên nếu đặt máy lên đùi để làm việc sẽ gây khó chịu khi làm việc trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("8ed26808-b1ef-4759-a1b3-3283d7ee348b"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/426ca6eb-f4b3-414f-992e-c491d219ddfb.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("38867dde-d9ef-4eb3-86bd-1a559f24297b"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Thiết kế cuốn hút với những đường nét thanh lịch", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("12c66232-6400-4081-807b-92b1563b85cc"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Dell Vostro 15 có thiết kế tối giản, thanh lịch, vỏ ngoài máy được chế tác bằng nhựa màu xám tạo nên sự chắc chắn, bền bỉ khi sử dụng và cho mình cảm giác cầm nắm khá tốt. Với tổng thể hài hòa, tinh giản trên từng chi tiết, sản phẩm sẽ hợp gu với các bạn yêu thích sự đơn giản. Tuy nhiên cũng chính vì sự tối giản nên máy khi sử dụng lâu dễ bám bẩn, dưới ánh sáng mạnh sẽ để lộ khuyết điểm nên bạn nên thường xuyên lau chùi để đảm bảo được tính thẩm mỹ của máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("de26daf6-23de-49e8-83a4-5bbd8c8da922"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Laptop có khối lượng 1.67 kg cùng độ dày 19 mm, không quá lớn và nằm vừa vặn trong một ngăn kéo của chiếc balo, hỗ trợ mình mang theo máy để phục vụ cho việc học tập trên trường và làm việc nhóm ở quán cà phê đều không thành vấn đề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("a6ea7a60-a20e-43cd-b759-6e6ff659b131"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Bàn phím của Dell Vostro 15 có thiết kế full size, có cả cụm phím số mang đến sự thuận tiện khi sử dụng, đặc biệt phù hợp với những bạn nào học hoặc làm trong ngành kế toán, tài chính hay dân văn phòng cần nhập liệu nhiều. Cá nhân mình rất thích bố cục của bàn phím khi khoảng cách giữa các phím được sắp xếp hợp lý, hành trình sâu cùng độ nảy tốt, mang đến cảm giác thoải mái và hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("7e7b0299-ff3b-4611-823d-d8cd399a0098"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "TouchPad của máy có kích thước vừa phải cùng độ nhạy cao và chính xác, hỗ trợ tính năng thao tác đa điểm để thực hiện các thao tác như phóng to, thu nhỏ, kéo thả,... mang lại cảm giác khi làm việc khá tốt. Nhưng đối với các bạn nam có khổ tay hơi to nên cân nhắc sắm thêm chuột bên ngoài để có thể làm việc và học tập thuận tiện hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("4946c2f7-1054-4419-bd62-a07e0fbef609"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6583da9e-ba29-4120-a232-98cbddb29636.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("590b9830-9dcc-4968-ba84-e3d094007e97"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Màn hình giải trí sống động, cùng âm thanh chân thực", Type = ParseGadgetDescriptionType("BoldText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("adba0865-50bb-4c40-ba4b-3de56b549703"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Laptop Dell có kích thước màn hình lớn 15.6 inch cùng thiết kế viền mỏng, độ phân giải Full HD (1920 x 1080) và tấm nền IPS mang đến góc nhìn được tối ưu, rộng lớn và đã mắt với chất lượng hình ảnh hiển thị rõ nét, trung thực. Các thông số màu sắc khi mình đo đạc được bằng công cụ Spyder 5 Pro được 64% sRGB và 48% AdobeRGB, chỉ ở mức trung bình khá nhưng vẫn phù hợp để thiết kế 2D cơ bản hay chỉnh sửa hình ảnh, video.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("ec14458f-d48b-4046-b8d7-dbad117aa041"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Một điều bất ngờ khi mình trải nghiệm Dell Vostro 15 chính là sự mượt mà trong mọi thao tác mà màn hình 120 Hz mang lại, không chỉ khiến cho mọi thao tác mình thực hiện như chuyển qua lại giữa các tab khác nhau, lướt tìm thông tin trên trang web đều được thể hiện một cách trơn tru mà còn nâng cao trải nghiệm chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("4bd32d5b-b7bf-4ca9-bd1a-f9c22bf083a7"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Màn hình có độ sáng không quá cao - 250 nits nên khi mình sử dụng máy ở những nơi có ánh sáng chiếu mạnh như ngoài trời hay cạnh cửa sổ thường bị hạn chế tầm nhìn, các bạn nên cân nhắc dùng ở những nơi có cường độ ánh sáng vừa phải để đảm bảo và duy trì được hiệu suất hiển thị ổn định.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("0907c92b-3284-40f9-9db1-2c3c3a422f12"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Với hệ thống loa hai kênh cùng công nghệ âm thanh Cirrus Logic High Definition Audio, mọi âm sắc của một bản nhạc hay một bộ phim đều được tái tạo to rõ, độ khuếch đại âm lớn. Thông thường mình chỉ cần cài đặt khoảng 60% loa đã có thể nghe nhạc đã tai ở một phòng riêng rồi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("b9943bb2-ab88-4bfd-98df-0c9a1bbe0b27"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Dell Vostro có dung lượng pin 41 Wh, đáp ứng thời lượng sử dụng 5 - 6 tiếng đối với việc thực hiện các tác vụ thông thường của sinh viên hay nhân viên văn phòng. Tất nhiên, thời lượng pin có thể đáp ứng ít hay nhiều tùy thuộc vào độ sáng, tần suất sử dụng với các thiết bị bên ngoài hay các ứng dụng nặng như thiết kế, bạn vẫn nên cắm thêm sạc để có thể trải nghiệm tốt hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("17c88bd7-9cda-4092-818e-80485ed5f910"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Sản phẩm có đầy đủ các cổng giao tiếp phổ biến gồm: USB 3.2, HDMI, jack tai nghe 3.5 mm, LAN (RJ45), USB 2.0 và khe đọc thẻ SD, hỗ trợ kết nối thuận tiện với nhiều thiết bị ngoại vi. HDMI dễ dàng xuất hình ảnh, trình chiếu slide phục vụ cho thuyết trình, LAN cho phép mình dùng mạng có dây để có kết nối ổn định hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("b87b2fb9-7e2f-49b8-b476-3448b47da482"), GadgetId = Guid.Parse("b42f85f1-925d-4a31-9450-727738535276"), Value = "Tổng thể, Dell Vostro 15 3520 i5 (5M2TT2) là một sự lựa chọn tốt trong phân khúc laptop học tập - văn phòng nhờ các tính năng xử lý mạnh mẽ, ổn định, màn hình Full HD và bàn phím full size cho cảm giác gõ tốt, đáp ứng tốt từ các nhu cầu cơ bản cần thiết như làm việc văn phòng đến giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 22 },
new GadgetDescription { Id = Guid.Parse("eeef2c00-89e5-4680-a638-29f85bab0cf8"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "Laptop Dell Vostro 3530 i5 1335U (80GG93) được định hình thiết kế riêng biệt cho dân văn phòng và sinh viên trong phân khúc giá tầm trung. Sản phẩm này không chỉ mang lại hiệu suất tuyệt vời cho nhiều tác vụ mà còn sở hữu ngoại hình hiện đại, thời thượng, hứa hẹn sẽ chinh phục ngay cả những người dùng khó tính nhất.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("616ec4b9-497d-45ec-aa5b-3c1faa7c7a65"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "Laptop Dell Vostro được trang bị bộ vi xử lý Intel Core i5 1335U thế hệ thứ 13, tích hợp GPU Intel Iris Xe Graphics mang lại khả năng xử lý mượt mà các tác vụ văn phòng như Microsoft Office, đồng thời hỗ trợ tốt cho các ứng dụng thiết kế đồ họa như Adobe Photoshop và Illustrator. Bộ vi xử lý này không chỉ giúp bạn hoàn thành công việc một cách trơn tru mà còn tối ưu hiệu suất điện năng, đảm bảo máy luôn hoạt động lâu dài nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("57f580f2-48e5-4994-b063-2829b85fbe29"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/a802d6b7-0a96-4f60-9469-4e7b6d22468c.jpg", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("e3489012-9af6-49d3-b4d0-8b02e2eb9219"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "RAM 16 GB giúp bạn có thể vừa làm việc trên Google Doc, vừa nghe nhạc trên Spotify hay xử lý hình ảnh trên Adobe Photoshop mà máy vẫn phản hồi nhanh chóng. Với sự trang bị tốt nên trong quá trình sử dụng bạn cũng không cần nhất thiết phải nâng cấp thêm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("277ec48a-e82f-417b-909e-c5df4d45c100"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "Với ổ cứng SSD chuẩn NVMe PCIe, thời gian mở ứng dụng và truy xuất dữ liệu được rút ngắn đáng kể, mang lại hiệu suất làm việc nhanh chóng và hiệu quả. Dung lượng 512 GB đủ để bạn lưu trữ một lượng lớn tài liệu, hình ảnh và video phục vụ cho công việc và học tập hàng ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("dbbe6851-acaa-43c4-823a-2d71084a7a89"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/aab381bb-adc0-48bd-aeec-29645cda3598.jpg", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("e6ad11c7-e65e-4ee8-ad34-9e3f9511ae9f"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "Màn hình 15.6 inch với tấm nền WVA cho góc nhìn rộng và màu sắc chính xác từ mọi hướng. Độ phân giải Full HD (1920 x 1080) đảm bảo hình ảnh sắc nét, chi tiết, giúp bạn đắm chìm trong những bộ phim yêu thích trên Netflix hay các giờ phút làm việc căng thẳng trên các phần mềm như SketchUp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("b1699d98-9a72-4fdc-8704-ae681e848c65"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "Tốc độ làm tươi lên đến 120 Hz trên laptop học tập - văn phòng giúp tái hiện các chuyển động khung hình mượt mà, không bị bóng nhòe hay rách xé, kết hợp với công nghệ chống chói Anti Glare giúp hạn chế tình trạng phản xạ sáng khi sử dụng máy ở ngoài trời hay dưới ánh đèn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("a2fe3c06-1cea-465e-83e5-a625c6473d56"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/cfcf143b-0d20-4154-abbd-7d782de8894c.jpg", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("ac05a5d3-480b-4d00-bcca-1ca3b86bc81b"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "Hệ thống loa tích hợp công nghệ Realtek High Definition Audio mang lại trải nghiệm âm thanh sống động, giúp bạn tận hưởng trọn vẹn những bản nhạc yêu thích trên YouTube hay những bộ phim kịch tính trên HBO Max. Chất lượng âm thanh rõ ràng, chân thực kết hợp với HD webcam sẽ làm bạn hoàn toàn hài lòng trong việc video call và họp online.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("e8b92b96-b530-4be9-9234-1c61f47e0d1f"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "Laptop Dell được phủ lớp sơn màu xám, kết hợp với các đường nét cắt mảnh, tạo nên phong cách hiện đại, sang trọng. Với khối lượng chỉ 1.66 kg, chiếc laptop này dễ dàng mang theo bên mình, phù hợp cho những người thường xuyên di chuyển, dù là đi học hay đi làm. Thiết kế gọn nhẹ, dễ dàng để vào balo hoặc túi xách, giúp bạn luôn sẵn sàng làm việc ở bất cứ đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("d290230d-d57f-40f2-bc81-0cdead49ec4f"), GadgetId = Guid.Parse("a784d49c-62bd-457b-9f80-21eb049fa08c"), Value = "Thiết bị trang bị các cổng kết nối tiện lợi dọc hai bên thân máy như: USB Type-C, USB 2.0, USB 3.2, HDMI, Headphone/microphone combo và LAN giúp bạn dễ dàng kết nối với các thiết bị ngoại vi, phục vụ tốt cho mọi nhu cầu truyền tải dữ liệu qua các phần mềm như Zoom, Microsoft Teams hay Skype.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("5b31b313-1140-459e-9388-b582efabd800"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Với laptop Dell G15 5530 i7 13650HX (i7HX161W11GR4060) bạn không chỉ là một game thủ mà còn là một nhà sáng tạo thực thụ. CPU Intel Core i7 thế hệ 13 và card đồ họa RTX 4060 mang đến hiệu năng vượt trội, giúp bạn chinh phục mọi thử thách, từ những trận game căng thẳng đến các dự án sáng tạo đòi hỏi cao. Thiết kế hiện đại và màn hình chất lượng cao sẽ làm bạn hài lòng ngay từ cái nhìn đầu tiên.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("1a7fbf86-6a9d-4ace-b524-90410216f5da"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "CPU Intel Core i7 13650HX sở hữu 14 nhân và 20 luồng mạnh mẽ, chiếc laptop gaming này thực sự là một con quái vật về hiệu năng. Bạn có thể dễ dàng xử lý mọi tác vụ một cách mượt mà, từ chỉnh sửa video, đồ họa 3D phức tạp đến chơi các tựa game AAA đòi hỏi cấu hình cao, mọi thứ đều trở nên nhanh chóng và trơn tru hơn bao giờ hết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("06c54b0e-3ad3-4c67-a5d9-3a0c440464f6"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/c10bf8c7-95eb-418a-ab38-c11ac749be17.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("bf0adbaf-f655-4410-9401-058209e7ed11"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Với GPU NVIDIA GeForce RTX 4060 8 GB, chiếc laptop Dell này hứa hẹn sẽ mang đến trải nghiệm đồ họa đỉnh cao. Từ việc chơi game ở mức thiết lập đồ họa cao cho đến việc render video 4K chiếc laptop này đều đáp ứng một cách xuất sắc. Các công nghệ tiên tiến như Ray Tracing và DLSS sẽ giúp hình ảnh trở nên sống động và chân thực hơn bao giờ hết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("58b02ad0-754f-43c5-bc3b-e92d1ccb8718"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Nhờ việc trang bị RAM 16 GB DDR5 4800 MHz, chiếc laptop của bạn sẽ trở thành một cỗ máy đa nhiệm thực thụ. Với bộ nhớ siêu khủng này, laptop sẽ chạy mọi tác vụ một cách mượt mà, từ lướt web, lập trình đến chơi game. Bạn sẽ không còn phải lo lắng về tình trạng máy chậm, đơ hay treo khi mở nhiều ứng dụng cùng lúc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("c6d5f903-78ed-4cd8-82ee-cd85e1c77932"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Ổ cứng SSD M.2 PCIe Gen 4 trên laptop Dell G15 mang đến tốc độ xử lý dữ liệu cực nhanh, rút ngắn thời gian chờ đợi của người dùng. Dung lượng 1 TB cho bạn thỏa sức lưu trữ tài liệu, phim ảnh, ứng dụng,... mà không sợ ổ cứng bị đầy, ảnh hưởng đến hiệu suất hoạt động của máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("e0c6b997-99ae-4b6e-b0c9-dd1a1dbf5692"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/f7f1aaa9-611c-405f-bdf8-2c4741bb84ac.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("928c13f3-a93c-481e-ab95-890d2a26fc92"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Màn hình 15.6 inch rộng rãi trên chiếc laptop gaming này sẽ đưa bạn đắm chìm vào thế giới game sống động. Với độ phân giải Full HD (1920 x 1080) sắc nét và công nghệ WVA, từng chi tiết trong game đều được hiển thị rõ ràng, chân thực đến từng khung hình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("9c0c2b75-4708-45a1-92db-d4f58ae3a7fb"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Công nghệ ComfortView Plus trên màn hình Dell giúp giảm thiểu ánh sáng xanh có hại, bảo vệ mắt bạn khỏi tình trạng khô mắt, mỏi mắt, đặc biệt khi làm việc trong thời gian dài. Độ sáng 300 nits đảm bảo các nội dung trên màn hình được hiển thị rõ nét ngay cả khi bạn sử dụng laptop ở nơi ánh sáng mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("60dc4722-2cb5-4850-a086-5ac6a97b3a4e"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Với công nghệ Realtek Audio, từng giai điệu, từng âm thanh đều được tái hiện một cách chân thực, sống động giúp bạn tận hưởng những phút giây thư giãn tuyệt vời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("0bb334d5-c5a8-45d8-b015-f08f854a09e0"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/47c5524f-19be-479c-bf57-2459e9bd13f0.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("f27062ba-7445-41d2-80cf-20e845fdd32f"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Chiếc laptop Dell với lớp vỏ nhựa xám không chỉ sở hữu vẻ ngoài mạnh mẽ, cá tính mà còn vô cùng bền bỉ, chịu được va đập tốt, bảo vệ các linh kiện bên trong một cách tối ưu. Khối lượng chỉ 2.81 kg chiếc laptop này vô cùng di động, bạn có thể dễ dàng mang theo đến bất cứ đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("f4878c21-8d6f-4d54-b507-7586c80c078a"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Đèn nền RGB trên chiếc laptop gaming này không chỉ đơn thuần là một chi tiết trang trí mà còn là một công cụ đắc lực hỗ trợ game thủ. Trong những trận chiến căng thẳng, ánh sáng từ đèn nền sẽ giúp bạn làm chủ bàn phím một cách dễ dàng, nâng cao khả năng chiến thắng khi chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("cfbaaca6-4a06-4633-ad06-1192b1b8c276"), GadgetId = Guid.Parse("656fc9f0-1acb-4dd3-9d31-3d63701c89c9"), Value = "Laptop được trang bị khá đầy đủ các cổng kết nối phổ biến hiện nay như: LAN, Jack tai nghe 3.5 mm, USB 3.2, HDMI và USB Type-C (hỗ trợ DisplayPort) hỗ trợ liên kết với các thiết bị ngoại vi tương thích một cách nhanh chóng và chính xác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("06d9a5cf-b927-4e09-8157-6137b2af69ab"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Các game thủ không còn phải bó buộc mình ở một chỗ với một chiếc máy tính để bàn cồng kềnh nữa. Với laptop Dell G15 5530 i9 13900HX (i9HX161W11GR4060) bạn có thể chơi game ở bất kỳ đâu với hiệu năng đến từ còn chip Intel Core i9. Thiết kế gọn nhẹ, hiệu năng mạnh mẽ, chiếc laptop này là người bạn đồng hành hoàn hảo trong mọi tác vụ. ", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("dfe37671-007a-4e1e-b733-3440d549f787"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Với 24 nhân và 32 luồng, Intel Core i9 Raptor Lake - 13900HX vượt trội so với các thế hệ CPU trước đó. Khả năng xử lý của con chip này không thua kém gì những chiếc PC mang đến trải nghiệm chơi game mượt mà và hiệu quả làm việc vượt trội. Dù bạn là một game thủ, nhà sáng tạo nội dung hay một nhà phát triển phần mềm, chiếc laptop này đều đáp ứng mọi nhu cầu của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("170923ba-c13b-477f-aa6f-3c8d39b14840"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/30c95498-e4ba-4027-9bbd-a18e7fd637be.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("1f9d2ec7-9d58-4d6c-81b5-f2afd29817fd"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Không chỉ dành riêng cho game thủ, chiếc laptop trang bị GPU NVIDIA GeForce RTX 4060 8 GB còn là một công cụ đắc lực cho các nhà sáng tạo nội dung. Khả năng xử lý đồ họa mạnh mẽ giúp bạn render video 4K nhanh chóng, chỉnh sửa hình ảnh chuyên nghiệp và thiết kế đồ họa 3D một cách mượt mà. Với chiếc laptop này, bạn có thể thỏa sức sáng tạo và biến những ý tưởng của mình thành hiện thực.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("39938491-2225-4e30-96cc-9cbd282449b2"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "RAM 16 GB DDR5 4800 MHz là một lợi thế tuyệt vời cho những ai làm việc trong lĩnh vực lập trình, thiết kế đồ họa hoặc các công việc liên quan đến xử lý dữ liệu lớn cần đa nhiệm cao. Việc chạy nhiều máy ảo, biên dịch code, hay render đồ họa 3D sẽ trở nên dễ dàng hơn bao giờ hết. Bạn sẽ tiết kiệm được rất nhiều thời gian và tăng năng suất làm việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("bb241283-0dc7-4845-8b1a-35cb4c12b64c"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Với ổ cứng SSD M.2 PCIe Gen 4 dung lượng 1 TB, chiếc laptop Dell G15 của bạn sẽ khởi động chỉ trong vài giây, mở ứng dụng gần như tức thì và truyền tải dữ liệu với tốc độ chóng mặt. Bạn sẽ không còn phải chờ đợi mỗi khi khởi động máy hay mở các tập tin lớn giúp tăng đáng kể hiệu suất làm việc và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("5644d6eb-9926-423a-9628-3421ba0ad97d"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Màn hình rộng 15.6 inch và tấm nền WVA, bạn sẽ có được tầm nhìn bao quát hơn giúp bạn không bỏ lỡ bất kỳ chi tiết nào trong game. Cho dù bạn đang chơi game ở góc độ nào, hình ảnh vẫn luôn sắc nét và rõ ràng. Độ phân giải Full HD (1920 x 1080) đảm bảo các nội dung được thể hiện rõ nét, màu sắc chân thật.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("21db98ea-5248-4924-8182-375dce94e740"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/c7078ba9-af9d-49eb-8af5-9c25d7fdf760.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("d4fd080a-5b3d-422c-bc1a-e980b67cce7b"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Laptop Dell còn được trang bị công nghệ ComfortView Plus giúp giảm thiểu đáng kể ánh sáng xanh có hại, nguyên nhân gây mỏi mắt và khô mắt khi sử dụng máy tính trong thời gian dài. Kết hợp với độ sáng 300 nits, bạn sẽ có một trải nghiệm xem thoải mái, ngay cả khi làm việc trong môi trường ánh sáng mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("4c679e9e-f572-4652-993e-e620c09c40c0"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Với tần số quét 165 Hz và công nghệ G-Sync, chiếc laptop gaming này mang đến trải nghiệm chơi game mượt mà, không bị giật hình hay xé hình. Từng chuyển động của nhân vật và hiệu ứng hình ảnh đều được hiển thị một cách trơn tru giúp bạn có những pha xử lý chính xác và nhanh nhạy hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("7e4b2eab-6c89-4ad4-952b-56101713559b"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Màn hình có độ phủ màu 100% sRGB cho khả năng tái tạo màu sắc một cách chính xác và chân thực nhất giúp bạn có được trải nghiệm hình ảnh tuyệt vời. Cho dù bạn là một nhà thiết kế đồ họa chuyên nghiệp hay chỉ đơn giản là muốn thưởng thức những bộ phim yêu thích, màn hình này đều đáp ứng mọi nhu cầu của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("8c278799-d0de-4de6-9a12-b0ce56adb80f"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Công nghệ Realtek Audio được tích hợp trong laptop sẽ giúp cải thiện chất lượng âm thanh, giảm thiểu nhiễu, mang đến âm thanh trong trẻo, sống động.Từng giai điệu, từng tiếng bass,... đều được tái hiện chân thật mang đến những phút giây giải trí tuyệt vời cho bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("7de494b9-446b-4ffa-b2d3-7b2273ed8254"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6ee5468f-d4dc-4c08-aa15-70d3d9a8a4ff.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("72324b3f-5232-477a-b800-787d96df7495"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Với lớp vỏ nhựa xám bền bỉ, chiếc laptop Dell không chỉ có vẻ ngoài ấn tượng mà còn đảm bảo độ bền cao, chịu được va đập tốt. Khối lượng chỉ 2.81 kg giúp bạn dễ dàng di chuyển máy mà không cảm thấy nặng nề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("76dab3fe-e5a4-4c5e-87f4-3ff7003c43dc"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Bàn phím của laptop được trang bị đèn nền chuyển màu RGB khá bắt mắt, tạo bầu không khí sôi động khi chơi game. Ánh sáng rực rỡ từ các phím sẽ giúp bạn dễ dàng định vị và thao tác, nâng cao hiệu suất làm việc và chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("e1265f34-8d67-49c7-ade5-d3a3fd08ab11"), GadgetId = Guid.Parse("09ecd98b-ee57-4f6a-9fdf-a8e1c492f1ae"), Value = "Laptop được trang bị các cổng LAN (RJ45), USB 3.2, HDMI, jack tai nghe 3.5 mm và USB Type-C (hỗ trợ DisplayPort) dễ dàng kết nối với các thiết bị ngoại vi khác một cách dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("4f0ac8ba-acd5-448d-830e-a5147e87ad2f"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Thổi một làn gió mới hoàn toàn trong phong cách làm việc cũng như trải nghiệm trên những mẫu máy tính xách tay. Laptop Dell XPS 13 9340 Ultra 5 125H (XPSU5934W1) thuộc phân khúc cao cấp không chỉ sở hữu cấu hình vượt trội mà thiết kế cũng vô cùng độc đáo, lạ mắt thoả mãn từ phần nhìn đến trải nghiệm.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4c9d9c55-90c2-4922-b58f-fffcc05e83c6"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Hiệu suất hoàn hảo cho mọi vấn đề", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("253a7a65-bdfb-48c1-b9a6-527c88fc6c8d"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Dell XPS được trang bị bộ vi xử lý Intel Core Ultra 5 Meteor Lake - 125H thế hệ 14 mới nhất, với tốc độ ép xung tối đa lên đến 4.5 GHz. Điều này không chỉ đảm bảo xử lý trơn tru mọi tác vụ từ công việc văn phòng, xử lý dữ liệu đến các ứng dụng giải trí đa phương tiện nặng mà còn hỗ trợ tốt các phần mềm đòi hỏi cấu hình cao như Adobe Premiere Pro hay vận dụng các công cụ thiết kế phần mềm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("3894a12c-dea4-4b9d-8fe3-a567c1504a7f"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Card đồ họa tích hợp Intel Arc Graphics mang lại khả năng xử lý hình ảnh nâng cao, cho phép bạn thỏa sức sáng tạo với các phần mềm thiết kế như Photoshop, Illustrator,... hoặc trải nghiệm các tựa game với chất lượng hình ảnh ấn tượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("7c50a5e0-36d2-41ca-93ea-2994992497ed"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Bộ nhớ RAM 16 GB LPDDR5X sở hữu tốc độ Bus 7467 MHz cung cấp độ đa nhiệm mượt mà, chuyển đổi giữa các ứng dụng nhanh chóng mà ít gặp hiện tượng giật lag. Lượng tài nguyên RAM lớn tích hợp sẵn trên chiếc laptop mỏng nhẹ này giúp bạn có thể mở hàng chục tab trình duyệt cùng lúc, làm việc trên các file Excel hàng chục nghìn dòng mà không có bất cứ trục trặc nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("c677f458-6ef6-480a-a350-af3ffb82af71"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Ổ cứng SSD dung lượng khủng 2 TB cho không gian lưu trữ cực kì thoải mái, render đồ hoạ, lưu trữ các file video cho edit hay công trình thiết kế lớn mà không cần thiết bị hỗ trợ bên ngoài. Đồng thời, người dùng có thể khởi động máy và các ứng dụng chỉ trong vài giây, tăng cường hiệu quả công việc đáng kể.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("0bdc1d05-9400-43e1-9f59-3676af0e299a"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Hiển thị mãn nhãn, trải nghiệm sinh động", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("39df311a-9748-4dd7-bfb7-0049e8bf7a6b"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Laptop Dell sở hữu màn hình 13.4 inch với tỷ lệ khung hình hợp lý, mang đến không gian hiển thị cân đối và thu hút độ tập trung cao. Công nghệ WVA cùng độ phân giải", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("ad547d18-3e83-4d0a-8c8a-d6f367d356cb"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "QHD+ (2560 x 1600) mang lại chất lượng hiển thị xuất sắc với màu sắc rực rỡ, chân thực và độ chi tiết cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("f70fef4d-1548-497f-ba57-a9933e215d68"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Khả năng hiển thị 100% dải màu sRGB khiến màn hình của XPS 13 trở nên lý tưởng cho các công việc đòi hỏi độ chính xác màu cao như thiết kế đồ họa, chỉnh sửa video và hình ảnh chuyên nghiệp. Mọi chi tiết nhỏ nhất sẽ được hiển thị rực rỡ hơn, mướt mắt với tần số quét 120 Hz giúp bạn có những trải nghiệm thị giác tuyệt vời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("b3cd0c61-3918-41a8-81de-cbf71f075034"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Các tính năng bảo vệ mắt, tăng cường tầm nhìn như Eyesafe, Anti Glare giúp giảm thiểu tác hại của ánh sáng xanh, hạn chế tình trạng nhức mỏi mắt khi làm việc trong thời gian dài. Tính năng cảm ứng đa điểm hỗ trợ bạn linh động hơn trong công việc, bạn dễ dàng thao tác ngay trên màn hình chỉ cần một lần chạm .", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("feea2857-9c05-418f-889c-7f057c1dfef5"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Máy được trang bị công nghệ Realtek Audio không chỉ tăng cường âm lượng mà còn cải thiện chất lượng âm thanh, mang đến trải nghiệm nghe nhạc, xem phim sống động và chân thực. Âm thanh được khuếch đại với tần số lớn, độ phân giải cao, tạo ra những âm sắc trong trẻo và mạnh mẽ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("dbdd49ee-3f53-4443-84cb-e3a72dee7244"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Dù bạn đang thưởng thức một bản nhạc, xem một bộ phim yêu thích hay tham gia vào một cuộc họp trực tuyến, chất lượng âm thanh vượt trội của laptop Dell XPS sẽ làm hài lòng bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("d9baea29-8671-4497-b772-88a2a268e28a"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Độc đáo, tinh tế đi kèm tính sang trọng", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("e9dc3133-2506-4781-85c0-ec2549416a68"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Dell XPS 13 thể hiện trọn vẹn được tính chất cao cấp, sự hiện đại đến từ tương lai trong kiểu dáng thiết kế, bàn phím tràn viền tăng diện tích bề mặt tiếp xúc, hàng phím chức năng F được loại bỏ đi thay vào đó là các nút cảm ứng độc đáo, touchpad ẩn hoàn toàn vào chiếu nghỉ tay. Chưa kể đến, lớp vỏ hợp kim Nhôm vô cùng bền bỉ trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("f64ef62a-b828-4e3b-8f37-9d7f19fc24b9"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Với khối lượng chỉ 1.22 kg, máy cực kỳ nhẹ và dễ dàng mang theo mọi lúc mọi nơi. Bạn có thể đặt nó vào balo hoặc túi xách mà không cảm thấy nặng nề, lý tưởng cho những người thường xuyên di chuyển và làm việc từ xa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("7e2252a8-8cf9-48a3-9ecf-5db841af0fc4"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Bảo mật vân tay hỗ trợ truy cập máy chỉ qua một chạm đơn giản, loại bỏ đi các bước nhập mật khẩu cầu kì. Bàn phím có đèn nền cho những buổi làm việc vào đêm khuya hoặc ở nơi thiếu sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("89d8585d-a498-4c64-8253-730642c309d3"), GadgetId = Guid.Parse("9406715c-e64d-4400-bc88-280ae9a16a1e"), Value = "Hai bên laptop là 2 cổng Thunderbolt 4 vừa mang đến sự tinh gọn tối đa cho máy vừa hỗ trợ truyền tải dữ liệu một cách nhanh chóng. Nếu có nhu cầu đa dạng liên kết bạn có thể sắm thêm một chiếc Hub chuyển đổi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("66cd805e-2a97-498e-a164-6087ecf888e7"), GadgetId = Guid.Parse("06293d68-6b96-405c-a7df-79a0c6ec99ad"), Value = "Laptop Dell XPS 13 9340 Ultra 7 155H (HXRGT) là một tuyệt tác đột phá cho sáng tạo không giới hạn với dòng chip Ultra thế hệ mới mạnh mẽ, đa tính năng tân tiến. Nhờ sự kết hợp hoàn hảo giữa hiệu suất và thẩm mỹ, đây chắc chắn là lựa chọn lý tưởng cho những chuyên gia.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("3a66c803-c3be-432d-a4fa-84f8e852af65"), GadgetId = Guid.Parse("06293d68-6b96-405c-a7df-79a0c6ec99ad"), Value = "Vẻ ngoài đẹp mắt và độc đáo", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("f164d626-da71-40a7-85c5-90bcb30a4326"), GadgetId = Guid.Parse("06293d68-6b96-405c-a7df-79a0c6ec99ad"), Value = "Laptop Dell XPS nổi bật với lớp vỏ bạc sang trọng được chế tác từ hợp kim Nhôm Carbon bền bỉ tạo cảm giác cao cấp và sang trọng. Khung máy mỏng nhẹ có cân nặng 1.17 kg, thiết kế thời thượng làm tăng thêm sự chuyên nghiệp cho không gian làm việc của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("36924446-2ccf-433b-9ac6-63cf4ee24267"), GadgetId = Guid.Parse("06293d68-6b96-405c-a7df-79a0c6ec99ad"), Value = "Kiểu dáng bàn phím và touchpad của Dell XPS 13 9340 cũng được cải tiến. Bàn phím dạng tràn viền giúp tăng kích thước phím, gia tăng điểm tiếp xúc và không gây cảm giác vướng víu khi gõ. Touchpad \"tàng hình\" tích hợp liền với chiếu để tay, mang lại cảm giác sử dụng thú vị và tiện lợi. Đặc biệt, Dell đã lược bỏ đi hàng phím F truyền thống, thay vào đó là dãy phím cảm ứng, tạo nên một thiết kế đẹp mắt và hiện đại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
