new GadgetDescription { Id = Guid.Parse("1880a753-33af-411c-aaca-741e203a6807"), GadgetId = Guid.Parse("06293d68-6b96-405c-a7df-79a0c6ec99ad"), Value = "Laptop có 2 cổng Thunderbolt 4 với tốc độ truyền tải cao lên đến 40 GB/s, hỗ trợ xuất hình ảnh chất lượng cao lên màn hình rời 5K. Bạn có thể trang bị thêm HUB để liên kết với mọi thiết bị ngoại vi mong muốn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
