new GadgetDescription { Id = Guid.Parse("5eaa56ab-0699-4230-aece-3d623e0921e5"), GadgetId = Guid.Parse("06293d68-6b96-405c-a7df-79a0c6ec99ad"), Value = "Laptop Dell sở hữu màn hình cảm ứng kích thước 13.4 inch với độ phân giải cao QHD+ (2560 x 1600), cho chất lượng hình ảnh sống động, sắc nét và màu sắc chân thực. Viền màn hình siêu mỏng giúp tăng không gian trải nghiệm, mướt mắt và phù hợp cho các công việc liên quan đến đồ họa, chỉnh sửa video.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("84725c33-a6c6-4c2f-8d0e-92c9149d160e"), GadgetId = Guid.Parse("06293d68-6b96-405c-a7df-79a0c6ec99ad"), Value = "Công nghệ chống phản xạ ánh sáng Anti-Reflective giúp giảm thiểu mỏi mắt khi làm việc trong môi trường sáng. Công nghệ Eyesafe hạn chế ánh sáng xanh độc hại, bảo vệ mắt người dùng, đồng thời độ sáng lên tới 500 nits đáp ứng tốt nhu cầu sử dụng ở mọi điều kiện ánh sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
