new GadgetDescription { Id = Guid.Parse("90646888-76f2-47ad-9b29-d780a8c9db6e"), GadgetId = Guid.Parse("06293d68-6b96-405c-a7df-79a0c6ec99ad"), Value = "Bộ nhớ RAM 16 GB LPDDR5X với tốc độ Bus ấn tượng lên đến 7467 MHz giúp máy đa nhiệm mượt mà, chạy nhiều nền tảng, ứng dụng nặng mà không gặp hiện tượng chậm hay lag. Ổ cứng SSD 1 TB M.2 PCIe cung cấp không gian lưu trữ rộng rãi, truy xuất dữ liệu và mở các phần mềm chỉ trong tích tắc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
