new GadgetDescription { Id = Guid.Parse("9b39aa73-9068-4411-b734-15a15a2c0eb2"), GadgetId = Guid.Parse("6bc9ea3d-0ce1-4359-a614-1a95e34e04f4"), Value = "Với bộ vi xử lý Intel Core i7 13800H gồm 14 nhân và 20 luồng trên laptop Dell mang lại công suất xử lý đa nhiệm tuyệt vời cho nhiều công việc khác nhau, kết hợp với đó là card rời NVIDIA GeForce RTX A500 4 GB chuyên dụng để xử lý tốt các tác vụ thiết kế đồ hoạ 2D, 3D đặc thù, nghiên cứu khoa học, code,... Đồng thời các nhân hiệu năng trên máy còn hỗ trợ vận dụng khai phá công suất tốt cho mọi yêu cầu nhưng vẫn đảm bảo được thời lượng sử dụng lâu dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
