new GadgetDescription { Id = Guid.Parse("6da55550-ba78-4238-a2fa-d66d86c4c8e1"), GadgetId = Guid.Parse("b0e8de1b-4df6-4c3a-8309-6a151d31f543"), Value = "Khám phá công cụ hỗ trợ làm việc và giải trí đa phương tiện, laptop Dell Latitude 3540 i5 1235U (71038100) sở hữu con chip Intel dòng U tiết kiệm điện năng vượt trội, cấu hình RAM 16 GB phù hợp, dung lượng 512 GB,... sẽ giúp bạn xử lý thật hiệu quả mọi vấn đề trong đời sống.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("666c76b3-09ec-4dc4-bddc-891f5d94f938"), GadgetId = Guid.Parse("b0e8de1b-4df6-4c3a-8309-6a151d31f543"), Value = "Dell Latitude 3540 trang bị vi xử lý Intel Core i5 1235U, kết hợp hoàn hảo với card Intel Iris Xe Graphics giúp máy vận hành mượt mà từ Word, Excel, Outlook,... đến các phần mềm đòi hỏi hiệu năng cao như Adobe Photoshop, Illustrator và thậm chí là cả các công cụ phát triển phần mềm như Visual Studio Code.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("78bb3c87-5b12-4a3b-8f93-f1959874164a"), GadgetId = Guid.Parse("b0e8de1b-4df6-4c3a-8309-6a151d31f543"), Value = "RAM 16 GB DDR4 dễ dàng xử lý công việc trên đa nền tảng cùng lúc, vừa mở Excel xử lý task, vừa nghe nhạc hay truy cập cửa sổ nhỏ để xem phim mượt mà, laptop hạn chế được các tình trạng giật lag và gây gián đoạn trong quá trình thao tác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("dc788730-c916-4c8f-bae5-599774ed0790"), GadgetId = Guid.Parse("b0e8de1b-4df6-4c3a-8309-6a151d31f543"), Value = "Laptop học tập - văn phòng được gắn ổ cứng SSD 512 GB NVMe PCIe Gen 4.0, rút ngắn thời gian đọc ghi dữ liệu, dung lượng thoải mái cho lưu trữ và thực hiện thời gian tải xuống tốt hơn hẳn so với các dạng ổ cứng cơ truyền thống khác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
