new GadgetDescription { Id = Guid.Parse("936fc6d3-a3d4-4411-a291-2d1e4af0c365"), GadgetId = Guid.Parse("b0e8de1b-4df6-4c3a-8309-6a151d31f543"), Value = "Laptop Dell tích hợp sẵn Full HD Webcam, mang lại chất lượng hình ảnh rõ nét cho các cuộc gọi video, có thể tham gia học trực tuyến hay họp online ở bất cứ đâu. Tính năng công tắc khóa camera giúp bảo vệ sự riêng tư của bạn khi không dùng camera nữa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("65f330d8-7b47-4f0b-89ff-9108bc3420b4"), GadgetId = Guid.Parse("b0e8de1b-4df6-4c3a-8309-6a151d31f543"), Value = "Máy được định hình với kiểu dáng \"chuẩn văn phòng\" với bộ khung thông dụng, kiểu dáng tinh giản cùng các thông số kích thước không quá lớn để vừa vặn trong chiếc balo. Lớp vỏ nhựa cao cấp được gia công cứng cáp, phủ bên ngoài là lớp sơn xám được xử lý nhám đi để hạn chế bám dấu vân tay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("9cc9d8dc-f430-4eb4-ab4d-9ee06d288043"), GadgetId = Guid.Parse("b0e8de1b-4df6-4c3a-8309-6a151d31f543"), Value = "Cân nặng 1.81 kg của laptop Dell Latitude cũng không gây quá khó khăn cho kể cả các bạn nữ trong quá trình đi học hay lên văn phòng công ty. Đa dạng cổng kết nối bao gồm: LAN, Jack tai nghe 3.5 mm, USB 3.2, HDMI và USB Type-C liên kết được tới các thiết bị bên ngoài phục vụ cho học tập, công việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("fbcb4bf6-a491-40ed-b742-ce33de1ddc61"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "Laptop Dell Latitude 3440 i5 1235U (L3440-I51235U-16G512G) là một trong những chiếc laptop lý tưởng dành cho sinh viên và người đi làm cho mọi tác vụ doanh nghiệp, học tập. Được trang bị bộ vi xử lý Intel Core i5 thế hệ 12, sản phẩm cho hiệu năng ấn tượng cùng thiết kế tinh tế và gọn nhẹ, giúp bạn làm việc thoải mái mọi lúc mọi nơi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("67ffa760-5ec2-47f2-adb4-bd3e0486df38"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "Bộ vi xử lý Intel Core i5 1235U thế hệ 12 với 10 nhân 12 luồng và tốc độ xung nhịp tối đa lên đến 4.4 GHz giúp máy xử lý mọi tác vụ một cách mượt mà. Từ công việc văn phòng như soạn thảo văn bản trên Microsoft Word, làm bảng tính trên Excel, đến chỉnh sửa ảnh trên Adobe Photoshop hay chạy các phần mềm lập trình như PyCharm. Kết hợp với đó là card đồ họa Intel Iris Xe Graphics, mang lại khả năng xử lý hình ảnh mượt mà khi xem phim hoặc chỉnh sửa ảnh cơ bản.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("8d5549df-8394-45c0-a49b-c2bdb7335c5c"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e7107ca7-9ded-4c0b-91d2-fd5f507d3c49.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("3b192ca4-1fe5-4d44-8a08-9c5cb203a9b3"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "RAM 16 GB trên laptop học tập - văn phòng giúp bạn xử lý đa nhiệm linh động, có thể mở nhiều ứng dụng cùng lúc mà không lo máy bị chậm hay treo. Khả năng nâng cấp RAM lên đến 64 GB cũng đảm bảo bạn có thể dễ dàng mở rộng hiệu suất của máy trong tương lai, đáp ứng nhu cầu ngày càng tăng về dung lượng xử lý và bộ nhớ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("45376f0f-dc72-496f-86e3-8420d5a0e273"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "Laptop Dell được trang bị ổ cứng SSD 512 GB NVMe PCIe 4.0, mang lại tốc độ khởi động máy, truy xuất dữ liệu và mở ứng dụng nhanh chóng. Ngoài ra, khả năng nâng cấp dung lượng ổ cứng lên đến 1 TB cũng giúp bạn yên tâm lưu trữ mọi tài liệu, tệp tin và phần mềm quan trọng mà không lo thiếu dung lượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("c34f5066-f314-4203-abda-3c85867fd0ad"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ddff6402-b04a-4cd0-8f37-ead6665a8257.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("dfe35d89-e4a5-43ea-a4ff-43de6f77cc2c"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "Màn hình 14 inch với độ phân giải Full HD (1920 x 1080) mang đến hình ảnh sắc nét và màu sắc trung thực. Độ sáng 250 nits hỗ trợ tương thích với nhiều điều kiện sáng, trong khi công nghệ chống chói Anti Glare giúp giảm thiểu độ chói và phản xạ ánh sáng, giúp bạn làm việc hiệu quả ngay cả trong điều kiện ánh sáng mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("6ac5e77e-ffb8-4519-8425-49043ca116f5"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "Âm thanh sống động là một điểm mạnh khác của Dell Latitude 3440 nhờ công nghệ Realtek Audio. Cho dù bạn đang nghe nhạc trên Spotify, xem phim trên Netflix hay chơi game, âm thanh phát ra sẽ luôn rõ ràng và chân thực.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("49d9fe09-675b-4482-a000-305d5bbc4715"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/8a6c8e0d-514a-46c9-b412-8bf00d260424.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("20c81c02-780b-496e-8552-47fed5abde20"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "Thiết kế của laptop Latitude mang đến vẻ ngoài thanh lịch và hiện đại với màu đen trang nhã. Khối lượng chỉ 1.54 kg giúp bạn dễ dàng mang theo laptop này bất cứ đâu, từ văn phòng làm việc đến các buổi họp bên ngoài hoặc những chuyến công tác xa. Một số công cụ hữu ích như Full HD Webcam hỗ trợ làm việc remote linh hoạt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c13da24a-4413-4782-88b0-d64b19552256"), GadgetId = Guid.Parse("cf96cd93-6789-471f-8c5f-304622e871ed"), Value = "Chiếc laptop này cũng được trang bị đầy đủ các cổng kết nối cần thiết như LAN, USB 3.2, HDMI, jack tai nghe 3.5 mm, USB Type-C giúp bạn dễ dàng kết nối với các thiết bị ngoại vi khác nhau như máy in, màn hình ngoài, bàn phím và chuột,... mang lại trải nghiệm làm việc, giải trí tiện lợi và hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("9ddda710-275a-4be7-84eb-0c7183e1dd60"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Bạn đang tìm kiếm một chiếc laptop học tập và làm việc với cấu hình cân bằng, tài nguyên lớn cùng tính ứng dụng cao? Hãy khám phá ngay laptop Dell Latitude 3540 i5 1335U (71038101) với bộ xử lý Intel Core thế hệ 13 dòng U tiết kiệm điện, RAM và SSD lớn,... chắc chắn sẽ làm bạn hài lòng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("a6188027-7d36-4da8-8e50-d3075f64614b"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Laptop Dell được trang bị bộ vi xử lý Intel Core i5 1335U một trong những CPU hoàn hảo cho văn phòng của thế hệ thứ 13 từ Intel. Bộ vi xử lý này không chỉ cung cấp hiệu suất cao cho các công việc hàng ngày mà còn hỗ trợ khả năng xử lý đồ họa với card Intel Iris Xe Graphics. Sự kết hợp này mang lại khả năng xử lý mượt mà cho các tác vụ từ văn phòng, xem phim, duyệt web cho đến giải trí và chơi game nhẹ, đáp ứng mọi nhu cầu sử dụng của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("93e12600-3d07-41cc-afd8-1e94280f95ed"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Với RAM 16 GB, bạn có thể thực hiện đa nhiệm một cách trơn tru và hiệu quả. Dễ dàng mở nhiều ứng dụng cùng lúc, chạy các chương trình nặng mà không gặp trở ngại và chuyển đổi linh hoạt giữa các tác vụ mà không làm giảm hiệu suất. Hãng cũng hỗ trợ cho máy nâng RAM lên được 64 GB tuỳ theo nhu cầu cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("df7dd20c-ead5-4400-bf06-c88997c60d87"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Ổ cứng SSD 512 GB mang lại tốc độ đọc/ghi nhanh và khả năng khởi động hệ điều hành ngay lập tức. Hơn nữa, ổ cứng SSD không chỉ truy xuất nhanh mà còn cung cấp bảo mật dữ liệu, khả năng chống sốc và bảo vệ dữ liệu tốt hơn các dạng ổ HDD truyền thống.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("c91c6400-c00e-4e09-b3f3-a01e37166994"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Laptop Dell Latitude sở hữu màn hình WVA 15.6 inch độ phân giải Full HD, mang đến hình ảnh rõ nét và sống động. Từ hình ảnh, video đến văn bản, mọi chi tiết trên màn hình đều được hiển thị rõ ràng với màu sắc và độ tương phản ổn định.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("51407254-f168-4663-86ee-71a17a379905"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Lớp phủ chống chói Anti Glare loại bỏ các tình trạng bóng gương hay hắt sáng ngược từ màn hình, giảm căng thẳng cho mắt khi sử dụng ngoài trời hoặc dưới ánh đèn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("cf77969d-1eb3-43a4-a440-678d64261705"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Công nghệ Realtek Audio đảm bảo trải nghiệm âm thanh sống động và mạnh mẽ, nhờ sự kết hợp giữa phần cứng và phần mềm để tái tạo chất âm chân thực và rõ ràng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("eb4d3004-2661-42dc-a9ef-4081b8aec4e1"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Thiết kế đẹp mắt của laptop học tập - văn phòng với vỏ ngoài bằng nhựa cao cấp, mang lại cảm giác cầm nắm chắc tay và vẻ ngoài luôn được thẩm mỹ nhất khi hạn chế được việc bám dấu vân tay. Khối lượng trung bình 1.81 kg vẫn khá thoải mái để mang theo bên mình cả ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("d8fecaec-0992-48b0-9bbd-b0dea768683d"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Full HD Webcam được trang bị sẵn hỗ trợ linh động hơn khi làm việc remote, tham gia học online hay video từ xa mà hình ảnh của bạn luôn được hiển thị rõ ràng. Dễ dàng đóng mở thủ công với công tắc khoá camera khi không sử dụng để giữ an toàn hình ảnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("58e3d1d9-c51d-45a8-b11b-b5516a1de68d"), GadgetId = Guid.Parse("7601f8e2-0eff-43bd-87dd-2a59cd0e0e44"), Value = "Ngoài ra, thiết bị cũng được bố trí đầy đủ các cổng kết nối: LAN, Jack tai nghe 3.5 mm, USB 3.2, HDMI và USB Type-C 3.2 thuận tiện giao tiếp với màn hình ngoài, máy chiếu, loa,... cho mục đích thuyết trình hay mở rộng không gian làm việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("9d62fae5-88b5-45fd-bae8-a6461eabdd81"), GadgetId = Guid.Parse("5c3bf750-9dd7-4891-b40b-ece7cf57b2fa"), Value = "Laptop Dell Latitude 3440 i7 1355U (71038103) cân bằng tuyệt vời với thiết kế tinh tế chuẩn doanh nhân. Sở hữu con chip Intel Gen 13 mạnh mẽ và RAM 16 GB, mẫu laptop này không chỉ đáp ứng mọi nhu cầu công việc mà còn mang lại trải nghiệm sử dụng đỉnh cao cho người dùng hiện đại.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("18431c47-51c8-4163-90a5-7f4d9c905e3a"), GadgetId = Guid.Parse("5c3bf750-9dd7-4891-b40b-ece7cf57b2fa"), Value = "Với vi xử lý Intel Core i7 1355U, 10 nhân 12 luồng, xung nhịp tối đa 5.0 GHz, cùng card đồ họa tích hợp Intel Iris Xe Graphics, laptop Dell Latitude có thể dễ dàng xử lý từ những công việc văn phòng hàng ngày đến các nhiệm vụ đồ họa 2D và các tựa game phổ biến. Hiệu suất mạnh mẽ này đảm bảo mọi tác vụ đều diễn ra mượt mà và nhanh chóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("96f0cb89-3140-4e07-9bba-7e9016d0e744"), GadgetId = Guid.Parse("5c3bf750-9dd7-4891-b40b-ece7cf57b2fa"), Value = "Laptop Dell trang bị bộ nhớ RAM 16 GB có hỗ trợ nâng cấp tối đa lên 64 GB, giúp bạn dễ dàng chạy nhiều ứng dụng cùng lúc mà không lo giật lag, truy xuất các file ứng dụng nặng mà không bị tràn RAM gây gián đoạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("9fbce546-c9af-4d83-a0df-33e8d4b8cb47"), GadgetId = Guid.Parse("5c3bf750-9dd7-4891-b40b-ece7cf57b2fa"), Value = "Ổ cứng SSD 512 GB NVMe PCIe 4.0 không chỉ giúp khởi động và mở ứng dụng nhanh chóng mà còn cung cấp không gian lưu trữ rộng rãi, tốc độ đọc ghi nhanh chóng đáp ứng mọi nhu cầu tải và lưu trữ của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("08d1f7b6-259d-4764-bda2-d1b1969f3ae1"), GadgetId = Guid.Parse("5c3bf750-9dd7-4891-b40b-ece7cf57b2fa"), Value = "Màn hình 14 inch độ phân giải Full HD (1920 x 1080) mang lại hình ảnh sắc nét và chi tiết, lý tưởng cho công việc và giải trí. Tỷ lệ khung hình hợp lý với tấm nền WVA cho tầm quan sát được mở rộng hơn đáng kể về mọi hướng, đảm bảo độ tập trung cao khi làm việc và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
