new GadgetDescription { Id = Guid.Parse("92b2e724-7a8f-42c2-9e91-618a5dc657eb"), GadgetId = Guid.Parse("5c3bf750-9dd7-4891-b40b-ece7cf57b2fa"), Value = "Laptop tích hợp công nghệ Realtek Audio mang đến trải nghiệm âm thanh sinh động như đang ở trong một rạp chiếu phim thu nhỏ. Âm thanh sống động và rõ ràng giúp bạn đắm chìm vào không gian giải trí tuyệt vời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("015686eb-2977-4d28-b331-2de444ab7916"), GadgetId = Guid.Parse("5c3bf750-9dd7-4891-b40b-ece7cf57b2fa"), Value = "Thân máy mang tông màu xám trầm sang trọng, lớp vỏ nhựa cao cấp được chế tác tỉ mỉ mang lại sự chắc chắn và đáng tin cậy. Khối lượng laptop học tập - văn phòng 1.6 kg, dễ dàng mang theo trong balo đến bất cứ đâu, từ văn phòng, trường học đến quán cà phê.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("3538a01e-2a87-43c0-bc82-30c2d5fa34c2"), GadgetId = Guid.Parse("5c3bf750-9dd7-4891-b40b-ece7cf57b2fa"), Value = "Đầy đủ các cổng kết nối hiện đại như: LAN (RJ45), Jack tai nghe 3.5 mm, USB 3.2, HDMI và USB Type-C 3.2 giúp bạn dễ dàng liên kết với các thiết bị ngoại vi như chuột, bàn phím và màn hình ngoài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("385ff170-0f9c-4b7b-95c8-201c0f2991a2"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Laptop Dell Inspiron 14 7430 2-in-1 i7 1355U (i7U165W11SLU) sẽ mang đến cho người dùng trải nghiệm sử dụng thú vị với hiệu năng mạnh mẽ từ con chip Intel Gen 13, tính năng cảm ứng đa điểm, mở gập 360 độ cùng thiết kế bắt mắt. Chiếc laptop cao cấp này chắc chắn sẽ thỏa mãn mọi nhu cầu của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("3292065c-860a-4e0d-85f2-2bd91b158039"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Thiết kế thời thượng, tính năng độc đáo", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("8af65566-9e23-4902-b20d-4b5b19baf133"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Máy được phủ bởi lớp sơn bạc trung tính, bộ khung nhỏ gọn cùng các đường viền mỏng được bo tròn, mặt lưng cũng không quá nhiều chi tiết với logo DELL quen thuộc được mạ chrome và đặt ngay ngắn ở chính giữa. Mình khá thích kiểu dáng thiết kế này của Dell Inspiron 14 bởi thiết kế đơn giản nhưng vẫn đan xen đâu đó được những nét hiện đại, thanh lịch nhất định, luôn mang đến cảm giác chuyên nghiệp và tinh tế dù cho có sử dụng trong bất cứ không gian nào. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("e4fd0975-c5fe-46b6-acdf-58de9a554a10"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Phần nắp lưng và chiếu nghỉ tay của laptop được chế tác bằng kim loại, gia cố tốt cho tính bền bỉ của thiết bị khi có xảy ra va chạm. Với các chi tiết bằng kim loại nhưng khối lượng tổng thể của máy chỉ có 1.62 kg, vừa vặn nằm gọn trong balo và theo mình cả ngày dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("9dd1d3f9-0c9e-495c-9bf5-417ac2b8d5bc"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/acf14267-9b18-4230-ba08-cde316a0ef8f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("638dc907-4281-4714-820e-8100d51c5030"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Điểm mình thấy nổi bật nhất trên chiếc laptop này chắc chắn là phần bản lề mở gập được 360 độ, những khớp nối được hoàn thiện tốt nên khi đóng mở đều thuận lợi, không hề có cảm giác bị khựng lại giữa chừng. Mình cũng thường hay gập hẳn thiết bị lại để sử dụng như một chiếc tablet, vừa lạ lẫm, vừa thao tác thuận tiện mà biết đâu lại còn tìm ra được cảm hứng mới trong công việc nữa. Nhà Dell cũng trang bị kèm theo chiếc laptop này một bút cảm ứng để thao tác dễ dàng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("f86e55b6-7b34-44fb-bb0f-7ebdcc14f8cf"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Bàn phím máy được thiết kế gần sát 2 bên cạnh viền, các phím được bố trí ở khoảng cách phù hợp nên không bị cấn khi mình gõ, hành trình phím cũng sâu và có độ nảy tốt khiến cảm giác khi thực hiện soạn thảo văn bản rất thích tay. Đèn bàn phím cũng giúp mình làm việc tốt hơn trong môi trường thiếu sáng nữa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("a1d07e37-e53d-461a-9fc6-7964dcb53b24"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Bàn di chuột có kích thước thoải mái, độ nhạy tốt nên khi kéo thả, di vuốt đều mượt mà, không hề gặp trường hợp bị khựng lại giữa chừng. Với mình thì phần TouchPad này không có điểm nào để chê cả, nhưng nếu bạn đã quen sử dụng chuột hay có khổ tay hơi lớn một xíu cũng có thể sắm thêm cho mình chuột bên ngoài để xử lý công việc trơn tru hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("62f1e277-5fe8-4dbb-a279-7104d654a2b4"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b1a458c6-625c-42f5-b44d-9bc5cc3d8484.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("21079e05-6e12-4904-8387-4e3f69699693"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Dọc hai bên laptop là các cổng quen thuộc như: HDMI, Jack tai nghe 3.5 mm, USB 3.2, Thunderbolt 4 giúp mình kết xuất hình ảnh hay truyền tải dữ liệu đến các thiết bị ngoại vi khác như loa, chuột, bàn phím rời,... một cách nhanh chóng và dễ dàng. Bạn cũng có thể sử dụng thêm một Hub để chuyển đổi nếu có nhu cầu kết nối cao hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c109dcf9-0949-4313-bad3-ece147aff969"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Máy cũng có tính năng bảo mật vân tay nên về tính bảo mật thông tin, hình ảnh của thiết bị mình cũng rất yên tâm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c657fcf0-c60a-4687-b695-d77374357467"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Mượt mà mọi tác vụ, xử lý công việc trơn tru", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("e958377b-7264-45bd-9aa9-79b4cc14e034"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Laptop Dell Inspiron mang trong mình sức mạnh của bộ vi xử lý Intel Core i7 1355U, hỗ trợ mình vận hành hoàn hảo mọi công việc văn phòng như soạn thảo văn bản, thực hiện các trang tính phức tạp trên Word, Excel,..., kết hợp với card Intel Iris Xe Graphics với khả năng xử lý chuyên nghiệp các tác vụ chỉnh sửa hình ảnh, thiết kế poster, banner,... trên các ứng dụng đồ họa của nhà Adobe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("7d449b7d-bf0b-4f3f-97aa-18368a3a5791"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Các thông số hiệu năng khi mà mình đo được bằng công cụ Cinebench R23 là 1724 điểm đơn nhân và 8786 điểm đa nhân, thì với những con số trên thì máy cũng đã đảm bảo được hiệu suất sử dụng tốt ở mức đáp ứng tác vụ văn phòng và đồ họa cần thiết rồi. Mình cũng có chơi thử một vài trận game Liên Minh Huyền Thoại thì ở setting đồ họa Medium máy đáp ứng mức FPS tương đối ổn định, tung chiêu tấn công hay combat đều mượt mà.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("a4c83976-9c98-4103-96a8-7a8c3cf10612"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Mình thường có thói quen mở rất nhiều tab trình duyệt khi làm việc nhưng máy vẫn chạy rất mượt mà, không hề có hiện tượng bị đơ lại, độ đa nhiệm luôn được đảm bảo hiệu quả nhờ bộ nhớ RAM 16 GB. Bản thân cũng có hơi tiếc một chút khi chiếc máy này không có hỗ trợ nâng cấp RAM nhưng với dung lượng hiện tại đã quá đầy đủ cho mình để thực hiện công việc hằng ngày, chơi game và làm đồ họa cần thiết rồi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("efffee77-37a2-49df-946a-c509ac844214"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Không gian ổ cứng SSD 512 GB chuẩn NVMe PCIe đủ lớn cho mình lưu trữ các tài liệu về công việc cũng như giải trí, tốc độ truy xuất dữ liệu cao nên mọi thao tác trên ứng dụng hay thời gian khởi động máy đều đáp ứng nhanh chóng chỉ trong vài giây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("cd8d928d-476b-479d-8e08-fffd05141356"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Màn hình sắc nét, hiển thị chân thực", Type = ParseGadgetDescriptionType("BoldText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("132f53b9-9744-407a-ad2c-9cb03c5a91d3"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Màn hình của laptop Dell có kích thước 14 inch sử dụng tấm nền WVA với thiết kế các cạnh viền mỏng xung quanh tạo nên cảm giác hút thị giác cũng khi có một không gian giải trí tương đối thoải mái. Ngoài ra, độ phân giải Full HD+ (1920 x 1200) còn mang đến cho mình trải nghiệm nội dung và hình ảnh khá rõ nét và đã mắt mỗi khi xem phim hay làm việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("32a4c999-13e9-4ee7-8a0d-69df64592e6f"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/abfca4e5-5b7b-44ff-8a7a-6e0768bed760.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("909763cf-7d01-41c7-9875-89cb9925061c"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Đồng thời màn hình cũng hỗ trợ cảm ứng đa điểm giúp mình thao tác sử dụng dễ dàng hơn không cần dùng đến chuột. Mình chỉ hơi tiếc một chút khi tần số quét của màn hình chỉ dừng lại ở con số 60 Hz, nhưng cũng không vì thế mà mức hiển thị bị ảnh hưởng, các chuyển động phim ảnh và đồ họa đều được tái hiện mượt mà, chính xác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("f9587bfd-6d3f-41ac-b279-928152d7ff70"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Công nghệ Dolby Atmos và Waves MaxxAudio Pro cũng nâng trải nghiệm giải trí của mình với chất lượng âm thanh ấn tượng, cảm nhận được độ chi tiết, sống động và tính đa chiều từ mọi hướng. Mang đến cho mình những giờ phút giải trí với âm nhạc và xem ảnh chân thực hơn sau những giờ làm việc căng thẳng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("71894e9a-c81b-4ca6-a5c1-2520e018b35d"), GadgetId = Guid.Parse("fc2aa795-232d-449c-a9f6-b0264fd9b7dc"), Value = "Tóm lại, laptop Dell Inspiron 14 7430 2-in-1 i7 1355U (i7U165W11SLU) gây ấn tượng bởi lối thiết kế tinh tế, đẹp mắt và nhiều tính năng sử dụng khác nhau, bên cạnh đó là hiệu năng mạnh mẽ đến từ bộ vi xử lý Intel Gen 13, một thiết bị chắc chắn sẽ làm hài lòng mọi đối tượng người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("2c47fd60-e4c6-4e48-9ccb-594a6508ed3a"), GadgetId = Guid.Parse("84a1876e-520d-47cb-bc90-89a064d9db90"), Value = "Được gắn với vi xử lý Intel Core 7 thế hệ mới mạnh mẽ cùng khả năng xử lý vượt trội với card rời, laptop Dell Inspiron 16 5640 Core 7 150U (N6I7512W1) hứa hẹn sẽ là công cụ làm việc, giải trí đáng tin cậy cho mọi đối tượng người dùng. Laptop đồ hoạ này sẽ hoàn thiện mọi tác vụ được yêu cầu từ cơ bản đến nâng cao nhất.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("1fe4cf33-dced-4feb-a874-bdb3bc5fa3b0"), GadgetId = Guid.Parse("84a1876e-520d-47cb-bc90-89a064d9db90"), Value = "Dell Inspiron 16 5640 sở hữu bộ vi xử lý Intel Core 7 150U với 10 nhân và 12 luồng, có xung nhịp tối đa lên đến 5.4 GHz mang lại sự tối ưu cho các công việc văn phòng từ làm việc tính toán, trình chiếu cho đến các tác vụ đồ hoạ và lập trình phức tạp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("dde61732-c728-4861-8a39-630d8d240e6d"), GadgetId = Guid.Parse("84a1876e-520d-47cb-bc90-89a064d9db90"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e9f59666-b63b-4d3b-8d54-d6ca0cbd988f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("10a84054-2fe9-4413-a2f6-2a444cfdd7a9"), GadgetId = Guid.Parse("84a1876e-520d-47cb-bc90-89a064d9db90"), Value = "Đi kèm với card rời NVIDIA Geforce MX570A 2 GB cho hiệu quả cao hơn khi tương tác với các phần mềm cơ khí như AutoCAD hay các nền tảng thiết kế của Adobe. Ngoài ra, với mức cấu hình tinh chỉnh hợp lý từ trung bình đến cao hơn thì bạn hoàn toàn có thể chiến thoả thích với các tựa game esports đình đám hiện nay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("161b225b-f0f9-47b0-a275-49e5c7f47c73"), GadgetId = Guid.Parse("84a1876e-520d-47cb-bc90-89a064d9db90"), Value = "Đa nhiệm linh hoạt và chuyển đổi giữa hàng chục tab Chrome mà không bị giật lag, gián đoạn với RAM 16 GB DDR5 trên laptop Dell, lựa chọn nâng cấp thoải mái lên 32 GB cho các yêu cầu xử lý đồ hoạ cao hơn. Ổ cứng SSD 1 TB M.2 PCIe cung cấp đủ không gian cho nhiều các dự án cá nhân hay tài liệu lưu trữ của người dùng mà không cần phải nâng cấp gì thêm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("569cc111-660f-4b87-bced-cc4fce2d24de"), GadgetId = Guid.Parse("84a1876e-520d-47cb-bc90-89a064d9db90"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ac02bdb3-c7f8-40e4-bc66-c0fc8dfb7d3c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("ee3c1570-37cf-4107-b71b-3afdcb3d8d12"), GadgetId = Guid.Parse("84a1876e-520d-47cb-bc90-89a064d9db90"), Value = "Sống động, sắc nét qua từng khung hình với kích thước 16 inch và độ phân giải QHD+ (2560 x 1600). Hơn nữa, tỷ lệ khung hình hợp lý 16:10 còn hỗ trợ hiển thị được nhiều nội dung hơn trên một khung hình cố định, thuận tiện chia nhỏ ra nhiều cửa sổ làm việc và tăng độ tập trung khi giải quyết tác vụ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("bf7908ad-4715-4438-b5ee-9a5519d34a24"), GadgetId = Guid.Parse("84a1876e-520d-47cb-bc90-89a064d9db90"), Value = "Màn hình với tần số quét 120 Hz mang lại độ mượt mắt cho những trải nghiệm hình ảnh chuyển động tốc độ cao. Hơn nữa, chuẩn màu chuyên dụng đạt 100% sRGB còn là công cụ hỗ trợ tuyệt vời khi bạn render hình ảnh, video đòi hỏi độ chính xác cao về màu sắc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
