new GadgetDescription { Id = Guid.Parse("4602bfa4-05b9-4d29-af55-a0d270ad0ba3"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Công nghệ Vapor Chamber Cooler (tản nhiệt buồng hơi) trên laptop cũng mang đến hiệu quả tản nhiệt vượt trội so với các giải pháp truyền thống giúp giảm nhiệt độ CPU và GPU xuống mức an toàn, đảm bảo hiệu suất hoạt động ổn định và kéo dài tuổi thọ của linh kiện, ngoài ra còn giúp giảm tiếng ồn từ quạt do không cần hoạt động với tốc độ cao để tản nhiệt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("664e6935-bc2c-48c9-800c-fefcc0d451b0"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Bên cạnh đó, máy cũng hỗ trợ tính năng mở khóa khuôn mặt và bảo mật vân tay, giúp đăng nhập vào máy, mở khóa thiết bị nhanh chóng, hơn nữa còn tăng cường khả năng bảo mật thông tin, dữ liệu cá nhân cho người dùng. Các cổng giao tiếp như Jack tai nghe 3.5 mm, USB 3.2, HDMI, USB Type-C cũng được tích hợp trên thân máy, cho phép bạn kết nối thuận tiện với các thiết bị ngoại vi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
