new GadgetDescription { Id = Guid.Parse("dbe46c64-88dc-4c31-ac88-7086d096f40b"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Sức mạnh vượt trội, chinh phục mọi chiến trường ảo", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("42f4de29-b383-4e6c-bc70-5e984f4d734c"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Chiếc laptop gaming này được trang bị CPU Intel Core Ultra 7 155H có cấu trúc 16 nhân và 22 luồng, đạt tốc độ tối đa lên đến 4.8 GHz, đi cùng card đồ họa NVIDIA GeForce RTX 4060 8 GB tân tiến, mang đến hiệu suất vượt trội, giúp bạn chinh phục mọi tựa game yêu thích, từ những game phổ thông đến những bom tấn AAA với mức cài đặt cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("5fc6b1ee-f65d-42ec-bb25-9aceca23f5dd"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Không chỉ dừng lại ở khả năng chiến game đỉnh cao, MSI Gaming Stealth 14 còn là người bạn đồng hành lý tưởng cho các nhà sáng tạo nội dung. Với sức mạnh đồ họa ấn tượng, máy hỗ trợ đắc lực cho các phần mềm đồ họa chuyên nghiệp nhà Adobe như Photoshop, Illustrator, After Effects,... hay các ứng dụng kỹ thuật như AutoCad, 3D Max, SketchUp,... giúp bạn hoàn thành mọi tác vụ một cách nhanh chóng và hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("42d8ea7b-d6e2-4e4a-b704-be68dae7adb7"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Điểm sáng của thế hệ chip mới còn nằm ở khả năng tối ưu hóa hiệu năng nhờ công nghệ AI thông minh. Hệ thống sẽ tự động phân tích nhu cầu sử dụng của bạn, từ đó phân bổ nguồn lực một cách hợp lý và cung cấp các công cụ hỗ trợ cần thiết, đảm bảo hiệu quả công việc ở mức cao nhất. Nhờ vậy, bạn có thể thoải mái sáng tạo và chinh phục mọi thử thách mà không lo máy gặp tình trạng quá tải hay giật lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("cf20ff15-0b30-467c-b69d-a3ce2bd7bb9c"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/77d0e8f1-5843-45f7-8938-902926531c18.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("4cbdd477-d09c-4cd9-8398-4ea1b0c8b1d5"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "MSI Gaming Stealth 14 được trang bị sẵn RAM 32 GB chuẩn DDR5, mang đến khả năng đa nhiệm vượt trội, cho phép bạn mở nhiều ứng dụng nặng cùng lúc hay chơi game đồ họa cao mà không lo máy bị đơ hay giật lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("f41b4d37-cff0-4101-878d-f8139faa30ce"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Đi kèm với đó là ổ cứng SSD 1 TB NVMe PCIe giúp bạn thoải mái lưu trữ kho tàng trò chơi yêu thích, file hình ảnh, video nặng hay bất kỳ dữ liệu nào mà không cần lo lắng về việc hết dung lượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("7280782d-cd71-416c-81c4-b3632ddaaa63"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Trải nghiệm không gian điện ảnh ngay trên màn hình laptop", Type = ParseGadgetDescriptionType("BoldText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("1ffa4d93-43bc-4ce8-b2b9-c04c18452db0"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Laptop MSI sở hữu màn hình 14 inch có độ phân giải 2.8K (2880 x 1800) trên tấm nền OLED cung cấp chất lượng hình ảnh sắc nét, chi tiết đến từng điểm ảnh, kết hợp hiển thị màu đen sâu, độ tương phản cao, giúp hình ảnh sống động và có chiều sâu hơn, mang đến cảm giác chân thực như đang xem phim hay chơi game trên màn hình lớn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("0611063a-5224-4eaa-a934-2c9a47e7e164"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Gam màu rộng 100% DCI-P3 đáp ứng tốt cho các ứng dụng đòi hỏi độ chính xác màu sắc cao như chỉnh sửa ảnh, video, thiết kế đồ họa của những nhà sáng tạo nội dung, đồng thời cũng mang đến trải nghiệm xem phim, chơi game ấn tượng hơn với màu sắc rực rỡ, sống động đậm chất điện ảnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("9c0ded4b-4a93-4211-af46-67884ddc3772"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Hơn thế nữa, tần số quét 120 Hz cũng hỗ trợ hình ảnh trong game chuyển động mượt mà hơn, giảm thiểu hiện tượng nhòe ảnh khi di chuyển chuột hay quay camera, nhờ vậy, bạn có thể dễ dàng quan sát đối thủ và đưa ra những pha xử lý chính xác hơn, ngoài ra còn giúp giảm thiểu hiện tượng mỏi mắt khi chơi game trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("02206f66-edcd-4552-b90c-e1966cdcb640"), GadgetId = Guid.Parse("aabdbef7-2469-4616-ad20-c9a1ba109122"), Value = "Công nghệ Hi-Res Audio trên laptop với khả năng tái tạo âm thanh có độ phân giải cao hơn so với định dạng CD thông thường, mang đến cho bạn trải nghiệm âm thanh sống động, chi tiết và chân thực như đang thưởng thức âm nhạc trực tiếp tại phòng thu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("38ec115e-1943-498d-af54-c2e3e1497a98"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Laptop MSI Gaming Stealth 16 Mercedes AMG A1VGG Ultra 9 185H (293VN) đánh dấu một bước ngoặt trong thiết kế và hiệu năng, hòa quyện giữa công nghệ đỉnh cao và sự sáng tạo từ Mercedes-AMG Motorsport. Sự kết hợp này mang lại một sản phẩm không chỉ mạnh mẽ về cấu hình mà còn đầy phong cách, phù hợp cho cả game thủ lẫn nhà sáng tạo nội dung.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("63c9a8de-4701-4820-9693-affa6dc85040"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Xử lý cực mượt mọi yêu cầu", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("c1dff4ab-49aa-422b-9ae9-7069bfca3f27"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Laptop MSI được trang bị vi xử lý Intel Core Ultra 9 185H với 16 nhân và 22 luồng, mang lại hiệu năng xử lý đa nhiệm tuyệt vời. Với cấu hình này, bạn có thể thoải mái chơi các tựa game AAA đỉnh cao hay thực hiện các công việc đồ họa, lập trình phức tạp mà không gặp bất kỳ trở ngại nào. Hơn nữa, ở thế hệ chip mới này còn tích hợp thêm nhân xử lý AI - NPU cho phép quản lý hiệu suất xử lý, truy cập phần mềm, ứng dụng nhanh gọn cùng hàng loạt các tính năng thông minh khác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("2fde06b7-973d-49db-afb0-f742d4c7f695"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Các game thủ hoàn toàn có thể kỳ vọng vào một thiết bị mang lại những trải nghiệm đồ họa mê hoặc nhờ card NVIDIA GeForce RTX 4070 8 GB với công nghệ Ray Tracing, tái tạo hình ảnh phản chiếu sống động và chân thực, cung cấp khả năng liên kết và chuyển đổi từ card đồ họa đến màn hình được tối ưu hóa, mang đến những cảnh phim và trải nghiệm gaming mượt mà và cuốn hút. Không chỉ dừng lại ở đó, GPU còn tăng đáng kể tốc độ triển khai ý tưởng trên các nền tảng thiết kế chuyên nghiệp nhờ sự hỗ trợ của các lõi AI Tensor, giúp bạn biến những ý tưởng sáng tạo thành hiện thực một cách nhanh chóng và hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("9f041e34-69e0-4ed6-a5ab-9533417b2993"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e80f83bf-4ce6-40fe-b5b4-b053b9ed2cad.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("e341fbfa-653c-45c3-a156-77b8ca9980c1"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Dung lượng RAM 32 GB loại DDR5 với tốc độ bus cao 5600 MHz đảm bảo khả năng chạy nhiều phần mềm lớn và các tựa game nặng cùng lúc mà không gặp bất kỳ trục trặc nào. Bạn cũng có thể nâng cấp RAM lên đến 64 GB để đáp ứng mọi nhu cầu ứng dụng cao cấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("827177ec-b8b7-4e84-8983-0b0330eaba67"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Ổ cứng SSD dung lượng 2 TB của laptop mang lại tốc độ truy cập dữ liệu nhanh chóng, giúp bạn tải xuống các phần mềm ứng dụng hay truy xuất các file tài liệu hàng triệu dòng chỉ trong tích tắc. Không gian lưu trữ lớn cho phép bạn thoải mái chứa đựng các tác phẩm đồ họa, video hay tựa game yêu thích mà không lo hết chỗ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("6fc90cd8-7f8f-4d19-948c-6e043f881a25"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Đã mắt trải nghiệm mọi nội dung", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("dc04ac7c-231f-4bc1-8ebf-cd111f0d94af"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Màn hình OLED 16 inch với độ phân giải WQXGA+ (2560 x 1600) mang đến trải nghiệm hình ảnh chân thực và sắc nét. Tốc độ làm mới 240 Hz hỗ trợ mướt mắt mọi pha chuyển động tốc độ cao trong game hay phim ảnh hành động, không bị giật xe màn hình làm gián đoạn trải nghiệm. Độ chuẩn màu 100% DCI-P3 giúp màu sắc hiển thị chính xác và sống động đặc biệt ở những tựa game có bối cảnh đồ sộ, hiệu ứng chi tiết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("fe0ad0d0-2723-4cf1-8611-cd84bdbb7bf5"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e65c72ed-3e13-4267-a356-506f144b923c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c4a534d5-e545-4151-a616-2877a5afbaa5"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Trải nghiệm trọn vẹn những khoảnh khắc thư giãn đỉnh cao với hệ thống 6 loa đạt chuyển Hi-Res tích hợp hệ thống âm thanh vòm Nahimic Audio, mang đến khả năng khuếch đại âm thanh vượt trội, truyền tải mọi chi tiết một cách sống động và chân thực như đang ngồi trong rạp chiếu phim cao cấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("70faf129-c132-4368-ab31-f9392b64daa8"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Những hiệu ứng âm thanh tinh tế này không chỉ nâng tầm trải nghiệm giải trí mà còn đem lại lợi ích đáng kể cho việc làm video, vlog hay chỉnh sửa hiệu ứng âm thanh, giúp bạn tạo ra những sản phẩm chất lượng chuyên nghiệp và cuốn hút.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
