new GadgetDescription { Id = Guid.Parse("835b4697-dece-4e3f-bafa-b38f7dfe6161"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Ngoại hình của laptop gaming được thiết kế với sự tinh tế và phong cách thể thao nổi bật từ cảm hứng của dòng xe thể thao. Nắp lưng bằng kim loại được khắc logo của MSI và Mercedes, kết hợp cùng các chi tiết độc đáo như nút nguồn phong cách và hoa văn AMG trên chiếu nghỉ tay. Thiết kế này không chỉ mang lại vẻ ngoài hấp dẫn mà còn tăng cường độ bền và khả năng chống va đập của máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("8113b32b-364f-4657-86fe-9e0fa54baf62"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/734ef9e3-8c98-4179-829c-74b174eeebd0.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("51155a6b-f28f-473f-908e-426df3b4c597"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Bàn phím RGB Per-Key có khả năng tùy chỉnh màu sắc từng phím giúp tạo nên không gian chơi game đầy cá nhân hóa. Hệ thống đèn nền chuyển màu RGB giúp bạn dễ dàng thích nghi với mọi môi trường ánh sáng, tạo cảm hứng cho những trận đấu căng thẳng. Tính năng cảm biến vân tay cũng tích hợp cho truy cập thiết bị được tiện và an toàn hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("91947c1c-778d-4cdb-9c01-639baf3ab720"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Tiêu chí \"một người, một động cơ\" của Mercedes AMG về việc mỗi động cơ đều được phụ trách thực hiện bởi một thợ máy trưởng duy nhất, nhằm đảm bảo sự tỉ mỉ ở mức độ tối đa. Tôn chỉ ấy của AMG nay được kết hợp với công nghệ tản nhiệt Cooler Boost 5 của MSI, nhằm đặc trưng hoá thiết bị cá nhân cũng như đảm bảo hiệu suất và nhiệt độ ổn định cho máy với công nghệ cao cấp khi sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("4fe05a4c-c01b-44e5-8744-963cd7499ca7"), GadgetId = Guid.Parse("bc85c748-2d02-4535-8375-0395bbc6c418"), Value = "Laptop MSI Gaming Stealth được trang bị đầy đủ các cổng kết nối thông dụng như LAN (RJ45), USB 3.2, HDMI và USB Type-C giúp bạn dễ dàng kết nối với các thiết bị ngoại vi khác. Sự đa dạng trong kết nối mang lại sự linh hoạt và tiện lợi cho người dùng, từ công việc hàng ngày đến những trận game đỉnh cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("5e05c285-d91b-471d-af6d-39cdfcbd871e"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Mẫu laptop MSI Gaming Stealth 18 Mercedes AMG A1VHG Ultra 9 185H (080VN) đánh dấu sự hợp tác ấn tượng và đầy cảm hứng của MSI với hãng xe Mercedes-AMG Motorsport nổi tiếng. Sản phẩm sở hữu bộ cấu hình cực đỉnh, màn hình rộng lớn chuẩn studio, đa tính năng tối tân và trên hết là mang theo một ý tưởng kiến thiết độc đáo từ trong ra ngoài.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("bf3b4994-c593-40bb-b418-73fd50c0c345"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Độc đáo với kiểu dáng thể thao", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("6015dbaf-458a-4fec-ac98-fd37c8ba2199"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Vẻ ngoài của mẫu laptop MSI Gaming Stealth nay đã được \"độ\" hoàn hảo hơn, năng động hơn theo hơi hướng từ dòng xe Mercedes AMG vốn đã cực kỳ thể thao. Nắp lưng và chiếu nghỉ tay bằng hợp kim Nhôm-Magie bền bỉ được đặt cách điệu với logo MSI và Mercedes thể hiện sự hợp tác của 2 thương hiệu lớn. Ngoài ra, cảm hứng thiết kế còn được thể hiện qua nhiều những chi tiết nhỏ như nút nguồn là độc đáo, hoa văn AMG độc quyền trên chiếu nghỉ tay và các cạnh viền,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("bcb71f30-7932-4a0b-9a53-9999e639b903"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/37c48a96-e883-4b5d-9bdd-ba7e75e856ed.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("a5b2ecda-c9c4-4859-a915-f5ea78401ecd"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Ý tưởng thiết kế độc đáo mang lại cảm giác như bạn đang điều khiển một cỗ máy hơn là chiếc laptop thông thường. \"Thể thao đường trường\" truyền nhiệt cho \"thể thao điện tử\" với đèn chuyển màu RGB - Per-Key cho phép tinh chỉnh màu sắc của từng phím, cá nhân hoá không gian làm việc và chiến game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("46285b89-ac08-4db2-9743-871b7b1567cd"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Hệ thống tản nhiệt Vapor Chamber Cooler cùng các khe thoát nhiệt bố trí hợp lý xung quanh hỗ trợ duy trì mức nhiệt độ thích hợp trong quá trình sử dụng, không bị sụt giảm hiệu suất. Ngoài ra, bạn có thể đăng nhập thiết bị dễ dàng và bảo mật với tính năng bảo mật vân tay, công tắc khoá camera tích hợp sẵn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("d17e4d12-240a-4085-966f-7b0d78e04397"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Hỗ trợ nhiều giao thức kết nối có dây thông qua các cổng: LAN (RJ45), USB 3.2, HDMI, USB Type-C cho các nhu cầu kết nối với các phụ kiện khác như chuột, bàn phím, console game,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("5af8a351-3886-4a66-b078-7412113d8df1"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6319cb36-0694-4ec0-a9d3-8a0c04594896.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("dcbf414b-9171-4e64-9b16-b2358dbc1c50"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "\"Động cơ\" bứt phá, hỗ trợ đỉnh cao", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("e251b5ae-5a78-4e9f-9054-18644fbc156b"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Trung tâm sức mạnh của laptop MSI chính là vi xử lý Intel Core Ultra 9 185H, sở hữu 16 nhân và 22 luồng, mang đến khả năng xử lý đa nhiệm vượt trội. Đặt mức setting cao nhất cho các thể loại game bạn chơi từ phổ thông đến AAA, hơn nữa hiệu năng còn đáp ứng hiệu quả nhất cho các công việc đồ hoạ, lập trình, phát triển kỹ thuật chuyên sâu ở nhiều lĩnh vực. Thêm vào đó, tích hợp trí tuệ nhân tạo (AI) giúp tối ưu hóa hiệu suất, đảm bảo mọi tác vụ khó khăn đều được xử lý trơn tru.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("949fca00-ada2-410c-a8a6-3f95d19f2069"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Card đồ họa NVIDIA GeForce RTX 4080 12 GB sở hữu công nghệ DLSS 3.5 và Ray Tracing tiên tiến giúp mang lại hình ảnh chân thực, sắc nét, tạo nên trải nghiệm đồ họa xuất sắc. Tối ưu được trình hiển thị đồ hoạ, rút ngắn thời gian dựng xuất hay render các tác phẩm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("1e883cc8-08a1-4ad6-bdaf-fa87981c30b8"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Không thể bỏ qua dung lượng RAM \"khủng\" lên đến 64 GB loại DDR5 với tốc độ Bus cao 5600 MHz, cho phép bạn chạy hàng loạt phần mềm lớn, các con game nặng cùng lúc mà không gặp bất kỳ trục trặc nào. Laptop gaming cũng cho phép khả năng nâng RAM đến 96 GB nếu bạn có nhu cầu ứng dụng cao hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("0ae99a9a-0001-4f6a-b82c-15bbfdaa2cf3"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9bb80237-ed4c-4646-ae39-ddc5ad93e6f5.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("e708d91c-3244-4875-b4b3-343321fcb79e"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Ổ cứng SSD dung lượng 2 TB cải thiện tốc độ truy cập dữ liệu nhanh chóng nên việc tải xuống các phần mềm ứng dụng hay truy xuất các file tài liệu hàng triệu dòng cũng nhanh chóng hơn. Hơn nữa, không gian lưu trữ lớn nên việc chứa đựng được tốt các tác phẩm đồ hoạ, video hay tựa game yêu thích hoàn toàn không thành vấn đề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("b08b2304-ff69-4669-8720-5c3d1e308ae0"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Màn hình chuẩn Studio, hiển thị rực rỡ", Type = ParseGadgetDescriptionType("BoldText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("c2d67584-362e-484a-944b-a08755dd3a81"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Màn hình Mini LED kích thước đến 18 inch cực lớn trên một mẫu máy tính xách tay, với sự trang bị này bạn có thể vừa việc dựng phim, làm hình ảnh, chơi game rất đã mắt mà không cần thiết phải có thêm một chiếc màn hình rời trong quá trình hoàn thiện dự án và giải trí. Tốc độ làm mới 120 Hz đảm bảo độ mãn nhãn trong những pha chuyển động tốc độ cao ở các tựa game nhập vai hay các bộ phim bom tấn đỉnh cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("2d3f5f8a-e282-4f81-a9b2-382dfeda8748"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Độ phân giải 4K/UHD+ (3840 x 2400) có điểm ảnh gấp 4 lần so với những chiếc màn hình laptop gaming Full HD thông thường, tăng cường hiển thị màu sắc, lôi cuốn và hấp dẫn thị giác người dùng qua từng pixel. Độ chuẩn màu 100% DCI-P3 còn là công cụ vô cùng hữu ích trong quá trình làm đồ hoạ, cho màu sắc chính xác và giống với thực tế nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("93bdd266-62b2-4cd2-b3ee-c46bd9d9c8b5"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "MSI Gaming Stealth 18 Mercedes AMG không chỉ cho phần nhìn nịnh mắt mà còn nâng tầm trải nghiệm của bạn với công nghệ âm thanh tiên tiến. Dolby Atmos tạo ra hiệu ứng âm thanh vòm 3D sống động, cho phép bạn cảm nhận âm thanh từ mọi hướng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("a7064e72-f714-4ea5-9f83-5ca858c2633a"), GadgetId = Guid.Parse("fa10b3c8-d877-45fa-ae34-20c192b3239f"), Value = "Kết hợp với Hi-Res Audio và hệ thống 6 loa, chiếc laptop này mang đến âm thanh chất lượng cao, chân thực và mạnh mẽ. Từ tiếng bước chân trong game đến những giai điệu tinh tế trong bản nhạc, mọi thứ đều được tái hiện hoàn hảo, giúp bạn tận hưởng cảm giác như đang ở trong rạp chiếu phim hoặc phòng thu âm chuyên nghiệp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("460a1647-f39e-410f-9804-c04eb80ae964"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Bắt kịp ngay với xu thế làm việc thông minh từ tương lai với hàng loạt những tính năng AI tích hợp trên dòng chip mới, không chỉ thế laptop MSI Prestige 13 AI Evo A1MG Ultra 7 155H (062VN) còn sở hữu dáng vẻ cao cấp chuẩn doanh nhân, nhiều tiện ích hiện đại miễn bàn dành riêng cho các chuyên gia.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("97298bb3-f0bd-4012-986a-09257cc54997"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Thời thượng, nhỏ gọn bất ngờ", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("1967b4b3-cfe5-49a7-a236-cebf603405a4"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Thiết kế máy dạng mỏng với bề dày chỉ vỏn vẹn 16.9 mm khi gập hoàn toàn, khối lượng \"có như không\" 0.99 kg cực kì linh động cho nhiều các chế độ làm việc trong ngày, mang vác đi khắp nơi mà không bị vướng víu. Mỏng nhẹ là thế nhưng laptop của bạn vẫn vô cùng bền bỉ khi phủ lớp vỏ Nhôm - Magie, hạn chế trầy xước và hỏng hóc trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("aea98ddd-4117-490b-8bbf-3cce51ab710f"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d2c60058-3fb0-48ee-9099-67738e2fe205.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("b2fb4670-b434-4bc9-ba84-b44bbe20c44e"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Bản lề laptop MSI theo dạng Ergolift, có độ nâng nhất định khi mở, vừa nâng cao đáng kể nét thẩm mỹ vừa hỗ trợ xả nhiệt tốt hơn. Trong các buổi chia sẻ, thảo luận bạn cũng có thể thoải mái mở gập máy đến 180 độ để trình chiếu nội dung dễ dàng với mọi người xung quanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("5fe63fee-77c4-4ee4-b876-2836cbbc6146"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Full HD Webcam có sẵn để người dùng có thể làm việc từ xa bất cứ lúc nào. Hãng cũng trang bị thêm một số các công cụ bảo mật cần thiết cho quyền riêng tư người dùng như: bảo mật vân tay, mở khóa khuôn mặt và công tắc khoá camera.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("0f7c9bd2-adec-41aa-934a-40e618957e27"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Ngoài ra, các cổng giao tiếp thông dụng được bố trí cẩn thận ở 2 bên thân máy như: Jack tai nghe 3.5 mm, USB 3.2, HDMI, USB Type-C và khe Micro SD hỗ trợ bạn kết nối máy in, màn hình rời, chuột,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("559282e0-a95a-49f4-887d-9bdeb01d9f5c"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/f246657d-4ad9-4018-956c-9124f4dc6863.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("6a38f4fe-61e9-410a-871c-e7ba6a01a646"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Khung ảnh sắc nét y như thật", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("19ab6a27-403b-499c-8658-fe92dcda7328"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Màn hình OLED với kích thước phổ thông 13.3 inch hoàn hảo để bạn làm việc tốt với các trang tính Excel, giải trí phim ảnh hay thậm chí là trải nghiệm tốt các con game đồ hoạ. Với chuẩn DisplayHDR True Black 500 cùng độ phân giải 2.8K (2880 x 1800), màn hình còn cung cấp chất ảnh chi tiết, độ sống động màu cao cùng tính tương phản rực rỡ để tương thích với mắt người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("08c7e081-78a5-48aa-b150-85795058e6a1"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Tiện ích tuyệt vời cho các anh em làm sáng tạo, thiết kế với chuẩn màu studio đạt 100% DCI-P3, vượt qua hàng loạt các tiêu chuẩn khắt khe về độ chính xác cho sắc độ vật thể trong đồ hoạ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c427b4f4-3dff-4930-aab5-57515b452f74"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Trải nghiệm ngay chất âm sống động trên mẫu laptop cao cấp - sang trọng qua hệ thống loa chuẩn Hi-Res, hiệu ứng âm thanh 3D như toả ra từ mọi hướng trong không gian nhờ công nghệ DTS Audio. Quá trình nghe nhạc, trải nghiệm phim hay căn chỉnh sound effects của bạn cũng sẽ được nâng cao hiệu quả hơn rất nhiều.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
