new GadgetDescription { Id = Guid.Parse("d9f20d45-0461-4ed4-ae40-c39c411ffb81"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Thế hệ chip mới này còn tích hợp thêm NPU, nhân xử lý AI hiện đại cho phép tự động tối ưu hiệu suất với mọi công việc của bạn, phân bổ nguồn điện tốt hơn để kéo dài được thời gian sử dụng máy. Một số những tiện ích AI có thể kể đến như nhận diện vật thể, tối ưu tốc độ chỉnh sửa trong đồ hoạ, lọc tiếng ồn tự động cho chất lượng cuộc họp cao hơn,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("e0b4a403-5c38-413d-b546-76a3ddf2db5f"), GadgetId = Guid.Parse("cc3aeb99-4d79-4605-93d5-8f1f3f4fb821"), Value = "Với bộ nhớ RAM 32 GB LPDDR5 Onboard tích hợp sẵn và ổ cứng SSD 1 TB NVMe PCIe Gen 4 tốc độ cao, máy tính có khả năng tối ưu hóa tốc độ xử lý cho mọi tình huống, từ công việc văn phòng đến học tập, thiết kế, giải trí và thậm chí là trải nghiệm chơi game, duyệt web hàng ngày. Không gian lưu trữ rộng rãi cho phép bạn tải xuống bất cứ tài liệu nào mà không cần đắn đo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("f19e808c-f9ae-4037-9137-d6d66302233e"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Laptop MSI Prestige 14 AI Studio C1VEG-056VN - Cấu hình đột phá, thiết kế hiện đại, sang trọng", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("dffe4a8e-fb6a-489a-8e9a-c8d08d5a603e"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Laptop MSI Prestige 14 AI Studio C1VEG-056VN với cấu hình mạnh mẽ đảm bảo đáp ứng tốt các nhu cầu làm việc đa dạng từ sáng tạo nội dung đến giải trí cao cấp. Bên cạnh đó, máy còn được đánh giá cao với kiểu dáng hiện đại, mỏng nhẹ cùng tông màu Xám sang trọng, tạo điểm nhấn về thiết kế và góp phần tăng thêm sức hấp dẫn cho mẫu laptop MSI Prestige này.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("50d69967-0f4f-4dd4-9ef9-8ddfa1d94910"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Laptop MSI Prestige 14 AI Studio C1VEG-056VN dễ dàng gây ấn tượng với hiệu năng vượt trội nhờ bộ vi xử lý Intel Core Ultra 7-155H bao gồm 16 lõi và 22 luồng, xung nhịp lên đến 4.8GHz. Sức mạnh này cho phép máy xử lý đa nhiệm một cách dễ dàng, từ việc chỉnh sửa video đến lập trình phức tạp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("6a10d516-2073-4113-be9f-5188d6a12eba"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Kết hợp với CPU mạnh mẽ này là khả năng xử lý đồ họa tuyệt vời tới từ GPU NVIDIA GeForce RTX 4050 6GB GDDR6. Thông qua đó, ngay cả các tác vụ nặng như thiết kế đồ họa, render 3D hay chơi game cũng đều có thể được xử lý mượt mà trên MSI Prestige 14 AI Studio với chất lượng hình ảnh sắc nét.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("4d9a98d3-1657-4c58-b385-3851e7e24c98"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Chưa dừng lại ở đó, thế hệ laptop MSI Prestige này còn ghi điểm với hệ thống tản nhiệt tối ưu với 2 quạt và 4 khe thoát khí, đảm bảo hoạt động mát mẻ ngay cả khi xử lý các tác vụ nặng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("a145aa4a-fa8e-4983-8e96-6454254da9a2"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Khả năng đa nhiệm của MSI Prestige 14 AI Studio C1VEG-056VN cũng không hề khiến người dùng phải thất vọng nhờ sở hữu RAM DDR5 dung lượng 32GB, tốc độ lên đến 5600MHz. Với thông số RAM mạnh mẽ này, MSI Prestige 14 AI Studio cho phép người dùng có thể mở cùng lúc nhiều ứng dụng nặng mà không gặp hiện tượng giật lag, giúp tối ưu hóa hiệu suất làm việc. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("7aca41cb-faff-466d-86c8-df21c2b56e3e"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Về lưu trữ, laptop MSI Prestige được trang bị ổ cứng SSD PCIe Gen4 dung lượng 1TB, cung cấp tốc độ ghi và đọc dữ liệu siêu nhanh chóng. Qua đó, nó không chỉ tăng tốc độ khởi động cho máy và phần mềm, mà còn cung cấp không gian khủng để lưu trữ các tệp lớn như video, hình ảnh chất lượng cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("3867fd37-edd3-44f1-bce8-f00e2f2c3748"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Laptop MSI Prestige 14 AI Studio C1VEG-056VN nổi bật với thiết kế hiện đại và tinh tế nhờ chế tác từ hợp kim nhôm cao cấp. Cụ thể, chất liệu nhôm cao cấp này không chỉ giúp laptop MSI Prestige 14 AI Studio này trở nên chắc chắn, bền bỉ mà còn hỗ trợ tản nhiệt tốt hơn, duy trì nhiệt độ ổn định khi làm việc. Với tông màu xám chủ đạo, laptop MSI Prestige cũng đem tới cảm giác sang trọng và chuyên nghiệp, phù hợp với những người dùng làm việc trong môi trường sáng tạo hoặc doanh nhân. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("3e6dfd97-f5a9-40b5-a74a-562f21a0e9a5"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Ấn tượng hơn, các đường nét bo tròn mềm mại trên máy khi kết hợp với bản lề mở góc 180 độ, giúp người sử dụng có thể dễ dàng thao tác máy ở nhiều góc độ khác nhau. Cùng với đó là trọng lượng chỉ 1.7kg và độ dày 18.9mm, giúp laptop MSI Prestige 14 AI Studio này dễ dàng được mang theo bên mình, đáp ứng tốt nhu cầu di động mà vẫn duy trì phong cách thẩm mỹ cao cấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("b9548e54-c3a3-409d-8955-986ade4781bb"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Về khả năng hiển thị, MSI Prestige 14 AI Studio C1VEG-056VN mang lại trải nghiệm thị giác tuyệt vời nhờ độ phân giải QHD 2.8K (2880x1800 pixel). Đồng thời, kích thước 14 inch cùng tấm nền IPS trên máy cũng khá thu hút khi cung cấp góc nhìn rộng, giúp người dùng dễ dàng làm việc hoặc giải trí mà không lo bị biến dạng màu sắc. Đặc biệt, máy còn có khả năng tái tạo màu sắc chính xác với thông số phủ màu 100% DCI-P3, rất lý tưởng cho các nhà sáng tạo nội dung như chỉnh sửa hình ảnh hoặc video.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("341765c8-63e6-49d5-9099-fadbaf535a02"), GadgetId = Guid.Parse("f75b784a-c3e2-42f7-b256-03c58b056049"), Value = "Ngoài những điểm nhấn trên này thì MSI Prestige 14 AI Studio C1VEG-056VN còn nổi bật với thời lượng sử dụng liền mạch suốt nhiều giờ liền. Theo đó, viên pin trên máy sở hữu dung lượng lớn 90WHrs, cung cấp thời gian sử dụng liên tục nhiều tiếng đồng hồ. Với người dùng thường xuyên phải di chuyển hoặc làm việc từ xa thì đây được xem là một ưu điểm đặc biệt tiện lợi, phù hợp cho cả những buổi làm việc dài mà không cần phải lo lắng về việc sạc pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("5a05cecb-97de-4318-9dcd-15ac739d72dc"), GadgetId = Guid.Parse("c66f7ab5-d829-49f1-945b-94179a3a9e9b"), Value = "Laptop MSI Gaming Katana 15 B13VFK i7 13620H (676VN) đến từ nhà MSI đã có mặt tại Thế Giới Di Động. Với chip Intel Gen 13 dòng H mới nhất và card RTX 40 series mạnh mẽ, đây là một siêu phẩm laptop gaming sẽ thỏa mãn mọi nhu cầu của cộng đồng game thủ.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
