new GadgetDescription { Id = Guid.Parse("9120e0fa-10ad-401c-b65a-fd04b13c2567"), GadgetId = Guid.Parse("c66f7ab5-d829-49f1-945b-94179a3a9e9b"), Value = "Laptop MSI Gaming Katana được hoàn thiện với vỏ nhựa cao cấp cùng tông màu đen nam tính. Thiết kế với bộ khung lớn, các chi tiết gân chìm cùng logo Rồng đặc trưng được đặt cách điệu trên nắp lưng tạo nên dáng vẻ cuốn hút của chiếc máy này. Laptop với khối lượng tổng thể 2.25 kg vẫn vừa vặn nằm trong balo của bạn để mang theo mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("d2517f5c-5934-45ed-9a1e-5af663e21f95"), GadgetId = Guid.Parse("c66f7ab5-d829-49f1-945b-94179a3a9e9b"), Value = "Bàn phím có đèn chuyển màu RGB 4 vùng vừa hỗ trợ sử dụng ngay cả khi thiếu sáng, vừa đem đến nhiều nguồn cảm hứng mỗi khi bước vào những trận đấu căng thẳng cũng như thể hiện được nét cá tính riêng biệt của những dòng laptop gaming.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("9e3b5f32-574b-4196-83cd-c22b09f49dbc"), GadgetId = Guid.Parse("c66f7ab5-d829-49f1-945b-94179a3a9e9b"), Value = "Máy có đầy đủ các cổng kết nối như HDMI, Jack tai nghe 3.5 mm, USB 3.2, USB Type-C, LAN và USB 2.0 cung cấp hiệu suất kết nối nhanh chóng với các thiết bị ngoại vi khi cần thiết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("7583806c-a216-456e-b4b0-5e238c1c5033"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Trong dòng sản phẩm laptop gaming, MSI luôn nổi tiếng với sự đổi mới và hiệu suất vượt trội. Và giờ đây, bước tiếp theo trong hành trình này đã được chứng minh qua sự ra đời của dòng laptop MSI Gaming Katana A15 AI B8VF R7 8845HS (419VN) - một kiệt tác về công nghệ và hiệu năng. Với sự kết hợp hoàn hảo giữa thiết kế đỉnh cao và hiệu suất đa nhiệm ấn tượng, chiếc laptop này hứa hẹn sẽ đưa trải nghiệm gaming và làm việc đến một tầm cao mới.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b1ce25c3-b513-41fb-b039-4175aeb3c1a1"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Linh hoạt với hiệu năng vượt bậc", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("95aac02e-6db0-4c0f-9766-1779ea0b38ba"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "MSI Gaming Katana được cung cấp năng lượng bởi CPU AMD Ryzen 7 8845HS, với 8 nhân và 16 luồng, tạo điều kiện lý tưởng cho hiệu suất vượt trội trong mọi tác vụ. Khả năng xử lý đa nhiệm mượt mà và đáng kinh ngạc là điểm nhấn đặc biệt của máy. Tốc độ CPU lên đến 3.8 GHz, không chỉ đáp ứng mượt mà cho các ứng dụng đòi hỏi cao về khả năng xử lý mà còn cung cấp sức mạnh đáng kinh ngạc trong các trò chơi hiện đại. Tuy nhiên, điểm đáng chú ý hơn nữa là tốc độ tối đa Turbo Boost tận 5.1 GHz, cho phép máy tăng cường hiệu suất một cách đáng kể trong các nhiệm vụ đòi hỏi sự nhanh chóng và mạnh mẽ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("e62d2256-d792-4e8f-90a4-4ff13131d848"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7774387c-8a2d-4c10-8b4d-aa648fc03d2e.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("48942155-d4ef-4ade-90b6-f63f3c35cd49"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Chưa dừng lại ở đó, card rời NVIDIA GeForce RTX 4060 8 GB đưa đến trải nghiệm gaming và làm việc tuyệt vời. Card mạnh mẽ này không chỉ đảm bảo hiệu suất đồ họa tối ưu mà còn hỗ trợ các tính năng tiên tiến như Ray Tracing, giúp tạo ra hình ảnh chân thực và đẹp mắt hơn bao giờ hết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("44a41359-9b13-4303-b8c7-9dadc5422f54"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Mẫu laptop gaming này không chỉ ấn tượng với khả năng xử lý CPU mạnh mẽ mà còn được trang bị với một hệ thống RAM đáng kinh ngạc. Với 16 GB RAM đi kèm và khả năng hỗ trợ tối đa lên đến 64 GB, máy cung cấp cho bạn sự linh hoạt trong việc mở nhiều ứng dụng đồng thời hay xử lý dữ liệu lớn một cách dễ dàng. Bộ nhớ SSD 512 GB cho tốc độ truy xuất dữ liệu nhanh nhẹn cùng một không gian lý tưởng, lưu trữ các tài liệu cá nhân cần thiết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("0f64cbcd-7ba3-442d-84cc-69dce2e31923"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Hệ thống làm mát Cooler Boost 5 trên laptop MSI được cải tiến để đảm bảo máy luôn duy trì hiệu suất tốt nhất, ngay cả trong những tình huống tải công việc nặng nề nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("274916e6-e9cc-455d-99f8-fbbf9d8b302d"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7f1cfcad-0a05-40ec-97e4-9e230ac84b7c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("94eda901-bd3c-4147-b625-39a9b2fe614c"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Kiểu dáng đương đại, đẳng cấp", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("9b90e753-49e3-42af-a36d-eaf73439aa3e"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Thiết kế của mẫu laptop này cho bạn cái nhìn cá tính và đầy mạnh mẽ của một mẫu laptop gaming. Với chất liệu nhựa cao cấp, máy mang lại cảm giác chắc chắn và độ bền cao, đồng thời khối lượng vừa tầm 2.25 kg dễ dàng cho các anh em game thủ có thể mang theo đến phim trường, studio, nơi làm việc mà không gây cảm giác cồng kềnh quá mức.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("6110189d-4c76-4485-974a-06db1f13cb1b"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Điểm nhấn về thiết kế của MSI Gaming Katana là bàn phím được trang bị đèn chuyển màu RGB - 4 vùng, cho phép bạn tùy chỉnh màu sắc theo ý thích hoặc theo cài đặt của trò chơi yêu thích. Điều này không chỉ tạo ra một không gian trải nghiệm cá nhân hóa mà còn tạo điểm nhấn thú vị trong các buổi chơi game hoặc làm việc vào ban đêm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("1f7fd0d8-6e88-4133-9dfb-27dde7bb738e"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Đa dạng các cổng kết nối hiện đại được tích hợp trên máy như LAN, USB 2.0, Jack tai nghe 3.5 mm, USB 3.2, HDMI, USB Type-C.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("8046d59c-3367-4339-a856-a450ebd822c3"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/32e6ee42-806d-4e01-975d-fc826b46f4f6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("0e6d11c2-9ef6-4bf4-90ad-b370ee6a6eb6"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Màn hình 15.6 inch sắc nét, chi tiết", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("5ee78f63-13bc-4216-8fab-e853a41ef66d"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Laptop MSI Gaming Katana còn được trang bị khung hình 15.6 inch đi cùng mức độ phân giải Full HD (1920 x 1080), bạn sẽ tận hưởng một không gian hiển thị sắc nét, từ các chi tiết nhỏ nhất trong trò chơi đến công việc đòi hỏi độ chuẩn xác cao. Tần số quét 144 Hz là một trong những điểm nhấn đặc biệt của máy, đem lại cảm giác chơi game mượt mà và đáp ứng nhanh nhạy. Điều này đặc biệt quan trọng trong các tựa game yêu cầu FPS cao, chính xác nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("dee099c5-41ad-432d-bf64-1b9161937145"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Tấm nền IPS đưa đến góc nhìn rộng và màu sắc chân thực, cho phép bạn thưởng thức hình ảnh từ mọi góc độ mà hiếm khi xuất hiện điểm mù, tạo ra một trải nghiệm hình ảnh tuyệt vời không chỉ cho việc chơi game mà còn cho việc xem phim và làm việc đa phương tiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("43f7f44f-93f5-4436-9cf6-ced381f690a3"), GadgetId = Guid.Parse("89b3def9-15ab-4104-a03e-895be10a9e1a"), Value = "Thêm vào đó, với công nghệ âm thanh Nahimic Audio, bạn sẽ tận hưởng bữa tiệc âm thanh sôi động và chân thực. Công nghệ này tối ưu hóa chất âm để mang lại âm thanh vòm 3D sống động, bạn hoàn toàn đắm chìm vào không gian giải trí tối ưu nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("466fcad9-dadb-4f81-89c5-550dcff6f65b"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "MSI luôn nổi tiếng với những cảm hứng thiết kế bất tận và đầy độc đáo. Với mẫu laptop MSI Gaming Sword 16 HX B14VFKG i7 14700HX (045VN) có ngoại hình lấy được nét đặc sắc từ vẻ ngoài của thanh kiếm, cấu hình đỉnh cao và tính năng tân tiến sẽ mở ra cho bạn những trải nghiệm chiến game thích thú từ phần nghe đến phần nhìn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("50b58f81-1170-40d8-8581-3e5540bae955"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Đẳng cấp gaming đến từ con chip hiệu năng cao", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("08ff4165-d4b2-4aef-ad97-be4189fd45f0"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "MSI Gaming Sword 16 được trang bị bộ vi xử lý Intel Core i7 14700HX gồm 20 nhân và 28 luồng, ép xung được cao nhất đến 5.50 GHz có khả năng xử lý tốt với nhiều thể loại game khác nhau, chiến được các con game AAA đồ hoạ cao qua mức setting hợp lý. Không những thế, laptop cũng hoàn toàn thực hiện được tốt việc chỉnh sửa đồ hoạ 2D, 3D, edit video hoặc dùng để làm việc coding.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("219851d7-6f9e-495d-bd32-fe81bbe56dc0"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6b4a6e66-ed25-4502-b9d7-91ec3e2fa7e6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("ba93fe51-2512-4ea3-be2b-abd96a85e4f1"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "VGA NVIDIA GeForce RTX 4060 8 GB với tính năng MUX Switch hiện đại giúp tăng tốc độ dựng hình và tạo liên kết trực tiếp tới màn hình, duy trì ổn định chất lượng khung hình và độ chi tiết của hình ảnh kể cả trong những màn combat kịch tính nhất. Laptop MSI còn được tích hợp công nghệ Max-Q Technologies, tận dụng sức mạnh của trí tuệ nhân tạo để tăng tốc độ xử lý và tối ưu hóa hiệu suất đồ hoạ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("5b608296-b20b-401c-a363-7e8b9b715b9b"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Laptop này được trang bị RAM 16 GB DDR5 dung lượng lớn và tốc độ truy cập nhanh, cung cấp khả năng xử lý đa nhiệm mượt mà, cho phép bạn chạy nhiều ứng dụng và tác vụ đồng thời mà không gặp trục trặc. Việc xử lý các con game nặng hay các file đồ hoạ nhiều layers còn được bổ trợ tối đa nhờ khả năng nâng cấp RAM vượt trội lên đến 96 GB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("ea6cf2d7-0b2e-4714-bcd8-4ef421e43de5"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Ổ cứng SSD 1 TB NVMe PCIe Gen 4 rút ngắn thời gian truy xuất ứng dụng hơn đáng kể, cải thiện tốc độ khởi động hệ thống cũng như đem lại không gian lưu trữ tuyệt vời cho mọi hoạt động cần thiết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("edcb749e-c40d-42d1-9025-396c393a76cb"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Hệ thống tản nhiệt Cooler Boost 5 duy trì mức nhiệt độ lý tưởng cho thiết bị hoạt động tốt nhất, tránh được tình trạng sụt giảm hiệu năng do máy nóng lên khi chiến game lâu, cũng như đảm bảo tốt cho tuổi thọ các linh kiện bên trong.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("f0d0797d-ecea-4832-a09f-ecba81b6b77b"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/af3c2130-0e7f-4720-ac6a-9a15cf6f4328.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("1fba5728-60c3-41bb-b3f3-41701a36b70d"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Màn hình kích thước hoàn hảo, âm thanh đắm chìm", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a38c9298-275a-43cd-846c-d81d66463d56"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Với kích thước 16 inch và tấm nền IPS cho không gian hiển thị lớn, giúp tận hưởng trải nghiệm chơi game và xem nội dung đa phương tiện một cách thoải mái và trực quan hơn. Tần số quét 240 Hz hỗ trợ màn hình tái tạo hình ảnh mượt mà hơn, thời gian phản hồi nhanh giúp tránh hiện tượng mờ nhòe trong hình ảnh động, đảm bảo bạn có một cảm giác chiến game cực đã, đặc biệt với những tựa game Esport hay FPS tốc độ cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("375a40e4-d3fb-494b-9ddd-48b481787982"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Độ phân giải QHD+ (2560 x 1600) kết hợp với 3 cạnh viền mỏng, vừa mang lại độ sắc nét tuyệt vời từ hình ảnh, tính cân bằng thị giác tốt và đồng thời cũng đảm bảo được mắt người dùng sẽ không bị xao nhãng và hoàn toàn đắm chìm vào các trải nghiệm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("d983fa3b-2c52-4197-ad74-f0c9bfb3c272"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Thông số màu sắc chuẩn studio 100% DCI-P3 còn là điều kiện cực kì thuận lợi cho các designer hay nhà sáng tạo nội dung khi làm công việc edit, đồ hoạ đòi hỏi chuẩn màu cao và chính xác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("41106587-30be-4090-b575-4d287da6090c"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/5ac13b12-3772-4676-97bc-b885704a33a7.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("8171c774-2aa8-4b75-87b2-432435aa8f79"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Hệ thống loa Hi-Res cho phép bạn chìm đắm trong âm nhạc và hiệu ứng âm thanh của game, âm lượng mạnh mẽ và chi tiết, giúp bạn nghe rõ từng tiếng động và giọng nói của đối thủ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("109a6e41-2acb-48f5-8c57-88db0002753f"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Thiết kế cá tính, thời thượng, bật lên phong cách gamer", Type = ParseGadgetDescriptionType("BoldText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("bf040a81-5c5f-46cc-ad97-8c61b7dd3e9b"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Laptop MSI Gaming Sword mang thiết kế đặc sắc lấy cảm hứng sâu từ thanh kiếm, mạnh mẽ, cá tính nhưng lại không kém phần hiện đại và thanh mảnh. Vỏ máy bằng nhựa cao cấp, cứng cáp và cho cảm giác cầm nắm rất chắc tay. Khối lượng vừa phải 2.3 kg không quá nặng và vướng víu nếu bỏ trong balo và di chuyển mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("43ecbcee-7055-432d-85fe-7c80abebcd7a"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Laptop gaming với bàn phím được trang bị đèn chuyển màu RGB - 24 vùng cho phép bạn tùy chỉnh ánh sáng theo sở thích cá nhân không chỉ tạo điểm nhấn và thẩm mỹ cho laptop mà còn giúp bạn nhìn rõ các phím trong điều kiện ánh sáng yếu, cung cấp cảm giác chơi game thuận tiện và tăng cường khả năng phản xạ của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("214c0aac-53b7-4d47-a836-7e8f7101d6d7"), GadgetId = Guid.Parse("544aef6a-b9b1-4441-a229-3ad9d65df7e4"), Value = "Laptop này cung cấp đa dạng cổng kết nối để bạn dễ dàng kết nối với các thiết bị và phụ kiện khác như: LAN, Jack tai nghe 3.5 mm, USB 3.2, HDMI và USB Type-C.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("fd8cb90c-cae2-46fb-973d-fbacc6cef11a"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Laptop MSI Gaming Bravo 15 B7ED R5 7535HS (010VN) không chỉ là một mẫu laptop gaming tầm trung thông thường, mà còn là người bạn đồng hành đáng tin cậy dành cho những game thủ cũng như những người đam mê công việc sáng tạo. Với thiết kế hầm hố, máy trở thành biểu tượng của sức mạnh và sự phong cách, hứa hẹn sẽ tạo nên một trải nghiệm tuyệt vời trên mọi đấu trường ảo.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("7d9d33ff-dcb5-4978-8835-e9a38bf7d7af"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Thiết kế cá tính đậm chất gaming", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("45ee76fe-b1f1-45b5-899f-7d7b86fe0722"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Điểm đầu tiên khi mình nhìn vào laptop này với sự nổi bật về thiết kế cá tính, sự độc đáo và toát lên sức mạnh tinh thần của những người yêu thích thế giới game. Với màu đen đặc trưng và hoạ tiết gân chìm tạo điểm nhấn và chiều sâu, laptop khiến mình tự tin thể hiện phong cách riêng của chính mình. Logo chú chim đại bàng với đôi mắt rực rỡ nổi bật được in chìm trên mặt A của máy tạo điểm nhấn thú vị, chứng tỏ tính cá nhân mạnh mẽ và đặc biệt của những dòng laptop chiến game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("a905107c-20e3-4373-b4b4-d20123521ef6"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/75b981f4-f97a-43fb-95c8-695c014745ff.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("e04e17b2-326f-4b7f-b06c-b5d23ac2391b"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Với khối lượng chỉ 2.25 kg, chiếc laptop này rất dễ dàng bỏ vào balo và mang theo bất cứ nơi nào mình muốn. Điều này mang lại sự linh hoạt đáng kể cho người dùng, giúp họ không bao giờ bỏ lỡ cơ hội trải nghiệm game và công việc sáng tạo, bất kể nơi họ đến. Vỏ laptop được chế tác từ chất liệu nhựa được build khá tốt. Qua thời gian trải nghiệm sử dụng thì mình thấy hiện tượng ọp ẹp hay lỏng lẻo không xảy ra ở em máy này, nhất là những khi mình gập màn hình lên xuống, máy vẫn rất chắc chắn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("7678b6c2-e45b-4a82-81b9-5124801bf9b5"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Bàn phím laptop MSI có bố cục giữ nguyên Full-size không chỉ tạo sự thuận tiện cho các thao tác chơi game mà còn giúp mình nhập liệu và tính toán nhanh chóng hơn. Đặc biệt, với bàn phím có đèn nền màu xanh, mình có thể tạo ra không gian ấn tượng và hấp dẫn, làm cho mỗi trận đấu trở nên sôi động, đặc biệt khi mình chơi game vào ban đêm. Laptop cũng được lắp đặt hệ thống tản nhiệt gồm 2 quạt cho hiệu suất sử dụng làm việc hay chơi game của mình được duy trì lâu dài nhất có thể.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("3b453945-a8af-4f92-9f1e-1ef5dd1bb9d4"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Theo cá nhân mình đánh giá, thì hành trình phím của em máy này khá sâu và có một độ nảy tốt, tạo cảm giác thích thú khi gõ bàn phím. Tuy nhiên, về phần touchpad của máy đối với mình thì khá nhỏ, khi sử dụng máy đem đến phạm vi hoạt động hơi bó hẹp. Nhưng nhìn chung thì touchpad vẫn rất mượt mà và không gây rít tay khi chạm vào, khi ra mồ hôi tay cũng không bị chững lại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("76f462bc-1621-4ef8-a158-d0bf79155ba1"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7bdbb20c-cb01-48d8-af11-adafea8599d6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("0e79d3ff-5b65-4f22-b94d-4d72d7d48a56"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Laptop MSI Gaming Bravo được trang bị một loạt các cổng giao tiếp đa dạng để đáp ứng mọi nhu cầu kết nối của mình. Đa dạng từ cổng USB Type-C, USB 2.0, Jack tai nghe 3.5 mm, USB 3.2, HDMI và cổng LAN. Điều này đảm bảo có thể kết nối máy tính với mọi loại thiết bị và phụ kiện một cách thuận tiện và linh hoạt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("d1873903-6760-4558-855d-20873dec3fe5"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Hiển thị hình ảnh chân thực, sắc nét", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("7e3e5dbe-72a7-4948-92b4-e06101bdc7cd"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Màn hình kích thước 15.6 inch trên laptop MSI Bravo 15 không chỉ sử dụng tấm nền IPS mà còn có độ phân giải Full HD (1920 x 1080), màn hình này không chỉ tạo ra hình ảnh rất rõ nét và chi tiết, mà còn cho phép mình thưởng thức nó từ bất kỳ góc độ nào. Với độ phủ màu đạt 45% NTSC đem đến khả năng tái tạo màu sắc ổn định làm cho mọi nội dung trên màn hình trở nên sống động và hấp dẫn, bất kể mình đang làm việc hay giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("3d7fe273-f30d-4a17-9dfb-9e384628656d"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Với tần số quét 144 Hz, mở ra một không gian giải trí đích thực. Với tần số quét cao như vậy, mình được đắm chìm hoàn toàn vào thế giới ảo với những chuyển động nhân vật game mượt mà và chính xác. Điều này giúp loại bỏ tình trạng xé hình hay nhiễu hình, đặc biệt là khi mình đang trải nghiệm các tựa game tốc độ cao như thể loại bắn súng và đua xe. Đây là một trải nghiệm thú vị cho các game thủ và người yêu giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("6bdc00f3-382e-47f9-9c4e-632925dee42c"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Hệ thống Hi-Res Audio của máy cung cấp chất lượng âm thanh sống động và thể hiện âm lượng to cùng với độ rung tần số hoàn hảo giúp mình cảm nhận trọn vẹn sự kịch tính của các bộ phim và tính chân thực của các trận đấu game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("4ac2be52-029f-47e2-b4e9-7da907e26a13"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Chinh phục đa dạng mọi tác vụ", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("706e0b52-7eeb-468c-a478-1bf04044460f"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Laptop MSI Gaming được trang bị một bộ vi xử lý mạnh mẽ là AMD Ryzen 5 7535HS cùng với card đồ họa rời AMD Radeon RX 6550M 4 GB. Nhờ sự kết hợp này, máy cho phép mình thực hiện mọi công việc một cách trơn tru, từ các nhiệm vụ đơn giản như học tập, làm việc văn phòng, lướt web giải trí cho đến xử lý đồ hoạ và lập trình. Đặc biệt, mình có thể chiến đấu thoải mái với các tựa game thịnh hành mà không cần lo lắng về hiệu suất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("909ae3f0-d834-4927-9771-1d5d3d8a7c8c"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/68da4d93-c229-484f-b9ff-0c32659a6c7c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("52802140-06a3-4630-a655-74b6ee6626b0"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Kết quả từ công cụ Cinebench R23 cho thấy laptop MSI Bravo 15 có hiệu suất rất cạnh tranh so với các sản phẩm sử dụng chip Intel tương đương. Với 1448 điểm đơn nhân và 10900 điểm đa nhân, laptop chắc chắn có khả năng xử lý nhiều tác vụ cùng một lúc một cách hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("7a6a85de-cafa-420c-a2eb-5b30134fb7e8"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Với các tác vụ văn phòng như Microsoft Office thì chắc chắn không làm khó được em này. Còn về tác vụ đồ họa thì mình cảm nhận khá tốt khi mình dựng clip trong hàng giờ vẫn không gặp tình trạng giật lag mà nhiệt máy cũng ổn định không quá nóng. Cá nhân mình đánh giá rằng, với cấu hình như vậy thì đừng nói chi đến Canva hay Capcut, thậm chí các phần mềm thiết kế chuyên sâu hơn như Photoshop, Premiere thì chiếc laptop vẫn hoàn toàn có thể hỗ trợ khá tốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("21daa501-b255-4321-bb76-6c846f18267a"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Hệ thống bộ nhớ của laptop gaming cũng là một điểm sáng. Với RAM 16 GB, mình có khả năng thực hiện các tác vụ đa nhiệm mượt mà và không gặp hiện tượng giật lag hay độ trễ. Với cá nhân mình, mức RAM này có thể đáp ứng được nhưng nếu bạn có nhu cầu cao hơn thì máy cũng hỗ trợ RAM tối đa 64 GB. Ngoài ra, ổ cứng SSD 512 GB NVMe PCIe Gen 4.0 khởi động nhanh chóng mọi ứng dụng chỉ trong vài giây, đồng thời cung cấp dung lượng lưu trữ vừa đủ cho các tệp và dữ liệu của người dùng. Điều này giúp mình hoàn toàn tiết kiệm thời gian và tối ưu hóa hiệu suất làm việc trên máy tính.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("444167f3-851a-4263-ba41-37f33be96c6d"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Với viên pin 53.5 Wh thật sự là một lựa chọn tuyệt vời cho người dùng cần máy tính có thời gian sử dụng pin khá dài. Với khả năng hoạt động từ 4 đến 5 tiếng trong các tác vụ văn phòng, máy tính này đáp ứng hoàn toàn nhu cầu sử dụng hàng ngày mà không cần lo lắng về việc mang theo sạc. Đối với các tác vụ đồ họa hay chơi game thì cho khả năng sử dụng từ 2 - 3 tiếng, để tối ưu được hiệu suất máy thì mình thường mang theo cắm sạc khi thực hiện các tác vụ này.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("359d43a7-6fad-4661-bd53-bb6b1edf9f38"), GadgetId = Guid.Parse("0f524086-17ee-4947-a5c7-357d4b8f2013"), Value = "Tổng kết lại, chiếc laptop MSI Gaming Bravo 15 B7ED R5 7535HS (010VN) có thể đáp ứng đa dạng mọi nhu cầu cơ bản của người dùng từ học tập cho đến làm việc, chơi game giải trí và cả thiết kế đồ họa chuyên nghiệp. Với mức giá tiếp cận thì em laptop này hoàn toàn phù hợp với các bạn sinh viên hay những ai mong muốn tìm kiếm cho mình một chiếc laptop gaming với chức năng đa nhiệm và mang đến những trải nghiệm sử dụng tuyệt hảo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("655c4e9f-d1fe-42d8-a968-11ff9942bf2a"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Chiếc laptop MSI Modern 15 B7M R5 7530U (231VN) là sự lựa chọn lý tưởng để đồng hành cùng với các bạn tân sinh viên hay thậm chí là nhân viên văn phòng trong phân khúc giá rẻ nhưng mạnh mẽ đa tác vụ nhờ dung lượng RAM 16 GB và ổ cứng SSD 512 GB.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("0469cdb2-3836-4e41-bccd-5b04619a3b31"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Hiệu năng đỉnh cao đến từ chiếc máy nhỏ gọn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("eaac07a6-51f5-4327-96b2-1e564a66913b"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Nếu bạn đang tìm kiếm một máy tính xách tay với hiệu năng mạnh mẽ trong tầm giá dưới 15 triệu, thì MSI Model 15 chính là sự lựa chọn đáng xem xét dành cho bạn bởi nó có thể đáp ứng đầy đủ những yêu cầu và tất cả chính là nhờ vào bộ vi xử lý AMD Ryzen 5 7530U thế hệ mới thuộc chip dòng U dành riêng cho dân văn phòng cùng card đồ họa AMD Radeon Graphics được tích hợp sẵn trong máy. Vì thế, khi trải nghiệm chiếc laptop MSI này đã mang lại cho mình cảm giác mượt mà trong quá trình xử lý đa tác vụ văn phòng, đồ họa ở mức cơ bản hay giải trí với một số tựa game nổi tiếng hiện nay như Liên Minh Huyền Thoại hay CS:GO với mức cấu hình trung bình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("0bc50d5b-e16d-42b5-b0e6-2d58ff035432"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ca5efbba-84e6-46d1-9e21-5fd5e034e9f6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("c55944b4-0ab5-42c6-b144-9320a79d65bd"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Bên cạnh đó, mức RAM của chiếc máy này cơ bản đã lên đến 16 GB không chỉ giúp máy xử lý đa nhiệm mượt mà mà còn đảm bảo khả năng đáp ứng linh hoạt với các ứng dụng đòi hỏi tài nguyên lớn. Hơn nữa, còn thích hợp cho công việc văn phòng hàng ngày đặc biệt là những nhiệm vụ bắt buộc tập trung và xử lý nhanh chóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("e6e73c3f-7740-4ac7-b818-2d77e49ad73e"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Tuy nhiên, bạn cũng cần lưu ý rằng máy không hỗ trợ khả năng nâng cấp RAM thêm và có nghĩa là bạn nên cân nhắc kỹ lưỡng về nhu cầu sử dụng trong tương lai để đảm bảo rằng mức RAM hiện tại là đủ để đáp ứng và không gặp trở ngại về hiệu suất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("ee5675d9-21e3-4d18-9c65-e4e1d8bd13e7"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Một điểm mạnh khác của MSI Model 15 được mình đánh giá khá cao chính là ổ cứng SSD dung lượng lớn lên đến 512 GB chuẩn SSD NVMe PCIe. Điều này không chỉ giúp máy khởi động nhanh chóng mà còn mang lại không gian lưu trữ rộng lớn cho dữ liệu cá nhân, tài liệu công việc và các ứng dụng khác. Và đồng thời là một sinh viên đang theo học khối ngành kinh tế như mình thì nhận thấy đã cực kỳ đủ dùng rồi. Với ổ SSD này, mình tin chắc rằng bạn có thể trải nghiệm sự nhanh nhẹn và ổn định, tính tiện ích và hiệu suất tổng thể của máy tính.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("f863b99c-bda2-45a4-be83-3819a15ce427"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Dung lượng pin thường là một yếu tố quyết định trong việc mình lựa chọn mẫu laptop nào. Thì với mẫu MSI Model 15 năm nay, trong tầm giá dưới 15 triệu thì trải nghiệm của mình về dung lượng pin khá ổn định, đối với mình nhận thấy rằng máy có thể sử dụng khi không có cục sạc là xấp xỉ dưới 4 tiếng và các bạn lưu ý là mình chỉ làm việc với các tác vụ thông thường như lướt web và soạn văn bản, vì thế khi mình mang máy ra ngoài để đi học, đi cafe hay làm việc thì mình luôn đem theo cục sạc và để giúp mình nâng cao hiệu suất cũng như có được trải nghiệm hoàn chỉnh hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("fcf75ae2-ebd5-4fe7-ab5b-14248d973583"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Màn hình và âm thanh rất vừa vặn trong tầm giá", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("463c53ea-120f-4bfe-9cff-88a102181038"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Màn hình của MSI Model 15 là một điểm đáng chú ý khác đối với mình. Với kích thước 15.6 inch thì mình đã có được một không gian làm việc đủ lớn để làm việc đa nhiệm như chia đôi màn hình và xử lý nhiều việc một lúc mà không gây trở ngại gì.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("063e34c0-1335-4973-b4e2-0f2afc40eb51"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/be158b94-4ee7-4ebf-a574-8793f3adb61c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("a6f7fcff-7df3-42e4-8a9d-1b9ce9420d00"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Bằng việc hãng đã trang bị tấm nền IPS nên mình cảm thấy là có một góc nhìn rộng hơn cùng độ phân giải Full HD (1920 x 1080) trên con laptop học tập - văn phòng này thì hình ảnh được tái tạo rất sống động và sắc nét và theo mình nhận định trong quá trình mình sử dụng cảm thấy nó đã giảm nhiễu hạt hơn so với đời trước, với phiên bản năm nay thì trải nghiệm xem phim và làm việc của mình đã tốt hơn rất nhiều rồi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("65668d4e-00b4-4a8a-b4cd-9e5f3db947e5"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Chất lượng âm thanh rõ ràng và sống động nhờ công nghệ Hi-Res Audio, tạo nên một không gian giải trí di động tuyệt vời. Điều này làm cho việc xem phim, nghe nhạc và thậm chí là thực hiện các cuộc họp trực tuyến của mình có phần trở nên thú vị hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("6845ed05-056c-42ce-9876-9ca5da212c29"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Thiết kế thanh lịch và nhỏ gọn tiện lợi", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("3ca0be24-c01b-45cb-9e49-55fad7ae0fcc"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Ban đầu mình cực kỳ ấn tượng bởi thiết kế của con laptop này vì dù theo mình được biết trước là phần vỏ ngoài được làm bằng nhựa và được phủ một lớp sơn đen, tuy nhiên thì mình không thấy nó có cảm giác giảm giá trị của sản phẩm mà còn đem lại cho mình một cái nhìn rất thanh lịch và tinh tế. Mặt A của máy với logo MSI đặt ở chính giữa thể hiện sự đẳng cấp và thương hiệu của sản phẩm, khi sờ lên thì mình cảm thấy được độ mượt và xịn sò, nhưng vẫn có một điểm trừ làm mình khá đáng tiếc là việc bám vân tay cực kỳ nhiều vì thế bạn nên có cho mình một chiếc khăn mỏng để lau nó đi nhé!", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("4478e62f-efdb-498b-8146-31d2a1b0fb2d"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Chiếc laptop này có khả năng mở lên đến 180 độ vì thế bản lề là một điều mà đại đa phần mọi người chú ý đến. Đối với phiên bản trước thì mình cũng nhận thấy nhiều tin đồn về bản lề của nó hơi dễ gãy, trong phiên bản năm nay thì hãng đã phần nào tiếp thu khi mình cảm thấy phần bản lề có vẻ chắc chắn hơn không chỉ giúp máy giữ khá vững ở mọi tư thế mà còn nâng nhẹ máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("d3f68b74-8142-4d36-be2f-c86d39ffb25c"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ee1bdae3-3170-407c-829f-a7fc72a2b4a7.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("b75fbe15-ad97-4ec4-a921-034f751cc2b0"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "MSI Model 15 không chỉ mạnh mẽ về hiệu năng mà còn gọn nhẹ và thuận tiện cho di chuyển. Kích thước màn hình thì 15.6 inch nhưng khối lượng của em nó chỉ 1.75 kg và độ dày chỉ 19.9 mm, vì vậy mà chiếc laptop có thể trở thành một người bạn đồng hành lý tưởng cho sinh viên khi luôn phải di chuyển giữa các lớp học, thư viện hay quán cafe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("185b3abf-2636-4598-a1fc-49e208fce709"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Tuy mỏng nhẹ thế nhưng mẫu sản phẩm này lại đạt độ bền chuẩn quân đội MIL STD 810G, đáp ứng các tiêu chí khắt khe và đảm bảo hoạt động trơn tru và bền bỉ trong mọi điều kiện vì thế mà khi mình cầm chiếc máy này, mình cảm thấy khá cứng cáp và không quá ọp ẹp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("55815b2e-95d8-4f4d-9758-466ce62972c3"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Chiếc laptop MSI Modern này còn được tích hợp webcam thuận tiện cho việc học trực tuyến cùng các cổng kết nối khác cũng rất đầy đủ như hai cổng USB 2.0, một cổng USB 3.2, HDMI, cổng USB Type-C và jack tai nghe 3.5 mm đảm bảo bạn có đủ tùy chọn kết nối cho mọi nhu cầu. Việc hỗ trợ nhiều cổng kết nối giúp máy trở thành một trung tâm đa phương tiện linh hoạt, phục vụ mọi nhu cầu từ làm việc đến giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("437cb6f2-bf31-4f1b-8fc3-e9073687ee25"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Bàn phím của MSI Model 15 được thiết kế với layout fullsize tiện dụng, những nút phím có độ nảy vừa phải và khoảng cách giữa chúng không quá xa. Điểm đặc biệt là chiếc máy này còn được trang bị đèn nền đơn sắc, giúp bạn làm việc dễ dàng trong điều kiện thiếu sáng, không chỉ nâng cao về độ thẩm mỹ mà còn là một tính năng hữu ích khi bạn cần làm việc vào ban đêm hoặc ở những nơi có ánh sáng yếu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("de777412-0c4b-4524-b70b-4ccbb4cefa60"), GadgetId = Guid.Parse("12c053a5-0184-43c3-9ab8-e07ad56ac64c"), Value = "Tổng kết, laptop MSI Modern 15 B7M R5 7530U (231VN) là một sự lựa chọn đáng cân nhắc đối với tất cả những người đang tìm kiếm một chiếc laptop mạnh mẽ, nhẹ nhàng và đẹp mắt trong tầm giá dưới 15 triệu. Hiệu năng ổn định, màn hình và âm thanh chất lượng, thiết kế thông minh và các tính năng thuận tiện khiến chiếc laptop này nổi bật trong thị trường đa dạng. Hãy để chiếc laptop này trở thành một sự đầu tư sáng giá cho bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("88f62c92-cc1c-4070-87b4-b35fef78ae32"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "Thiết kế sang trọng, hiệu năng mạnh mẽ và nhiều tiện ích hiện đại là những gì được gói gọn trong chiếc laptop MSI Modern 15 B12MO i5 1235U (628VN). Đây chính xác là một mẫu laptop học tập - văn phòng sẽ thỏa mãn mọi yêu cầu làm việc, giải trí hàng ngày, phù hợp với sinh viên hay nhân viên văn phòng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("c083f9d8-fa8a-47b7-a85d-ac8b1ed37178"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "Laptop sử dụng chip Intel Core i5 1235U có xung nhịp tối đa lên đến 4.4 GHz hỗ trợ bạn làm việc mượt mà với các tác vụ trên Microsoft Office, Outlook,... soạn thảo văn bản, thực hiện các bảng tính phức tạp, chạy code và đồ họa đơn giản đều không thành vấn đề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("563a0321-0b41-444d-920c-7b033b08f2bb"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7f3222a0-24fa-476c-b8be-4897ccedcb5f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("75617db6-0d60-460c-b8bd-9768cdaac2f8"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "Card đồ họa tích hợp Intel Iris Xe Graphics trên laptop MSI nâng cao khả năng xử lý đồ họa, cho phép bạn chỉnh sửa hình ảnh, render video cơ bản, xem phim 4K và chiến thoải mái nhiều tựa game nhẹ đến trung bình như League of Legends, Valorant,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("26d623b7-d710-409b-9611-8876dc26d3ab"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "Dung lượng RAM 16 GB chuẩn DDR4 hỗ trợ vận hành trơn tru nhiều các ứng dụng nặng, cho phép bạn mở nhiều cửa sổ trình duyệt cùng một lúc mà không lo bị đơ máy và gián đoạn. Với ổ cứng SSD 512 GB NVMe PCIe, thiết bị cho phép bạn khởi động hệ điều hành và ứng dụng chỉ trong giây lát, đồng thời đem lại không gian lưu trữ tương đối thoải mái.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("5ef6752e-cfe6-4ef8-81d1-9c2ab1b2408f"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7b18e3f9-1cb5-4ef2-96b7-faf059b08e4e.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("f8a5b1c1-28e7-4fe4-bafa-c11bb1193830"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "Màn hình 15.6 inch với độ phân giải Full HD (1920 x 1080) cung cấp hình ảnh sắc nét, chi tiết từng khung hình. Tấm nền IPS cùng thông số màu 45% NTSC cũng khá hữu ích khi mang đến trải nghiệm phim ảnh thu hút, giúp người dùng chỉnh sửa hình ảnh và đồ họa tốt với độ chính xác màu sắc tương đối ổn định.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("ba89c47e-d77a-4c22-943a-f68fdf666ba8"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1b3f2e89-fd3e-48be-a600-be013c67afaa.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("46cbe2fc-4922-4187-befe-4d987f2e45d0"), GadgetId = Guid.Parse("f222de58-5ca9-4277-988b-2d3981ad4302"), Value = "Một mẫu laptop hiện đại vừa mang thiết kế tinh tế, vừa có khối lượng chỉ 1.7 kg, vừa vặn nằm trong balo để mang theo bên mình. Thêm vào đó, bàn phím còn được trang bị đèn nền hỗ trợ sử dụng ngay cả trong không gian thiếu sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
