new GadgetDescription { Id = Guid.Parse("16c39ae2-108b-49d5-aad6-6194dadc07aa"), GadgetId = Guid.Parse("38110022-cc9d-4d92-8974-cfaf360262d9"), Value = "Laptop MSI Modern 14 C13M i5 1335U (611VN) sẽ là một người cộng sự lý tưởng cho bạn trong phân khúc laptop học tập - văn phòng khi sở hữu cấu hình mạnh mẽ từ CPU Intel Core i5 cùng những tính năng thiết kế nổi bật. Laptop còn sở hữu kiểu dáng thời thượng, gọn nhẹ phù hợp với người dùng cần một thiết bị có tính di động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
