new GadgetDescription { Id = Guid.Parse("c180d184-46d3-4bf9-94ce-f2509321da77"), GadgetId = Guid.Parse("38110022-cc9d-4d92-8974-cfaf360262d9"), Value = "Sở hữu RAM 16 GB tốc độ 3200 MHz cho khả năng đa nhiệm vượt trội giúp bạn dễ dàng xử lý nhiều ứng dụng cùng lúc mà không gặp tình trạng giật lag hay giảm hiệu suất. Bạn có thể vừa có thể thiết kế slide trên Powerpoint vừa mở Chrome để tìm thông tin và ý tưởng, mà không lo máy bị đơ hay xử lý chậm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("923610fd-9493-4fd8-87ba-2e060e750696"), GadgetId = Guid.Parse("38110022-cc9d-4d92-8974-cfaf360262d9"), Value = "Ổ cứng SSD NVMe PCIe cung cấp tốc độ truy xuất dữ liệu nhanh chóng, thời gian khởi động máy, mở ứng dụng, tải tệp tin,... đều được rút ngắn một cách đáng kể. Dung lượng 512 GB mang đến một không gian lưu trữ thoải mái cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
