new GadgetDescription { Id = Guid.Parse("cfb42d55-a763-40ff-b9f2-d60f7cc70315"), GadgetId = Guid.Parse("38110022-cc9d-4d92-8974-cfaf360262d9"), Value = "Chiếc laptop học tập - văn phòng này có màn hình 14 inch đáp ứng tốt nhu cầu làm việc và giải trí. Màn hình có độ phân giải Full HD (1920 x 1080), dù là hình ảnh, video hay văn bản đều được hiển thị rõ nét, cho bạn thưởng thức trọn vẹn mọi nội dung.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("31428aab-01b8-42e5-9af0-b1161b41c5ff"), GadgetId = Guid.Parse("38110022-cc9d-4d92-8974-cfaf360262d9"), Value = "Tấm nền IPS cung cấp góc nhìn rộng lên đến 178 độ, cho bạn xem rõ các mọi nội dung trên màn hình từ nhiều hướng, thoải mái khi chia sẻ những thông tin với người xung quanh. Chống chói Anti Glare trên màn hình giúp giảm thiểu độ chói sáng từ môi trường xung quanh, cho bạn thoải mái sử dụng laptop ở những nơi có cường độ sáng ca", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
