new GadgetDescription { Id = Guid.Parse("0379ca21-fe72-4dcd-a1b7-5532324b051e"), GadgetId = Guid.Parse("38110022-cc9d-4d92-8974-cfaf360262d9"), Value = "Hệ thống loa Hi-Res trên laptop MSI Modern mang đến cho bạn trải nghiệm âm thanh hoàn toàn mới, nâng tầm mọi nội dung giải trí của bạn. Nhờ chất âm sống động và rõ ràng, bạn sẽ cảm nhận được từng nốt nhạc du dương, từng lời thoại trong phim ảnh hay cuộc gọi video một cách chân thực và chi tiết nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("912fffdc-05f8-45ae-9ea4-9a6bdf5b8a9c"), GadgetId = Guid.Parse("38110022-cc9d-4d92-8974-cfaf360262d9"), Value = "Chiếc laptop MSI này khoác lên mình diện mạo thanh lịch với gam màu bạc hiện đại, tôn lên nét tinh tế và chuyên nghiệp cho người dùng. Thiết kế tối giản nhưng không kém phần sang trọng, dễ dàng hòa hợp với mọi không gian. Với khối lượng chỉ 1.4 kg, MSI Modern trở thành người bạn đồng hành lý tưởng cho những ai thường xuyên di chuyển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
