new GadgetDescription { Id = Guid.Parse("ecca4b5c-c8ee-479e-b93b-aa6381e69539"), GadgetId = Guid.Parse("a0e699c2-c101-44fd-b245-d651abaefc46"), Value = "Mẫu laptop Gaming \"quốc dân\" sở hữu mức giá thân thiện phù hợp với mọi game thủ, mang trong mình hiệu suất mạnh mẽ vượt trội cùng đa dạng các tính năng sử dụng. Laptop MSI Gaming GF63 Thin 12UCX i5 12450H (873VN) chắc chắn là sự lựa chọn tuyệt vời cho những buổi chiến game của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
