new GadgetDescription { Id = Guid.Parse("bea818c3-916a-4121-a6f7-948ed9b851b9"), GadgetId = Guid.Parse("25ee73ab-71b1-4d6f-882e-8d087b0253dc"), Value = "Laptop MSI Thin 15 B13UC-1411VN là sự lựa chọn hoàn hảo cho các game thủ yêu thích sự cơ động mà không muốn hy sinh hiệu năng. Với thiết kế mỏng nhẹ, hiệu suất mạnh mẽ, màn hình chất lượng cao và bàn phím LED xanh, laptop này không chỉ đáp ứng mọi nhu cầu gaming mà còn đem lại phong cách sống động cho người dùng. Đây chắc chắn là một khoản đầu tư xứng đáng cho mọi game thủ đam mê.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("8634527a-eee2-489f-a630-4927371aac9c"), GadgetId = Guid.Parse("5aa144eb-a50c-4f8f-aa58-84f3fbeb11b9"), Value = "Laptop MSI Gaming Cyborg 14 A13VE i7 13620H (090VN) không chỉ là sở hữu ngoại hình mạnh mẽ mà còn là một cộng sự đáng tin cậy cho cả game thủ và những chuyên gia thiết kế đồ họa chuyên nghiệp. Với một mức giá hợp lý bạn đã có thể sở hữu ngay một bộ cấu hình hoàn hảo cho những trận game bất tận đầy giải trí.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("e935b079-32bc-4940-b167-f28c27deafaa"), GadgetId = Guid.Parse("5aa144eb-a50c-4f8f-aa58-84f3fbeb11b9"), Value = "MSI đã trang bị cho laptop này bộ vi xử lý Intel Core i7 13620H mạnh mẽ, sẵn sàng đối phó với mọi thử thách. Dù bạn là game thủ đam mê các tựa game AAA hoặc chuyên gia đồ họa cần xử lý các tác vụ nặng, chiếc laptop này luôn đảm bảo khả năng xử lý nhanh chóng và ổn định.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
