new GadgetDescription { Id = Guid.Parse("fb3a528b-8d99-49d1-95a3-ea0438671c2c"), GadgetId = Guid.Parse("5aa144eb-a50c-4f8f-aa58-84f3fbeb11b9"), Value = "Nâng cao trải nghiệm chơi game của bạn lên một tầm cao mới với card đồ họa NVIDIA GeForce RTX 4050 6 GB. Hãy trải nghiệm những hành động mãn nhãn và những trận đấu đầy căng thẳng với hình ảnh sắc nét, loại bỏ hoàn toàn hiện tượng giật lag và giựt giật, giúp bạn hoàn toàn đắm chìm vào thế giới ảo của game. Card đồ họa trên MSI Cyborg còn hỗ trợ hoàn thành các tác vụ đồ họa nặng như chỉnh sửa ảnh, thiết kế video chất lượng cao hay render 3D một cách mượt mà và nhanh chóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("7dcb54d6-64c2-49a9-b985-3b8e81e79a8c"), GadgetId = Guid.Parse("5aa144eb-a50c-4f8f-aa58-84f3fbeb11b9"), Value = "Laptop gaming này được trang bị bộ nhớ RAM 16 GB DDR5, mang đến tốc độ xử lý nhanh chóng và khả năng đa nhiệm mượt mà. Bạn có thể thỏa sức tham gia các trận đấu game hấp dẫn hoặc xử lý các công việc nặng mà không lo bị giật lag hay gián đoạn. Khả năng nâng cấp RAM lên tối đa 64 GB sẽ đáp ứng mọi nhu cầu sử dụng trong tương lai.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
