new GadgetDescription { Id = Guid.Parse("05598650-8890-493d-b19f-90cc9654940d"), GadgetId = Guid.Parse("5aa144eb-a50c-4f8f-aa58-84f3fbeb11b9"), Value = "Trải nghiệm âm thanh tuyệt vời với công nghệ Hi-Res Audio mang đến chất lượng âm thanh vượt trội. Âm thanh trong trẻo, mượt mà và không bị nhiễu hay méo mó, giúp bạn cảm nhận như đang nghe âm nhạc trực tiếp tại phòng thu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("177d006c-d930-43e9-9494-67cde8ec0908"), GadgetId = Guid.Parse("5aa144eb-a50c-4f8f-aa58-84f3fbeb11b9"), Value = "Laptop MSI Gaming Cyborg có vỏ nhựa đen nhám đầy cuốn hút và năng động, tạo nên phong cách cá tính cho người dùng. Với khối lượng chỉ 1.6 kg, đây là một trong những laptop gaming nhẹ nhất trong phân khúc của nó, giúp bạn dễ dàng mang theo bất cứ nơi đâu, từ học tập đến làm việc và tham gia các sự kiện game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c581ba6f-4149-4236-b405-44ba6d928d27"), GadgetId = Guid.Parse("5aa144eb-a50c-4f8f-aa58-84f3fbeb11b9"), Value = "Bàn phím của laptop MSI được trang bị đèn màu xanh giúp bạn dễ dàng thao tác trong mọi điều kiện ánh sáng. Đèn nền bàn phím mang lại cảm giác mạnh mẽ và đầy phong cách, thể hiện rõ mọi cá tính của game thủ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("6fd21f52-31cd-4dd6-a256-4cc0a601a8e4"), GadgetId = Guid.Parse("5aa144eb-a50c-4f8f-aa58-84f3fbeb11b9"), Value = "Với đầy đủ các cổng giao tiếp như LAN, USB 3.2, HDMI, Jack tai nghe 3.5 mm và USB Type-C, MSI Gaming Cyborg đảm bảo bạn kết nối dễ dàng với các thiết bị ngoại vi khác một cách thuận tiện và linh hoạt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("d031665d-7b2e-4453-b51d-635c8cadae4c"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "Laptop MSI Gaming Cyborg 15 AI A1VEK Ultra 7 155H (053VN) với con chip AI thế hệ mới sẽ cùng bạn chinh chiến trên mọi tựa game. Giảm tối đa thời gian chờ đợi nhờ ổ SSD PCIe thế hệ 4 và bộ nhớ RAM 16 GB DDR5 tốc độ cao, luôn sẵn sàng đồng hành cùng game thủ trong mọi chiến thắng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b6108ed2-dadc-412e-aca8-293ddaaf55d5"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "Chiến game đồ hoạ AAA đỉnh cao", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("bd8c380b-d9f2-4bca-9501-292aa7853d8f"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "Tự tin giành chiến thắng ở các tựa game AAA cấu hình cao hay hoàn thành nhiều dự án, kiến trúc đồ hoạ 3D, dựng phim và làm các công việc phát triển phần mềm nhờ con chip Intel Core Ultra 7 155H thế hệ mới được tích hợp nhân AI. Các tính năng AI hữu hiệu tối ưu việc phân bổ hiệu năng và tự động tinh chỉnh theo hoạt động, để bạn đạt được hiệu suất cao nhất khi chiến game hay làm việc mà vẫn đảm bảo được thời lượng sử dụng lâu dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("ab7aa986-bbab-487c-bdd6-1bab06f2ace1"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "Card đồ hoạ rời NVIDIA GeForce RTX 4050 6 GB mang đến trải nghiệm chơi game đỉnh cao duy trì hiệu suất hoạt động mạnh mẽ trong suốt thời gian dài, đồng thời chắc chắn được tốc độ khung hình luôn ổn định kể cả trong những pha combat gay cấn nhất. Hơn nữa, các chế độ AI được làm mới trên CPU cũng giúp hình ảnh được tái tạo đều màu và đẹp mắt hơn, công nghệ Ray Tracing thực hiện những khung cảnh đổ bóng theo ánh sáng thực đầy hấp dẫn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("7881b23c-6d70-4c26-940b-63eb841694a9"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6b338bf2-be93-49a8-ba6f-be98e2ed445b.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
