new GadgetDescription { Id = Guid.Parse("905d54e4-7a95-4ba0-8a1b-ef0cf195b8c4"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "Ổ cứng SSD 512 GB NVMe PCIe thế hệ thứ 4 với băng thông tốc độ cao cho mọi thao tác đều được phản hồi nhanh chóng, tệp dữ liệu khủng và những tựa game bom tấn cũng tải trong vài giây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
