new GadgetDescription { Id = Guid.Parse("21c39a2e-0aa8-4f38-8889-fa2fe238f896"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "Máy vẫn kiểu dáng quen thuộc từ những mẫu máy gaming nhà MSI, điển hình với logo Rồng ở mặt A laptop, bộ khung hầm hố ấn tượng, xen lẫn những nét mạnh mẽ và cá tính của máy lại thể hiện ra được đâu đó đôi phần sang trọng và cao cấp. Khối lượng 1.98 kg rất thuận tay khi cầm nắm, không có bất cứ khó khăn nào khi bỏ trong balo và mang theo cả ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
