new GadgetDescription { Id = Guid.Parse("c123d864-63b2-4aae-9de0-31521709ba84"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "Kết hợp cùng hệ thống đèn chuyển màu RGB - 4 vùng đầy sắc màu và đậm chất thể thao, chiếc laptop gaming này xứng danh một người bạn đồng hành lý tưởng trong mọi cảm hứng của người dùng, bên cạnh đó sự trang bị này cũng rất thuận tiện nếu phải sử dụng máy trong điều kiện thiếu sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
