new GadgetDescription { Id = Guid.Parse("fec96929-7375-4d00-83da-7c4bf553c16d"), GadgetId = Guid.Parse("0777f2c9-3654-4e65-8151-2a2a421bdbf1"), Value = "Hệ thống hai loa cao tần và hai loa trầm đạt chuẩn Hi-Res mang tới trải nghiệm âm thanh chân thực nhất khi chơi game và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("a2f9014c-d54c-4b0b-aead-5299daa96a6f"), GadgetId = Guid.Parse("c640834d-ed2b-4098-9042-36d339887f07"), Value = "Bạn đang tìm kiếm cho mình một chiếc laptop học tập - văn phòng giúp xử lý hiệu quả mọi tác vụ công việc và giải trí thường ngày. Laptop Acer Aspire 3 A315 510P 32EF i3 N305 (NX.KDHSV.001) mang hiệu năng ổn định, thiết kế tinh tế cùng một mức giá lý tưởng, chắc chắn sẽ là sự lựa chọn đáng cân nhắc để đáp ứng đa nhu cầu của người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("d6b70780-92d4-4120-8953-3e15aca78ab4"), GadgetId = Guid.Parse("c640834d-ed2b-4098-9042-36d339887f07"), Value = "Acer Aspire 3 được trang bị bộ vi xử lý Intel Core i3 N305 kết hợp cùng card Intel UHD Graphics hỗ trợ người dùng vận hành trơn tru mọi thao tác soạn thảo văn bản, chuẩn bị trình chiếu hay thực hiện trang tính trên Word, Excel, PowerPoint,... Bạn cũng có thể hoàn thành các tác vụ chỉnh sửa hình ảnh cơ bản với Photoshop, AI,... một cách dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
