new GadgetDescription { Id = Guid.Parse("98d295ed-35f5-459e-9a21-d144f5cbd2f6"), GadgetId = Guid.Parse("c640834d-ed2b-4098-9042-36d339887f07"), Value = "Bộ nhớ RAM 8 GB LPDDR5 cho phép khả năng đa nhiệm mượt mà, mở cùng lúc nhiều tác vụ, linh hoạt chuyển đổi qua lại giữa nhiều ứng dụng mà laptop không gặp các hiện tượng đơ hay lag máy. Ổ cứng SSD 256 GB mang đến không gian lưu trữ ổn định cùng khả năng khởi động và truy xuất ứng dụng nhanh hơn đáng kể. Hơn nữa, bạn hoàn toàn có thể tháo lắp ổ cứng và nâng cấp dung lượng lên tối đa 1 TB phục vụ cho các nhu cầu cao hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("269cedc9-08a0-4666-8077-fca5468d377c"), GadgetId = Guid.Parse("c640834d-ed2b-4098-9042-36d339887f07"), Value = "Màn hình laptop Acer có kích thước 15.6 inch sử dụng tấm nền IPS đem lại không gian trải nghiệm nội dung rộng lớn cùng góc nhìn bao quát và khả năng tái tạo màu sắc tốt từ mọi hướng. Ngoài ra, độ phân giải Full HD (1920 x 1080) cũng giúp các khung hình trở nên sắc nét, tối ưu hiển thị khi bạn làm việc hay xem phim.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
