new GadgetDescription { Id = Guid.Parse("f6646c8f-eaf6-44df-b9d1-3924533576e0"), GadgetId = Guid.Parse("c640834d-ed2b-4098-9042-36d339887f07"), Value = "Laptop Acer Aspire được hoàn thiện với vỏ nhựa màu bạc thanh lịch, laptop luôn thể hiện được tính tinh tế nhất định khi bạn sử dụng trong bất cứ môi trường nào. Máy có khối lượng tổng thể 1.7 kg vẫn vừa vặn nằm gọn trong chiếc balo của bạn, mang theo mọi nơi phục vụ công việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
