new GadgetDescription { Id = Guid.Parse("fc3b8f29-2204-461f-9f3f-ae737358c9f0"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "Laptop Acer Swift Go 14 41 R251 R5 7430U (NX.KG3SV.005) được ví như người bạn đồng hành lý tưởng, sẵn sàng cùng bạn chinh phục mọi thử thách trong học tập, công việc và giải trí. Sở hữu thiết kế thanh lịch, cùng hiệu năng ổn định hỗ trợ người dùng giải quyết tất cả tác vụ văn phòng trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("908dc8fa-6e64-4a75-bef4-5d7e14e5867d"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "Với độ dày chỉ 15.9 mm và khối lượng nhẹ 1.25 kg, Swift Go 14 xứng đáng là một trong những chiếc laptop mỏng nhẹ nhất trong phân khúc. Bạn có thể dễ dàng mang theo mọi lúc mọi nơi, dù là đến trường, văn phòng hay những chuyến du lịch xa, như mang theo thêm một cuốn sách bên mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("728aba2f-843b-4fa3-b337-f6cad1804c45"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "Vỏ làm từ chất liệu hợp kim Nhôm cao cấp mang đến cho máy vẻ ngoài sang trọng và đẳng cấp đặc trưng. Bề mặt được phủ lớp sơn mịn màng, tạo cảm giác êm ái khi cầm nắm. Không những thế, phần cấu tạo bản lề hay các bề mặt cũng khá chắc chắn, có thể hạn chế hư hỏng nhiều khi bị va đập.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("55caa266-050b-421f-97ac-19bf05f02c28"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7fda40da-1750-4d50-80ed-920c500ae006.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("13f0be48-3e6b-405b-a2a1-f5542a9d0155"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "Mở khoá thiết bị nhanh chóng và tiện nghi hơn với tính năng bảo mật vân tay. Laptop Acer cũng hỗ trợ đa dạng các cổng giao tiếp như: Jack tai nghe 3.5 mm, USB 3.2, HDMI,... hỗ trợ truyền xuất dữ liệu hiệu quả hơn với các thiết bị bên ngoài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("8c99d31a-4939-4541-b444-b7c9ca7358be"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "Trang bị vi xử lý AMD Ryzen 5 - 7430U thế hệ mới, Swift Go 14 sẵn sàng đáp ứng mọi nhu cầu công việc và giải trí của bạn. Con chip mạnh mẽ với nhiều nhân, nhiều luồng cùng tốc độ cao giúp xử lý đa nhiệm hiệu quả và mượt mà mọi tác vụ từ văn phòng đến giải trí đa phương tiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
