new GadgetDescription { Id = Guid.Parse("705d61c6-8a4f-46e2-8156-b5bf7c3d6d4a"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "Ổ cứng SSD M.2 PCIe dung lượng 1 TB cho phép khởi động máy nhanh chóng, truy cập dữ liệu mượt mà và tiết kiệm thời gian chờ đợi. Ổ cứng SSD cũng có độ bền cao hơn và ít bị hỏng hóc hơn so với ổ cứng HDD truyền thống. Hơn nữa, máy còn hỗ trợ nâng cấp ổ cứng tối đa 2 TB, đáp ứng mọi nhu cầu lưu trữ dữ liệu của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("ed53da02-d54c-41b9-aa2d-1cd174f82a17"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "Độ phân giải Full HD (1920 x 1080) trên màn hình 14 inch mang đến hình ảnh sắc nét, sống động cho bạn những trải nghiệm thị giác tuyệt vời dù là xem phim, chỉnh sửa ảnh hay chơi game. Độ sáng ổn định 300 nits giúp nội dung luông được rõ ràng trong mọi hoàn cảnh hay môi trường khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("abb1dc5b-ced1-42cc-9afe-28430accc01a"), GadgetId = Guid.Parse("4fe789e6-643f-43b7-ad25-1cfb5376fd48"), Value = "Màn hình laptop cũng có tính năng Acer ComfyView hạn chế việc bị chói hay giảm thiểu ánh sáng xanh tác động đến mắt người dùng, nguyên nhân chính gây ra mỏi mắt hay mất ngủ cho nhân viên văn phòng, sinh viên thường xuyên phải sử dụng máy cả ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("ea5b7311-d5ac-4dff-ac24-83fa8088ca24"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Laptop Acer Swift Go 14 AI SFG14 73 71ZX Ultra 7 155H (NX.KSLSV.002) là một chiếc laptop gọn nhẹ nhưng vẫn đảm bảo hiệu suất mạnh mẽ, phù hợp với nhiều nhu cầu sử dụng khác nhau, từ công việc văn phòng cho đến xử lý đồ họa, nhờ vào việc sử dụng chip Ultra 7 thế hệ mới.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("c50dd53e-7719-4f50-b132-9227f1d72f78"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Với thiết kế chỉ dày 14.9 mm và khối lượng 1.32 kg, bạn có thể dễ dàng đặt máy vào balo và mang đi khắp mọi nơi mà không cảm thấy bất tiện, là một người bạn đồng hành lý tưởng cho cuộc sống năng động của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("a91f0763-658e-4590-a62a-7abe5d00b463"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Vỏ ngoài được làm từ kim loại - Nhôm mang lại cho sản phẩm một vẻ đẹp sang trọng và tinh tế. Logo Acer nhỏ, được khắc chìm ở giữa góc trên ở bề mặt A của sản phẩm, tạo nên một nét thanh lịch cho chiếc laptop này.", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
