new GadgetDescription { Id = Guid.Parse("4f1fc8ff-bf61-4755-8fbf-78ce78f45661"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Màn hình 14 inch của máy có độ phân giải 2.8K (2880 x 1800), mang lại hình ảnh sắc nét và sống động. Tấm nền IPS cho phép góc nhìn rộng và màu sắc chính xác từ mọi góc độ. Đặc biệt, với độ sáng 400 nits, bạn có thể sử dụng máy một cách thoải mái trong mọi môi trường ánh sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("52061d43-a508-451e-926b-006b89e16447"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Máy hỗ trợ chuẩn màu 100% sRGB, giúp hình ảnh hiển thị chân thực, phù hợp cho việc làm việc đồ họa nhờ khả năng chuẩn chỉ về các gam màu. Điều này đặc biệt quan trọng đối với những người làm việc trong lĩnh vực thiết kế, sáng tạo, nơi mà sự chính xác của màu sắc đóng vai trò quan trọng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
